scripts.script:
if (!args.target || !args.attacker) return;

// 获取双方 Token
const targetToken = args.target.token?.object || args.target.getActiveTokens()[0];
const attackerToken = args.attacker.token?.object || args.attacker.getActiveTokens()[0];

if (targetToken && attackerToken) {
    const dist = canvas.grid.measureDistance(targetToken, attackerToken);
    if (dist > 10) {
        // 给予攻击者劣势 (修改 target 的 grantLevel)
        // 注意：CHECK 脚本运行在防御者身上，args.target 是防御者(自己)
        // 让我们自己变得“难以被命中”
        args.flags.grantLevel -= 1;
        ui.notifications.info(`${actor.name} 的赤血甲生效：距离过远，攻击劣势！`);
    }
}

const arts = system.arts;
const bonus = (arts.yanzou?.total||0) + (arts.qishu?.total||0) + (arts.shuxie?.total||0) + (arts.zuohua?.total||0);
S.resources.alcohol.max += bonus;

/**
 * 软猬系列通用反伤脚本
 */
if (!args.attacker || args.attacker.uuid === actor.uuid) return;
if (actor.isReceivingReflection) return;

const t1 = actor.token?.object || actor.getActiveTokens()[0];
const t2 = args.attacker.token?.object || args.attacker.getActiveTokens()[0];
let inRange = false;

if (t1 && t2) {
    const measure = canvas.grid.measurePath([t1.center, t2.center]);
    if (measure.distance <= 1.1) inRange = true;
} else {
    ui.notifications.warn(`${actor.name} 的 ${thisItem.name} 无法检测距离，请手动处理。`);
}

if (inRange) {
    args.attacker.isReceivingReflection = true;
    try {
        await args.attacker.applyDamage({ 
            amount: 15, 
            type: "poison", 
            attacker: actor, 
            isHit: true, 
            ignoreDefense: true 
        });

        const content = `
            <div class="xjzl-chat-card item-card">
                <header class="card-header" style="border-left: 4px solid #8e44ad;">
                    <img src="${thisItem.img}" style="border:none;" />
                    <h3 style="color: #8e44ad;">${thisItem.name}</h3>
                </header>
                <div class="card-content-wrapper">
                    <div style="font-weight:bold; color:#8b0000; margin-bottom:5px;"><i class="fas fa-skull-crossbones"></i> 反伤触发！</div>
                    <div style="font-size:0.9em;">距离检测: <b>近身</b></div>
                    <div style="font-size:0.9em;">反伤目标: <b>${args.attacker.name}</b></div>
                    <div style="margin-top:5px; border-top:1px dashed #ccc; padding-top:5px;">
                        已反弹 <span style="color:red; font-weight:bold;">15</span> 点毒素伤害。
                    </div>
                </div>
            </div>
        `;

        ChatMessage.create({
            user: game.user.id,
            speaker: ChatMessage.getSpeaker({ actor: actor }),
            content: content
        });
    } finally {
        args.attacker.isReceivingReflection = false;
    }
}

if (args.isBroken) {
    await actor.update({"system.resources.rage.value": actor.system.resources.rage.value + 1});
    ui.notifications.info("金麟服触发：架招被破，怒气+1");
}

if (args.isHit && args.attacker) {
    const targetToken = actor.token?.object || actor.getActiveTokens()[0];
    const attackerToken = args.attacker.token?.object || args.attacker.getActiveTokens()[0];
    if (targetToken && attackerToken) {
        const dist = canvas.grid.measureDistance(targetToken, attackerToken);
        if (dist <= 1.5) {
             const effectData = {
                name: "下盘不稳 (雪影衫)",
                icon: "icons/svg/falling.svg",
                duration: { rounds: 1 },
                changes: [{ key: "flags.xjzl-system.unstable", mode: 5, value: "true" }]
            };
            await args.attacker.createEmbeddedDocuments("ActiveEffect", [effectData]);
            ui.notifications.info("雪影衫触发：对方下盘不稳！");
        }
    }
}

const neigongId = actor.system.martial.active_neigong;
if (neigongId) {
    const neigong = actor.items.get(neigongId);
    // 阳刚属性检查 (element 为 yang 或 gang)
    // 注：DataModel 定义 element 只有 yin, yang, taiji
    if (neigong && (neigong.system.element === 'yang')) {
        S.combat.block += 5;
    }
}
