const updates = {};
const res = actor.system.resources;

// 减少醉意
if (res.alcohol.value > 0) {
  updates['system.resources.alcohol.value'] = Math.max(0, res.alcohol.value - 10);
}
// 减少杀戮
if (res.shalu.value > 0) {
  updates['system.resources.shalu.value'] = Math.max(0, res.shalu.value - 10);
}

if (!foundry.utils.isEmpty(updates)) {
  await actor.update(updates);
  ui.notifications.info(`${actor.name} 饮茶静心，气息平复。`);
}

