if (res.alcohol.value > 0) {
  updates['system.resources.alcohol.value'] = Math.max(0, res.alcohol.value - 10);
}

// 消除疲劳状态
// 使用 Manager 移除 slug 为 'fatigue' 的特效
const hasFatigue = actor.effects.some(e => e.getFlag('xjzl-system', 'slug') === 'fatigue');
if (hasFatigue) {
    await game.xjzl.api.effects.removeEffect(actor, 'fatigue');
    ui.notifications.info(`${actor.name} 饮下一盏热茶，疲劳尽消。`);
}

if (!foundry.utils.isEmpty(updates)) {
  await actor.update(updates);
}

