// 解除昏迷(unconscious)或晕眩(stun)
const statusesToRemove = ['unconscious', 'stun'];
let removed = false;
for (const slug of statusesToRemove) {
    const hasStatus = actor.effects.some(e => e.getFlag('xjzl-system', 'slug') === slug || e.statuses.has(slug));
    if (hasStatus) {
        await game.xjzl.api.effects.removeEffect(actor, slug);
        removed = true;
    }
}
if (removed) ui.notifications.info(`${actor.name} 借茶力清醒了过来。`);

if (!foundry.utils.isEmpty(updates)) {
  await actor.update(updates);
}

