system.usageScript:
const res = actor.system.resources; if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 30)});
const roll = await new Roll('1d20').evaluate();
roll.toMessage({flavor: '鲜掉眉毛数量'});

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 20)});
await actor.applyHealing({amount: 10, type: 'huti', showScrolling: true});

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 40)});
await actor.applyHealing({amount: 25, type: 'huti', showScrolling: true});

// 1. 检查目标
if (!game.user.targets.size) return ui.notifications.warn('请先选择一个目标！');
const target = game.user.targets.first().actor;

// 2. 准备致盲特效数据 (纯数据，不要写函数)
// 先从配置找，找不到就手写
let blindData = null;
const conf = CONFIG.statusEffects.find(e => e.id === 'blind');
if (conf) {
    blindData = foundry.utils.deepClone(conf);
    blindData.duration = { rounds: 3 };
    blindData.label = '被香料致盲'; // 可选：改个名
}

// 3. 发起请求
// 注意：onFail 传递的是 blindData 对象，系统收到失败回调后会自动应用这个AE
await Macros.requestSave({
    target: target,
    attacker: actor,
    type: 'qinggong',
    dc: 11,
    label: '躲避香料攻击',
    onFail: blindData
});

ui.notifications.info(`${actor.name} 撒出了一把烧烤香料！`);

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 20)});
if (res.alcohol) await actor.update({'system.resources.alcohol.value': res.alcohol.value + 5});

const res = actor.system.resources; if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 80)});
const roll = await new Roll('1d20').evaluate();
roll.toMessage({flavor: '黯然销魂饭 - 顿悟检定'});
if (roll.total > 15) {
    ui.notifications.info(`${actor.name} 陷入顿悟状态！`);
} else {
    ui.notifications.info(`${actor.name} 觉得非常好吃，但无事发生。`);
}

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 90)});
await actor.applyHealing({amount: 50, type: 'huti', showScrolling: true});

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 60)});

// 1. 获取当前层数 (用于计算本次难度)
const slug = 'food-laba-buff';
const existEffect = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === slug);
// 如果存在特效，读取层数；如果不存在，视为0层
const currentStacks = existEffect ? (existEffect.getFlag('xjzl-system', 'stacks') || 1) : 0;

const dc = 15 + (currentStacks * 5);
const dmg = 100 + (currentStacks * 100);

// 2. 执行检定 (Self Roll) - 直接等待结果，无需回调
const roll = await actor.rollAttributeTest('renxing', {
    bonus: 0,
    flavor: `腊八粥毒性判定 (当前${currentStacks}层, DC ${dc})`
});

if (!roll) return; // 玩家关闭弹窗

// 3. 判定结果
if (roll.total >= dc) {
    // 成功：得修为
    await actor.manualModifyXP('general', 300, { title: '腊八粥', reason: '食用腊八粥' });
    ui.notifications.info(`${actor.name} 压制住了毒性，功力大增！`);
} else {
    // 失败：扣血
    await actor.applyDamage({ amount: dmg, type: 'poison', isHit: true });
    ui.notifications.warn(`${actor.name} 毒气攻心，痛苦万分！`);
}

// 4. 无论成败，都要叠层 (记录毒性积蓄)
const effectData = {
    name: '腊八',
    icon: 'systems/xjzl-system/assets/icons/consumable/食物2.png',
    description: '体内积蓄的腊八粥毒性，层数越高，下次食用越危险。',
    flags: { 'xjzl-system': { 'slug': slug, 'stackable': true } }
};
await game.xjzl.api.effects.addEffect(actor, effectData);

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 40)});
await actor.applyHealing({amount: 10, type: 'huti', showScrolling: true});

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 40)});
await actor.applyHealing({amount: 5, type: 'huti', showScrolling: true});

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 30)});
await actor.applyHealing({amount: 20, type: 'huti', showScrolling: true});

const res = actor.system.resources;
if (res.satiety) await actor.update({'system.resources.satiety.value': Math.min(res.satiety.max, res.satiety.value + 30)});
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.max(0, res.alcohol.value - 50)});

scripts.script:
if (args.damage > 0) {
    await actor.applyHealing({amount: 10, type: 'mp', showScrolling: true});
}

// 遍历所有武器等级
const ranks = system.combat.weaponRanks;
for (const key in ranks) {
    // 注意：PASSIVE阶段只能读到基础值或之前的修正
    if (ranks[key].value < 4) {
        ranks[key].mod = (ranks[key].mod || 0) + 1;
    }
}
