system.usageScript:
// 获取已服用次数
const count = actor.getFlag('xjzl-system', 'consumed_jinyinhua_count') || 0;
const xpGain = Math.max(1, 10 - count);

// 增加修为
await actor.manualModifyXP('general', xpGain, { title: '服用金银花', reason: `第${count+1}次服用` });

// 更新计数
await actor.setFlag('xjzl-system', 'consumed_jinyinhua_count', count + 1);
ui.notifications.info(`${actor.name} 服用金银花，获得 ${xpGain} 修为。`);

// 永久增加气血上限
const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '服用人参',
    enabled: true,
    changes: [{key: 'resources.hp.bonus', value: 20}]
});
await actor.update({'system.customModifiers': modifiers});
ui.notifications.info("气血上限永久+20！");

// 永久增加内力上限
const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '服用灵芝',
    enabled: true,
    changes: [{key: 'resources.mp.bonus', value: 10}]
});
await actor.update({'system.customModifiers': modifiers});
ui.notifications.info("内力上限永久+10！");

// 1. 施加副作用 (永久减少上限)
const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '月魂丹丹毒',
    enabled: true,
    changes: [
        {key: 'resources.hp.bonus', value: -2},
        {key: 'resources.mp.bonus', value: -2}
    ]
});
await actor.update({'system.customModifiers': modifiers});

// 2. 提示分配修为
ui.notifications.warn("丹毒入体，气血内力上限-2。请手动打开一个武学/内功，点击修炼并输入500点修为（来源选择通用）。");

// 增加修为
await actor.manualModifyXP('general', 3000, { title: '炼魂生息丹', reason: '丹药效果' });

// 记录服用数量
const count = (actor.getFlag('xjzl-system', 'consumed_lianhun_count') || 0) + 1;
await actor.setFlag('xjzl-system', 'consumed_lianhun_count', count);

let msg = `已获得3000修为，请DM发放恶行值。当前服用次数: ${count}。`;
if (count > 10) msg += " 寿命+2年。";
ui.notifications.info(msg);

// 1. 增加修为
await actor.manualModifyXP('general', 1000, { title: '雪怪寒血', reason: '服用效果' });

// 2. 扣除内力上限
const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '雪怪寒血副作用',
    enabled: true,
    changes: [{key: 'resources.mp.bonus', value: -7}]
});
await actor.update({'system.customModifiers': modifiers});
ui.notifications.info("修为+1000，内力上限-7。 (修正组已添加)");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '血蟒丹副作用',
    enabled: true,
    changes: [{key: 'stats.tipo.mod', value: -2}]
});
await actor.update({'system.customModifiers': modifiers});
ui.notifications.info("体魄永久-2 (修正组已添加)");

if (actor.getFlag('xjzl-system', 'consumed_xuebaotang')) return ui.notifications.warn("你已经喝过此汤了，再喝也无用。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '雪豹补身汤',
    enabled: true,
    changes: [{key: 'resources.hp.bonus', value: 5}]
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_xuebaotang', true);
ui.notifications.info("气血上限永久+5！");

// 1. 移除失血
await game.xjzl.api.effects.removeEffect(actor, 'bloodloss', 99);

// 2. 回复 50%
const healAmount = Math.floor(actor.system.resources.hp.max * 0.5);
await actor.applyHealing({ amount: healAmount, type: 'hp', showScrolling: true });

// 3. 走火入魔
await game.xjzl.api.effects.toggleStatus(actor, 'rage', true, { overlay: true });
ui.notifications.warn("药力过猛，走火入魔！请在两回合后手动移除状态。");

if (actor.getFlag('xjzl-system', 'consumed_jinyingao')) return ui.notifications.warn("只能服用一次。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '金银膏',
    enabled: true,
    changes: [{key: 'arts.jiaoyi.mod', value: 1}]
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_jinyingao', true);
ui.notifications.info("交易等级永久+1！");

const tradeLevel = actor.system.arts.jiaoyi.total || 0;
const extra = tradeLevel * 10;
const total = 50 + extra;
await actor.applyHealing({ amount: total, type: 'hp', showScrolling: true });
await actor.applyHealing({ amount: total, type: 'mp', showScrolling: true });

const targets = ['bleed_stack', 'unstable', 'root', 'fushen', 'cuogu', 'fengzhao', 'rage'];
for (const slug of targets) {
    await game.xjzl.api.effects.removeEffect(actor, slug, 99);
}
ui.notifications.info("净化完成。");

// 默认此为永久提升
const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '金蛟丹',
    enabled: true,
    changes: [
        {key: 'resources.hp.bonus', value: 30},
        {key: 'resources.mp.bonus', value: 30}
    ]
});
await actor.update({'system.customModifiers': modifiers});
ui.notifications.info("气血内力上限永久+30！");

if (actor.getFlag('xjzl-system', 'consumed_jinfengyulu')) return ui.notifications.warn("只能服用一次。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '金风玉露丸',
    enabled: true,
    changes: [
        {key: 'stats.shenfa.mod', value: 5},
        {key: 'stats.qigan.mod', value: 5}
    ]
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_jinfengyulu', true);
ui.notifications.info("身法气感永久+5！");

await actor.applyHealing({amount: 150, type: 'hp', showScrolling: true});

// 检查性别 (女/Female)
const gender = actor.system.info?.gender || "";
if (gender.includes("女") || gender.toLowerCase().includes("female")) {
    // 应用AE
    // 假设 item.effects[0] 是那个回合回血的
    const effectData = item.effects.contents[0].toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

ui.notifications.info("四季膏方已应用，请手动删除当前季节之外的 3 个特效。");
// 将所有特效都加上
for (const e of item.effects) {
    await game.xjzl.api.effects.addEffect(actor, e.toObject());
}

ui.notifications.info("服用污泥丸，请手动移除一个地级以下状态。");
// 模拟呕吐 (饱食度-50)
if (actor.system.resources.satiety) {
    await actor.update({'system.resources.satiety.value': Math.max(0, actor.system.resources.satiety.value - 50)});
}

// 回复
const hp = Math.floor(actor.system.resources.hp.max * 0.5);
const mp = Math.floor(actor.system.resources.mp.max * 0.2);
await actor.applyHealing({amount: hp, type: 'hp'});
await actor.applyHealing({amount: mp, type: 'mp'});
ui.notifications.warn("请在10回合后手动应用体魄-10的副作用。");

if (actor.getFlag('xjzl-system', 'consumed_longhudan')) return ui.notifications.warn("只能服用一次。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '龙虎丹',
    enabled: true,
    changes: [
        {key: 'stats.liliang.mod', value: 5},
        {key: 'stats.neixi.mod', value: 5}
    ]
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_longhudan', true);
ui.notifications.info("力量内息永久+5！");

await game.xjzl.api.effects.removeEffect(actor, 'root');
await game.xjzl.api.effects.removeEffect(actor, 'cuogu');
await game.xjzl.api.effects.toggleStatus(actor, 'pain', true, {duration:{rounds:1}});

// 应用延迟回血Buff (1回合)
const effectData = item.effects.contents[0].toObject();
await game.xjzl.api.effects.addEffect(actor, effectData);

if (actor.getFlag('xjzl-system', 'consumed_kaihuidan')) return ui.notifications.warn("只能服用一次。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '玲珑开慧丹',
    enabled: true,
    changes: [{key: 'stats.wuxing.mod', value: 1}]
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_kaihuidan', true);
ui.notifications.info("悟性永久+1！");

scripts.script:
// 检查是否触发过
if (!thisEffect.getFlag('xjzl-system', 'triggered')) {
    ui.notifications.warn('青龙汤生效！残疾检定自动视为 100！');
    await actor.applyHealing({amount: 100, type: 'huti', showScrolling: true});
    // 标记为已触发，并删除AE(避免重复)
    await thisEffect.delete();
}

system.usageScript:
const res = actor.system.resources;
const updates = {};
if (res.alcohol) updates['system.resources.alcohol.value'] = Math.max(0, res.alcohol.value - 30);
if (res.satiety) updates['system.resources.satiety.value'] = Math.max(0, res.satiety.value - 30);
if (res.shalu) updates['system.resources.shalu.value'] = Math.max(0, res.shalu.value - 30);
if (!foundry.utils.isEmpty(updates)) await actor.update(updates);

// 检查性别添加速度Buff
const gender = actor.system.info?.gender || "";
if (gender.includes("女") || gender.toLowerCase().includes("female")) {
    const effect = item.effects.contents[0].toObject();
    effect.changes.push({key: 'system.combat.speed', mode: 2, value: '2'});
    effect.name += ' (女子加成)';
    await game.xjzl.api.effects.addEffect(actor, effect);
} else {
    // 男子只加基础Buff
    await game.xjzl.api.effects.addEffect(actor, item.effects.contents[0].toObject());
}

if (actor.system.resources.satiety) {
    await actor.update({'system.resources.satiety.value': Math.max(0, actor.system.resources.satiety.value - 20)});
}

scripts.script:
const hp = actor.system.resources.hp.value;
const max = actor.system.resources.hp.max;
const amount = (hp / max < 0.5) ? 60 : 30;
await actor.applyHealing({amount: amount, type: 'hp', showScrolling: true});

system.usageScript:
// 检查今日是否服用
if (actor.getFlag('xjzl-system', 'consumed_dingxin_today')) return ui.notifications.warn("今日已服用过定心丸。");

if (actor.system.resources.shalu) {
    await actor.update({'system.resources.shalu.value': 0});
    ui.notifications.info("心神已定，杀戮归零。");
    // 标记今日已服 (需手动清除或配合每日重置脚本)
    await actor.setFlag('xjzl-system', 'consumed_dingxin_today', true);
}

