system.usageScript:
if (actor.getFlag('xjzl-system', 'consumed_xiya')) return ui.notifications.warn("只能服用一次。");
if (actor.system.stats.neixi.value >= 40) return ui.notifications.warn("你的根基已深，此药无效。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
modifiers.push({
    id: foundry.utils.randomID(),
    name: '栖鸦秘药',
    enabled: true,
    changes: [{key: 'stats.neixi.mod', value: 10}]
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_xiya', true);
ui.notifications.info("内息永久+10！");

if (actor.getFlag('xjzl-system', 'consumed_siwu')) return ui.notifications.warn("只能服用一次。");
const baseLi = actor.system.stats.liliang.value;
const baseTi = actor.system.stats.tipo.value;
if (baseLi >= 40 && baseTi >= 40) return ui.notifications.warn("根基已深，无效。");

const modifiers = foundry.utils.deepClone(actor.system.customModifiers || []);
const changes = [];
if (baseLi < 40) changes.push({key: 'stats.liliang.mod', value: 5});
if (baseTi < 40) changes.push({key: 'stats.tipo.mod', value: 5});

modifiers.push({
    id: foundry.utils.randomID(),
    name: '四物药散',
    enabled: true,
    changes: changes
});
await actor.update({'system.customModifiers': modifiers});
await actor.setFlag('xjzl-system', 'consumed_siwu', true);
ui.notifications.info("属性已提升！");

