system.usageScript:
const hpMax = actor.system.resources.hp.max;
const boost = Math.floor(hpMax * 0.2);
// 创建临时Buff增加上限
const effectData = {
    name: "血海丹-上限提升",
    icon: item.img,
    changes: [{key: 'system.resources.hp.bonus', mode: 2, value: boost}],
    duration: {seconds: 3600}
};
await game.xjzl.api.effects.addEffect(actor, effectData);
await actor.applyHealing({amount: boost, type: 'hp', showScrolling: true});
await game.xjzl.api.effects.toggleStatus(actor, 'bloodloss', true);

const gender = actor.system.info?.gender || "";
if (!gender.includes("男") && !gender.toLowerCase().includes("male")) return ui.notifications.warn("女性服用无效。");
// 应用Buff
const effect = item.effects.contents[0].toObject();
await game.xjzl.api.effects.addEffect(actor, effect);
// 副作用
await game.xjzl.api.effects.toggleStatus(actor, 'fali', true);

if (actor.getFlag('xjzl-system', 'consumed_tongsheng_today')) return ui.notifications.warn("今日已服用。");
const amt = Math.floor(actor.system.resources.mp.max * 0.5);
await actor.applyHealing({amount: amt, type: 'mp'});
await actor.setFlag('xjzl-system', 'consumed_tongsheng_today', true);

if (actor.getFlag('xjzl-system', 'consumed_huangfeng_today')) return ui.notifications.warn("今日已服用。");
const amt = Math.floor(actor.system.resources.hp.max * 0.5);
await actor.applyHealing({amount: amt, type: 'hp'});
await actor.setFlag('xjzl-system', 'consumed_huangfeng_today', true);
