const updates = {};
const res = actor.system.resources;
// 醉意 -10
if (res.alcohol) updates['system.resources.alcohol.value'] = Math.max(0, res.alcohol.value - 10);
// 杀戮 -10
if (res.shalu) updates['system.resources.shalu.value'] = Math.max(0, res.shalu.value - 10);

if (!foundry.utils.isEmpty(updates)) {
    await actor.update(updates);
    ui.notifications.info(`${actor.name} 饮下了茶，顿觉神清气爽。`);
}

