system.usageScript:
const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 65)});

// 简单的伤痕检测脚本 (假设伤痕是名为 '伤痕-XX' 的 Item 或 Effect)
// 这里做一个简单的模拟提示，具体检测逻辑极度依赖伤痕的具体实现方式
const scars = actor.items.filter(i => i.name.includes('伤痕') || i.name.includes('残疾'));
const count = scars.length;
if (count > 0) {
    const xpGain = count * 100;
    await actor.manualModifyXP('general', xpGain, { title: '大梦千秋', reason: '伤痕转化' });
    ui.notifications.info(`感悟伤痕，获得 ${xpGain} 点通用修为。`);
} else {
    ui.notifications.info('身上无伤，未获得修为。');
}

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 10)});

// 侠者判定: 道德指数 > 0 或 身份包含侠
// 简单起见，这里检查 xiayi > 50
const isXia = (actor.system.social.xiayi || 0) > 50;
if (isXia) {
    // 如果是侠，添加额外的 Buff
    const extraEffect = {
        name: '临川贡酒-侠义加成',
        icon: item.img,
        changes: [
            { key: 'system.combat.dodge', mode: 2, value: 1 },
            { key: 'system.combat.speed', mode: 2, value: 1 }
        ],
        duration: { rounds: 10 }
    };
    await actor.createEmbeddedDocuments('ActiveEffect', [extraEffect]);
    ui.notifications.info(`${actor.name} 侠气干云，酒力更盛！`);
}

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 10)});

// 施加醉倒 (复用昏迷 Unconscious)
await game.xjzl.api.effects.toggleStatus(actor, 'unconscious', true);

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 65)});

// 模拟伤痕检测
const scars = actor.items.filter(i => i.name.includes('伤痕'));
if (scars.length > 0) {
    const xp = scars.length * 50;
    await actor.manualModifyXP('general', xp, { title: '醉花酒', reason: '伤痕' });
}

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 1)});
if (res.rage) await actor.update({'system.resources.rage.value': Math.min(res.rage.max, res.rage.value + 1)});

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 10)});
await actor.applyHealing({amount: 20, type: 'huti'});

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 15)});
await game.xjzl.api.effects.removeEffect(actor, 'fatigue');

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 15)});
await game.xjzl.api.effects.removeEffect(actor, 'pain');

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 15)});
await game.xjzl.api.effects.removeEffect(actor, 'bleed_stack', 99);
await game.xjzl.api.effects.removeEffect(actor, 'sielie');

const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': Math.min(res.alcohol.max, (res.alcohol.value || 0) + 15)});
await game.xjzl.api.effects.removeEffect(actor, 'blind');
await game.xjzl.api.effects.removeEffect(actor, 'deaf');
