ui.notifications.info("大梦千秋入喉，往事涌上心头。请根据伤痕数量手动增加修为 (每道100)。");

scripts.script:
// 检测是否标签为【侠】
if (S.social.xiayi >= 100 && S.social.exing < 50) {
    S.combat.dodge += 1;
    S.combat.speed += 1;
}

system.usageScript:
const res = actor.system.resources;
if (res.alcohol) await actor.update({'system.resources.alcohol.value': res.alcohol.value + 10});

// 1. 获取系统预设的“醉倒”状态数据
const statusData = CONFIG.statusEffects.find(e => e.id === 'zuidao');
if (statusData) {
    // 2. 克隆并设置持续时间为10回合
    const effectData = foundry.utils.deepClone(statusData);
    effectData.duration = { rounds: 10 };
    effectData.flags.core = { overlay: true }; // 覆盖大图标

    // 3. 应用状态
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

ui.notifications.warn(`${actor.name} 喝了土烧，一头栽倒在地！`);

scripts.script:
// 检查武器类型是否为匕首 (dagger)，通常笔也归为此类或特殊备注
if (args.move.weaponType === 'dagger') {
    args.output.damage += 10;
    args.output.bonusDesc.push('太白酒(+10)');
}

// 检查身上是否有“醉倒”特效 (通过 slug 判断最稳健)
// 注意：actor.statuses 是 V13 核心集合，也可以用 actor.statuses.has('zuidao')
const isDrunk = actor.effects.some(e => e.getFlag('xjzl-system', 'slug') === 'zuidao');

if (isDrunk) {
    S.combat.block += 10;
}

