ui.notifications.info("醉花酒入喉。请根据伤痕数量手动增加修为 (每道50)。");

scripts.script:
// 检查是否已触发过
if (!thisEffect.getFlag('xjzl-system', 'triggered')) {
    ui.notifications.warn('寒泉酒生效！强行维持意识！');
    // 移除晕眩状态 (濒死会自动上stun，这里立刻解掉)
    setTimeout(async () => {
         await game.xjzl.api.effects.removeEffect(actor, 'stun');
    }, 500);

    // 标记已触发并设置1回合后删除
    await thisEffect.update({
        'flags.xjzl-system.triggered': true,
        'duration': { rounds: 1 }
    });
}

