scripts.script:
if (args.move.weaponType === 'instrument') {
    const yanzou = actor.system.arts.yanzou?.total || 0;
    const bonus = Math.min(5, yanzou);
    if (bonus > 0) {
        ChatMessage.create({
            content: `<div class='xjzl-chat-card'><div class='card-content-wrapper'><b>《宫商录》生效</b><br>乐器招式敌人检定难度 +${bonus}</div></div>`,
            speaker: ChatMessage.getSpeaker({actor: actor})
        });
    }
}

S.combat.def_waigong += 10;
S.combat.def_neigong -= 5;
// 单刀等级加成
if (S.combat.weaponRanks.blade) {
    S.combat.weaponRanks.blade.mod += 1;
}

if (args.isCrit && args.attacker) {
    if (actor.getFlag('xjzl-system', 'neigong_anxiang_triggered')) return;

    await Macros.requestSave({
        target: args.attacker,
        type: 'dingli',
        dc: 14,
        label: '抵抗暗香迷魂',
        attacker: actor,
        onFail: {
            name: '走火入魔',
            icon: 'icons/svg/skull.svg',
            duration: { rounds: 1 },
            flags: { 'xjzl-system': { slug: 'rage' } }
        }
    });
    await actor.setFlag('xjzl-system', 'neigong_anxiang_triggered', true);
}

if (args.isCrit && args.attacker) {
    await Macros.requestSave({
        target: args.attacker,
        type: 'dingli',
        dc: 18,
        label: '抵抗暗香迷魂',
        attacker: actor,
        onFail: {
            name: '走火入魔',
            icon: 'icons/svg/skull.svg',
            duration: { rounds: 1 },
            flags: { 'xjzl-system': { slug: 'rage' } }
        }
    });
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effectData = thisItem.effects.getName('寒阴真气').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effectData = thisItem.effects.getName('寒阴真气(圆)').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.finalDamage > 0) {
    const effectData = thisItem.effects.getName('恶臭').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.finalDamage > 0) {
    const effectData = thisItem.effects.getName('恶臭(圆)').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.move && args.move.name) {
    const len = args.move.name.length;
    args.output.damage += len * 2;
}

if (args.move && args.move.name) {
    const len = args.move.name.length;
    args.output.damage += len * 2;
}

if (args.isCrit) {
    const effectData = thisItem.effects.getName('星火').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.isCrit) {
    const effectData = thisItem.effects.getName('星火(圆)').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.finalDamage > 0) {
    let step = actor.getFlag('xjzl-system', 'neigong_bingxuan_step') || 0;
    step = (step % 2) + 1;
    let effectName = step === 1 ? 'unstable' : 'cuogu';
    await game.xjzl.api.effects.toggleStatus(args.target, effectName, true, { duration: { rounds: 1 } });
    await actor.setFlag('xjzl-system', 'neigong_bingxuan_step', step);
}

if (args.finalDamage > 0) {
    let step = actor.getFlag('xjzl-system', 'neigong_bingxuan_step') || 0;
    step = (step % 4) + 1;
    const map = {1:'unstable', 2:'cuogu', 3:'fushen', 4:'fengzhao'};
    await game.xjzl.api.effects.toggleStatus(args.target, map[step], true, { duration: { rounds: 2 } });
    await actor.setFlag('xjzl-system', 'neigong_bingxuan_step', step);
}

if (args.attacker && Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName('枯木').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.attacker, effectData);
}

if (args.attacker && Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName('枯木').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.attacker, effectData);
}

if (args.finalDamage > 0) {
    const effectData = thisItem.effects.getName('枯木').toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

const qizhenCount = actor.items.filter(i => i.type === 'qizhen' && i.system.equipped).length;
const bonus = Math.min(20, qizhenCount * 2);
S.stats.liliang.mod += bonus; S.stats.shenfa.mod += bonus; S.stats.tipo.mod += bonus;
S.stats.neixi.mod += bonus; S.stats.qigan.mod += bonus; S.stats.shencai.mod += bonus;

const qizhenCount = actor.items.filter(i => i.type === 'qizhen' && i.system.equipped).length;
const bonus = Math.min(40, qizhenCount * 4);
S.stats.liliang.mod += bonus; S.stats.shenfa.mod += bonus; S.stats.tipo.mod += bonus;
S.stats.neixi.mod += bonus; S.stats.qigan.mod += bonus; S.stats.shencai.mod += bonus;

const targetNgId = args.target.system.martial.active_neigong;
if (targetNgId) {
    const ng = args.target.items.get(targetNgId);
    if (ng && ng.system.element === 'yin') {
        args.flags.ignoreBlock = true;
    }
}

const targetNgId = args.target.system.martial.active_neigong;
if (targetNgId) {
    const ng = args.target.items.get(targetNgId);
    if (ng && ng.system.element === 'yin') {
        args.flags.ignoreBlock = true;
    }
}

const neixi = S.stats.neixi.total;
const bonus = Math.min(50, Math.floor(neixi / 2));
args.output.bonusDesc.push(`三昧真火: +${bonus} (对阴柔目标生效)`);
// calc阶段无法获取target，仅显示加成可能性，实际加成在preDamage

const targetNgId = args.target.system.martial.active_neigong;
if (targetNgId) {
    const ng = args.target.items.get(targetNgId);
    if (ng && ng.system.element === 'yin') {
        const neixi = S.stats.neixi.total;
        const bonus = Math.min(50, Math.floor(neixi / 2));
        args.config.amount += bonus;
    }
}

if (canvas.ready && actor.token?.object) {
    const self = actor.token.object;
    const targets = canvas.tokens.placeables.filter(t => t !== self && Math.hypot(t.x - self.x, t.y - self.y) / canvas.grid.size <= 1);
    const effect = thisItem.effects.getName('五雷').toObject();
    effect.origin = thisItem.uuid;
    for (const t of targets) {
        if (t.actor) await game.xjzl.api.effects.addEffect(t.actor, effect);
    }
}

if (canvas.ready && actor.token?.object) {
    const self = actor.token.object;
    const targets = canvas.tokens.placeables.filter(t => t !== self && Math.hypot(t.x - self.x, t.y - self.y) / canvas.grid.size <= 3);
    const effect = thisItem.effects.getName('五雷').toObject();
    effect.origin = thisItem.uuid;
    for (const t of targets) {
        if (t.actor) await game.xjzl.api.effects.addEffect(t.actor, effect);
    }
}

if (args.move.type === 'basic') {
    args.flags.grantHit += 999; // 确保必中
}

if (args.move.type === 'basic') {
    args.flags.grantHit += 999;
}

if (args.type === 'waigong' && args.finalDamage > 0) {
    ChatMessage.create({content: '火浣功：受到外功伤害，对2米内敌人造成5点火焰伤害。', speaker: ChatMessage.getSpeaker({actor: actor})});
}

if (args.type === 'waigong' && args.finalDamage > 0) {
    ChatMessage.create({content: '火浣功：受到外功伤害，对2米内敌人造成10点火焰伤害。', speaker: ChatMessage.getSpeaker({actor: actor})});
}

if (args.attacker) {
    let stacks = actor.system.martial.stanceActive ? 1 : 2;
    const effect = thisItem.effects.getName('蟾毒').toObject();
    effect.origin = thisItem.uuid;
    for(let i=0; i<stacks; i++) await game.xjzl.api.effects.addEffect(args.attacker, effect);
    const ae = args.attacker.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'chandu');
    if (ae && ae.getFlag('xjzl-system', 'stacks') >= 3) {
        await args.attacker.applyDamage({ amount: 20, type: 'poison' });
        await game.xjzl.api.effects.removeEffect(args.attacker, 'chandu', 3);
    }
}

if (args.attacker) {
    let stacks = actor.system.martial.stanceActive ? 1 : 2;
    const effect = thisItem.effects.getName('蟾毒').toObject();
    effect.origin = thisItem.uuid;
    for(let i=0; i<stacks; i++) await game.xjzl.api.effects.addEffect(args.attacker, effect);
    const ae = args.attacker.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'chandu');
    if (ae && ae.getFlag('xjzl-system', 'stacks') >= 3) {
        await args.attacker.applyDamage({ amount: 40, type: 'poison' });
        await game.xjzl.api.effects.removeEffect(args.attacker, 'chandu', 3);
    }
}

if (args.attacker) {
    let stacks = actor.system.martial.stanceActive ? 1 : 2;
    const effect = thisItem.effects.getName('蟾毒').toObject();
    effect.origin = thisItem.uuid;
    for(let i=0; i<stacks; i++) await game.xjzl.api.effects.addEffect(args.attacker, effect);
    const ae = args.attacker.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'chandu');
    if (ae && ae.getFlag('xjzl-system', 'stacks') >= 3) {
        await args.attacker.applyDamage({ amount: 60, type: 'poison' });
        await game.xjzl.api.effects.removeEffect(args.attacker, 'chandu', 3);
    }
}
