             await args.attacker.update({ "system.resources.mp.value": Math.max(0, mp - 5) });
             ui.notifications.info(`${args.attacker.name} 流失 5 点内力`);
        }
    }
}

if (args.attacker && args.finalDamage > 0) {
    await args.attacker.applyDamage({ amount: 10, type: "liushi", isHit: true });
    if (!actor.system.martial.stanceActive) {
        const mp = args.attacker.system.resources.mp.value;
        if (mp > 0) {
             await args.attacker.update({ "system.resources.mp.value": Math.max(0, mp - 10) });
             ui.notifications.info(`${args.attacker.name} 流失 10 点内力`);
        }
    }
}

if (args.isCrit) {
    const triggered = thisItem.getFlag('xjzl-system', 'neigong_jinglei_critTriggered');
    if (!triggered) {
        await args.target.applyDamage({ amount: 30, type: "liushi", isHit: true });
        await thisItem.setFlag('xjzl-system', 'neigong_jinglei_critTriggered', true);
        ui.notifications.info("惊雷首暴触发！");
    }
}

if (args.move.element === 'yang') {
