scripts.script:
if (args.move.damageType === 'neigong') {
    args.output.damage += 10;
    args.output.bonusDesc.push('九霄神鹤功 +10');
}

if (args.move.damageType === 'neigong') {
    args.output.damage += 15;
    args.output.bonusDesc.push('九霄神鹤功 +15');
}

if (args.move.damageType === 'neigong') {
    args.output.damage += 20;
    args.output.bonusDesc.push('九霄神鹤功 +20');
}

if (args.target) {
    const neigongId = args.target.system.martial?.active_neigong;
    if (neigongId) {
        const ng = args.target.items.get(neigongId);
        if (ng && ng.system.element === 'yin') {
            // 攻击优势
            args.flags.grantLevel += 1;
        }
    }
}

if (args.target) {
    const neigongId = args.target.system.martial?.active_neigong;
    if (neigongId) {
        const ng = args.target.items.get(neigongId);
        if (ng && ng.system.element === 'yin') {
            // 攻击优势
            args.flags.grantLevel += 1;
            // 虚招对抗优势(我方虚招)
            args.flags.grantFeintLevel += 1;
        }
    }
}

// 1. 同步查找内功物品 (不使用 await)
const neigong = actor.items.find(i => i.name.includes("天凤心诀") && i.type === "neigong");
let bonus = 20;

// 2. 根据内功等级判断加成
if (neigong && neigong.system.stage) {
    if (neigong.system.stage >= 2) bonus = 30;
    if (neigong.system.stage >= 3) bonus = 40;
}

// 3. 应用数值到输出对象
args.output.damage += bonus;
args.output.bonusDesc.push(`惊人 +${bonus}`);

// 只有在攻击动作结算后才移除
if (args.type === "attack" || args.isAttack) {
    await thisEffect.delete();
}

if (args.baseData.isWeaponMatch) {
    args.output.damage += 5;
    args.output.bonusDesc.push("武脉九断 +5");
}

const rage = S.resources.rage.value || 0;
if (rage > 0) {
    args.output.damage += rage;
    args.output.bonusDesc.push(`众合难 +${rage}`);
}

const effect = thisItem.effects.getName("生杀 (小成)").toObject();
effect.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, effect);

const effect = thisItem.effects.getName("生杀 (圆满)").toObject();
effect.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, effect);

if (args.type !== 'attack') return;
const eff = thisItem.effects.getName("灾厄 (领悟/小成)").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);
await game.xjzl.api.effects.addEffect(args.attacker, eff);

if (args.type !== 'attack') return;
const eff = thisItem.effects.getName("灾厄 (领悟/小成)").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);
await game.xjzl.api.effects.addEffect(args.attacker, eff);

if (args.type !== 'attack') return;
const eff = thisItem.effects.getName("灾厄 (圆满)").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);
await game.xjzl.api.effects.addEffect(args.attacker, eff);

if (args.move && args.move.isUltimate) {
    args.output.damage += 10;
    args.output.bonusDesc.push("三罪落无间 (圆满): +10");
}

if (args.type === 'poison') {
    args.output.damage = 0;
    args.output.bonusDesc.push('至阳·百毒难侵 (免疫)');
}

if (args.output.damage > 0) {
    const reduce = 10;
    args.output.damage = Math.max(0, args.output.damage - reduce);
    // args.output.bonusDesc.push(`至阳护体 -${reduce}`);
}

if (!args.attacker || args.attacker.uuid === actor.uuid) return;
if (args.attacker.isReceivingReflection) return;

args.attacker.isReceivingReflection = true;
try {
    await args.attacker.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    if (actor.token?.object) {
        actor.showFloatyText("至阳反震!", { fill: "#FFA500", stroke: "#000000", strokeThickness: 4 });
    }
} finally {
    args.attacker.isReceivingReflection = false;
}

if (args.type === 'poison') {
    args.output.damage = 0;
    args.output.bonusDesc.push('至阳·百毒难侵 (免疫)');
}

if (args.output.damage > 0) {
    const reduce = 20;
    args.output.damage = Math.max(0, args.output.damage - reduce);
}

if (!args.attacker || args.attacker.uuid === actor.uuid) return;
if (args.attacker.isReceivingReflection) return;

args.attacker.isReceivingReflection = true;
try {
    await args.attacker.applyDamage({
        amount: 20,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    if (actor.token?.object) {
        actor.showFloatyText("至阳反震!", { fill: "#FFA500", stroke: "#000000", strokeThickness: 4 });
    }
} finally {
    args.attacker.isReceivingReflection = false;
}

if (args.type === 'poison') {
    args.output.damage = 0;
    args.output.bonusDesc.push('至阳·百毒难侵 (免疫)');
}

// 1. 阴柔减半
if (args.element && ['yin', 'rou'].includes(args.element)) {
    args.output.damage = Math.floor(args.output.damage / 2);
    args.output.bonusDesc.push('至阳·克制阴柔 (半伤)');
}

// 2. 固定减伤
if (args.output.damage > 0) {
    const reduce = 30;
    args.output.damage = Math.max(0, args.output.damage - reduce);
}

if (!args.attacker || args.attacker.uuid === actor.uuid) return;
if (args.attacker.isReceivingReflection) return;

args.attacker.isReceivingReflection = true;
try {
    await args.attacker.applyDamage({
        amount: 30,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    if (actor.token?.object) {
        actor.showFloatyText("至阳反震!", { fill: "#FFA500", stroke: "#000000", strokeThickness: 4 });
    }
} finally {
    args.attacker.isReceivingReflection = false;
}

if (!args.flags.damageResult) return;

// 检查目标是否存在且有内功
const target = game.user.targets.first()?.actor;
if (!target) return;

const ngId = target.system.martial?.active_neigong;
if (ngId) {
    const ngItem = target.items.get(ngId);
    // 假设系统配置中阴柔的key为 'yin' 和 'rou'
    if (ngItem && ['yin', 'rou'].includes(ngItem.system.element)) {
        args.flags.damageResult.damage *= 2;
        args.flags.damageResult.breakdown += "\n+ 至阳克阴 (伤害x2)";
    }
}

if (args.flags.level > 0) {
    const use = await foundry.applications.api.DialogV2.confirm({window:{title:"血蟒爆发"}, content:"放弃优势以爆发伤害?"});
    if (use) {
        args.flags.level = 0;
        let count = actor.getFlag('xjzl-system', 'neigong_xuemang_count') || 0;
        let bonus = Math.max(0, 20 - count * 5);
        actor.system.combat.damages.skill.mod += bonus;
        await actor.setFlag('xjzl-system', 'neigong_xuemang_count', count + 1);
        ui.notifications.info(`血蟒爆发! 伤害+${bonus}`);
    }
}

if (args.flags.level > 0) {
    const use = await foundry.applications.api.DialogV2.confirm({window:{title:"血蟒爆发"}, content:"放弃优势以爆发伤害?"});
    if (use) {
        args.flags.level = 0;
        let count = actor.getFlag('xjzl-system', 'neigong_xuemang_count') || 0;
        let bonus = Math.max(0, 40 - count * 5);
        actor.system.combat.damages.skill.mod += bonus;
        await actor.setFlag('xjzl-system', 'neigong_xuemang_count', count + 1);
        ui.notifications.info(`血蟒爆发! 伤害+${bonus}`);
    }
}
