scripts.script:
let count = actor.getFlag('xjzl-system', 'neigong_qisha_count') || 0;
count++;
if (count % 3 === 0) {
    ui.notifications.info('七杀经：本次出招不消耗内力(消耗后返还)');
    // 记录这次需要返还内力，待资源扣除后执行
    args.flags.qisha_refund = true;
}
await actor.setFlag('xjzl-system', 'neigong_qisha_count', count);

if (args.attacker.flags.qisha_refund) {
    const cost = args.move.currentCost.mp || 0;
    if (cost > 0) await args.attacker.applyHealing({amount: cost, type: 'mp'});
}

if (args.isDead) {
    await args.attacker.applyHealing({amount: 5, type: 'mp'});
    ui.notifications.info('七杀经：击杀回复5点内力');
}

if (args.isCrit) {
    await args.target.applyDamage({ amount: 20, type: 'poison', isHit: true, attacker: args.attacker });
    ui.notifications.info('六煞经：暴击附加20毒素伤害');
}

if (args.isCrit) {
    await args.target.applyDamage({ amount: 40, type: 'poison', isHit: true, attacker: args.attacker });
    await game.xjzl.api.effects.addEffect(args.target, 'yishang');
}

if (args.type === 'attack') {
    const dushu = args.attacker.system.arts.dushu.total || 0;
    const bonus = dushu * 2;
    if (bonus > 0) {
        await args.target.applyDamage({ amount: bonus, type: 'poison', isHit: true, attacker: args.attacker });
    }
}

if (args.type === 'poison' && args.output.damage > 0) {
    const roll = await new Roll('1d20').evaluate();
    if (roll.total >= 15) {
        await args.target.applyHealing({amount: args.output.damage, type: 'hp'});
        args.output.abort = true;
        ui.notifications.info(`毒邪神煞：吸收毒素 ${args.output.damage}`);
    } else {
        args.output.damage *= 2;
    }
}

if (args.type === 'attack') {
    const dushu = args.attacker.system.arts.dushu.total || 0;
    const bonus = dushu * 3;
    if (bonus > 0) {
        await args.target.applyDamage({ amount: bonus, type: 'poison', isHit: true, attacker: args.attacker });
    }
}

if (args.type === 'poison' && args.output.damage > 0) {
    const roll = await new Roll('1d20').evaluate();
    if (roll.total >= 10) {
        await args.target.applyHealing({amount: args.output.damage, type: 'hp'});
        args.output.abort = true;
        ui.notifications.info(`毒邪神煞：吸收毒素 ${args.output.damage}`);
    } else {
        args.output.damage *= 2;
    }
}

if (args.type === 'attack') {
    const dushu = args.attacker.system.arts.dushu.total || 0;
    const bonus = dushu * 4;
    if (bonus > 0) {
        await args.target.applyDamage({ amount: bonus, type: 'poison', isHit: true, attacker: args.attacker });
    }
}

if (args.type === 'poison' && args.output.damage > 0) {
    const roll = await new Roll('1d20').evaluate();
    if (roll.total >= 5) {
        await args.target.applyHealing({amount: args.output.damage, type: 'hp'});
        args.output.abort = true;
        ui.notifications.info(`毒邪神煞：吸收毒素 ${args.output.damage}`);
    } else {
        args.output.damage *= 2;
    }
}

let count = actor.getFlag('xjzl-system', 'neigong_wudu_count') || 0;
count++;
if (count % 3 === 0) {
    args.flags.applyWudu = true; // 传递给 HIT 脚本
}
await actor.setFlag('xjzl-system', 'neigong_wudu_count', count);

if (args.attacker.flags.applyWudu) {
    // 检查是否存在
    const existing = args.target.effects.find(e => e.name === '五毒');
    if (!existing) {
        const effect = thisItem.effects.getName('五毒');
        if (effect) {
            const effectData = effect.toObject();
            delete effectData._id;
            effectData.duration = { rounds: 2 };
            effectData.origin = thisItem.uuid;
            await game.xjzl.api.effects.addEffect(args.target, effectData);
            ui.notifications.info('五毒真经：施加五毒');
        }
    }
}

let count = actor.getFlag('xjzl-system', 'neigong_wudu_count') || 0;
count++;
if (count % 2 === 0) {
    args.flags.applyWudu = true;
}
await actor.setFlag('xjzl-system', 'neigong_wudu_count', count);

if (args.attacker.flags.applyWudu) {
    const existing = args.target.effects.find(e => e.name === '五毒');
    if (existing) {
        await args.target.applyDamage({ amount: 40, type: 'poison', isHit: true, attacker: args.attacker });
        await existing.delete();
        ui.notifications.info('五毒真经：毒发！');
    } else {
        const effect = thisItem.effects.getName('五毒');
        if (effect) {
            const effectData = effect.toObject();
            delete effectData._id;
            effectData.duration = { rounds: 3 };
            effectData.origin = thisItem.uuid;
            await game.xjzl.api.effects.addEffect(args.target, effectData);
            ui.notifications.info('五毒真经：施加五毒');
        }
    }
}

if (args.attacker.flags.applyWudu) {
    const existing = args.target.effects.find(e => e.name === '五毒');
    if (existing) {
        await args.target.applyDamage({ amount: 50, type: 'poison', isHit: true, attacker: args.attacker });
        await existing.delete();
        ui.notifications.info('五毒真经：毒发！');
    } else {
        const effect = thisItem.effects.getName('五毒');
        if (effect) {
            const effectData = effect.toObject();
            delete effectData._id;
            effectData.duration = { rounds: 3 };
            effectData.origin = thisItem.uuid;
            await game.xjzl.api.effects.addEffect(args.target, effectData);
            ui.notifications.info('五毒真经：施加五毒');
        }
    }
}
