scripts.script:
if (!args.target) return;
const s = args.target.system.social || {};
// 判定：恶行>=100 且 侠义<50 为恶
const isStatEvil = (s.exing >= 100 && s.xiayi < 50);
// 判定：是否有恶念显露Flag
const hasEvilFlag = args.target.getFlag('xjzl-system', 'isEvil');

if (isStatEvil || hasEvilFlag) {
    args.output.damage += 10;
    args.output.bonusDesc.push('斩妖除魔 +10');
}

if (!args.target) return;
const s = args.target.system.social || {};
const isStatEvil = (s.exing >= 100 && s.xiayi < 50);
const hasEvilFlag = args.target.getFlag('xjzl-system', 'isEvil');

if (isStatEvil || hasEvilFlag) {
    args.output.damage += 20;
    args.output.bonusDesc.push('斩妖除魔 +20');
}

if (!args.target) return;
const s = args.target.system.social || {};
const isStatEvil = (s.exing >= 100 && s.xiayi < 50);
const hasEvilFlag = args.target.getFlag('xjzl-system', 'isEvil');

if (isStatEvil || hasEvilFlag) {
    args.output.damage += 30;
    args.output.bonusDesc.push('斩妖除魔 +30');
}

const targets = game.user.targets.size > 0 ? game.user.targets : null;
if (!targets) return;

const dc = 15 + (args.S.stats.shencai.total || 0);
const sourceEffect = thisItem.effects.getName("恶念显露");

if (sourceEffect) {
    for (const t of targets) {
        await Macros.requestSave({
            target: t.actor,
            attacker: actor,
            type: "dingli",
            dc: dc,
            label: "浩然正气-恶念显露",
            onFail: sourceEffect.toObject()
        });
    }
}

if (args.target && game.combat && game.combat.turns) {
    const myId = actor.id;
    const targetId = args.target.actor.id;
    const myIndex = game.combat.turns.findIndex(c => c.actorId === myId);
    const tarIndex = game.combat.turns.findIndex(c => c.actorId === targetId);

    // 索引越小，顺序越靠前(高)
    if (myIndex !== -1 && tarIndex !== -1) {
        if (tarIndex < myIndex) { args.output.damage += 10; args.output.bonusDesc.push("非攻(后手) +10"); }
        else if (tarIndex > myIndex) { args.output.damage -= 10; args.output.bonusDesc.push("非攻(先手) -10"); }
    }
}

if (args.target && game.combat && game.combat.turns) {
    const myId = actor.id;
    const targetId = args.target.actor.id;
    const myIndex = game.combat.turns.findIndex(c => c.actorId === myId);
    const tarIndex = game.combat.turns.findIndex(c => c.actorId === targetId);

    if (myIndex !== -1 && tarIndex !== -1) {
        if (tarIndex < myIndex) { args.output.damage += 15; args.output.bonusDesc.push("非攻(后手) +15"); }
        else if (tarIndex > myIndex) { args.output.damage -= 5; args.output.bonusDesc.push("非攻(先手) -5"); }
    }
}

if (args.target && game.combat && game.combat.turns) {
    const myId = actor.id;
    const targetId = args.target.actor.id;
    const myIndex = game.combat.turns.findIndex(c => c.actorId === myId);
    const tarIndex = game.combat.turns.findIndex(c => c.actorId === targetId);

    if (myIndex !== -1 && tarIndex !== -1) {
        if (tarIndex < myIndex) { args.output.damage += 20; args.output.bonusDesc.push("非攻(后手) +20"); }
    }
}

// 仅提示，需手动消耗反应和判定半空状态
if (args.config && args.config.isHit) {
    ui.notifications.info("【纵横诀】若位于半空，可消耗反应躲过此击（请手动）。");
}

if (game.user.targets.size > 0) {
    const source = thisItem.effects.getName("鬼谷·压制(-1)");
    if (source) {
        for (const t of game.user.targets) {
            await game.xjzl.api.effects.addEffect(t.actor, source.toObject());
        }
    }
}

if (game.user.targets.size > 0) {
    const source = thisItem.effects.getName("鬼谷·压制(-2)");
    if (source) {
        for (const t of game.user.targets) {
            await game.xjzl.api.effects.addEffect(t.actor, source.toObject());
        }
    }
}

if (game.user.targets.size > 0) {
    const source = thisItem.effects.getName("鬼谷·压制(-3)");
    if (source) {
        for (const t of game.user.targets) {
            await game.xjzl.api.effects.addEffect(t.actor, source.toObject());
        }
    }
}

const enemies = game.combat.combatants.filter(c => c.token.disposition < 0);
const source = thisItem.effects.getName("法禁·刑(-1)");
if (source && enemies.length > 0) {
    for (const c of enemies) {
        await game.xjzl.api.effects.addEffect(c.actor, source.toObject());
    }
}

const enemies = game.combat.combatants.filter(c => c.token.disposition < 0);
const source = thisItem.effects.getName("法禁·刑(-2)");
if (source && enemies.length > 0) {
    for (const c of enemies) {
        await game.xjzl.api.effects.addEffect(c.actor, source.toObject());
    }
}

const enemies = game.combat.combatants.filter(c => c.token.disposition < 0);
const allies = game.combat.combatants.filter(c => c.token.disposition >= 0);
const srcXing = thisItem.effects.getName("法禁·刑(-2)");
const srcShang = thisItem.effects.getName("法禁·赏(+2)");

if (srcXing) { for (const c of enemies) await game.xjzl.api.effects.addEffect(c.actor, srcXing.toObject()); }
if (srcShang) { for (const c of allies) await game.xjzl.api.effects.addEffect(c.actor, srcShang.toObject()); }
