scripts.script:
// 1. 定义受影响的六大属性
const validStats = ["liliang", "shenfa", "tipo", "neixi", "qigan", "shencai"];

// 2. 给属性本身加优势
for (const s of validStats) {
    const flag = s + "CheckLevel";
    actor.xjzlStatuses[flag] = (actor.xjzlStatuses[flag] || 0) + 1;
}

// 3. 给对应的技能加优势 (排除悟性系技能)
// 悟性系技能列表 (硬编码排除，比判断属性归属更直接)
const wuxingSkills = ["wuxue", "jianding", "bagua", "shili"];

for (const sk of Object.keys(CONFIG.XJZL.skills)) {
    if (!wuxingSkills.includes(sk)) {
        const flag = sk + "CheckLevel";
        actor.xjzlStatuses[flag] = (actor.xjzlStatuses[flag] || 0) + 1;
    }
}

if (args.move.type === 'feint') {
    args.output.damage += 15;
    args.output.bonusDesc.push("仁经要略: +15");
}

if (args.move.type === 'feint') {
    args.output.damage += 20;
    args.output.bonusDesc.push("仁经要略: +20");
}

const t = args.target;
const isEvil = (t.system.social?.exing >= 100 && t.system.social?.xiayi < 50) || t.effects.some(e => e.getFlag("xjzl-system", "slug") === "shengde_evil_mark");
if (isEvil) {
    args.config.amount += 10;
    ui.notifications.info("圣德天书：感应到恶念，伤害+10");
}

const t = args.target;
const isEvil = (t.system.social?.exing >= 100 && t.system.social?.xiayi < 50) || t.effects.some(e => e.getFlag("xjzl-system", "slug") === "shengde_evil_mark");
if (isEvil) {
    args.config.amount += 20;
    ui.notifications.info("圣德天书：感应到恶念，伤害+20");
}

const t = args.target;
const isEvil = (t.system.social?.exing >= 100 && t.system.social?.xiayi < 50) || t.effects.some(e => e.getFlag("xjzl-system", "slug") === "shengde_evil_mark");
if (isEvil) {
    args.config.amount += 30;
    ui.notifications.info("圣德天书：感应到恶念，伤害+30");
}

const dc = 15 + Math.floor(S.stats.shencai.total / 10);
await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "dingli",
    dc: dc,
    label: "圣德天书威压",
    onFail: {
        name: "恶念显露",
        icon: "icons/svg/mystery-man.svg",
        description: "在圣德天书持有者眼中视为[恶]标签",
        flags: { "xjzl-system": { slug: "shengde_evil_mark" } }
    }
});

S.combat.damages.weapon.mod += 10;
S.arts.duanzao.checkMod += 1;
S.arts.chengyi.checkMod += 1;
S.arts.zhibao.checkMod += 1;

if (game.combat) {
    const turns = game.combat.turns;
    const myIdx = turns.findIndex(c => c.actorId === actor.id);
    const tIdx = turns.findIndex(c => c.actorId === args.target.id);
    if (myIdx !== -1 && tIdx !== -1) {
        if (tIdx < myIdx) args.config.amount += 10; // Target higher order
        else if (tIdx > myIdx) args.config.amount -= 10;
    }
}

if (game.combat) {
    const turns = game.combat.turns;
    const myIdx = turns.findIndex(c => c.actorId === actor.id);
    const tIdx = turns.findIndex(c => c.actorId === args.target.id);
    if (myIdx !== -1 && tIdx !== -1) {
        if (tIdx < myIdx) args.config.amount += 15;
        else if (tIdx > myIdx) args.config.amount -= 5;
    }
}

if (game.combat) {
    const turns = game.combat.turns;
    const myIdx = turns.findIndex(c => c.actorId === actor.id);
    const tIdx = turns.findIndex(c => c.actorId === args.target.id);
    if (myIdx !== -1 && tIdx !== -1) {
        if (tIdx < myIdx) args.config.amount += 20;
    }
}

const effectData = {
    name: "鬼谷压制",
    icon: "icons/svg/downgrade.svg",
    flags: { "xjzl-system": { slug: "guigu_dodge_debuff", stackable: true } },
    changes: [{ key: "system.combat.dodge", mode: 2, value: -1 }]
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

const effectData = {
    name: "鬼谷压制",
    icon: "icons/svg/downgrade.svg",
    flags: { "xjzl-system": { slug: "guigu_dodge_debuff", stackable: true } },
    changes: [{ key: "system.combat.dodge", mode: 2, value: -1 }]
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

const effectData = {
    name: "鬼谷压制II",
    icon: "icons/svg/downgrade.svg",
    flags: { "xjzl-system": { slug: "guigu_dodge_debuff_2", stackable: true } },
    changes: [{ key: "system.combat.dodge", mode: 2, value: -2 }]
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

const effectData = {
    name: "鬼谷压制II",
    icon: "icons/svg/downgrade.svg",
    flags: { "xjzl-system": { slug: "guigu_dodge_debuff_2", stackable: true } },
    changes: [{ key: "system.combat.dodge", mode: 2, value: -2 }]
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

const effectData = {
    name: "鬼谷压制III",
    icon: "icons/svg/downgrade.svg",
    flags: { "xjzl-system": { slug: "guigu_dodge_debuff_3", stackable: true } },
    changes: [{ key: "system.combat.dodge", mode: 2, value: -3 }]
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

// 随机状态
if (!args.target.system.martial.stanceActive) {
    const roll = Math.floor(Math.random() * 4) + 1;
    let msg = "";
    if (roll === 1) msg = "击飞目标3米 (手动)";
    else if (roll === 2) {
        msg = "目标倒地";
        await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
    }
    else if (roll === 3) {
        msg = "错骨";
        await game.xjzl.api.effects.toggleStatus(args.target, "cuogu", true);
    }
    else if (roll === 4) {
        msg = "缚身";
        await game.xjzl.api.effects.toggleStatus(args.target, "fushen", true);
    }
    ui.notifications.info(`鬼谷玄功触发: ${msg}`);
}

const effectData = {
    name: "鬼谷压制III",
    icon: "icons/svg/downgrade.svg",
    flags: { "xjzl-system": { slug: "guigu_dodge_debuff_3", stackable: true } },
    changes: [{ key: "system.combat.dodge", mode: 2, value: -3 }]
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (args.target.system.social?.exing > 0) {
    args.flags.grantHit += 5;
}

if (args.target.system.resources?.shanie > 0) {
    args.config.amount += 10;
}

if (args.target.system.social?.exing > 0) {
    args.flags.grantHit += 10;
}

if (args.target.system.resources?.shanie > 0) {
    args.config.amount += 15;
}

if (game.combat) {
    const enemies = game.combat.combatants.filter(c => c.token.disposition === -1).map(c => c.actor);
    const effectData = {
        name: "刑(检定-1)",
        icon: "icons/svg/skull.svg",
        description: "投掷检定结果-1（请手动调整）。",
        changes: []
    };
    for (const enemy of enemies) {
        await game.xjzl.api.effects.addEffect(enemy, effectData);
    }
}

if (game.combat) {
    const enemies = game.combat.combatants.filter(c => c.token.disposition === -1).map(c => c.actor);
    const effectData = {
        name: "刑(检定-2)",
        icon: "icons/svg/skull.svg",
        description: "投掷检定结果-2（请手动调整）。",
        changes: []
    };
    for (const enemy of enemies) {
        await game.xjzl.api.effects.addEffect(enemy, effectData);
    }
}

if (game.combat) {
    const enemies = game.combat.combatants.filter(c => c.token.disposition === -1).map(c => c.actor);
    const allies = game.combat.combatants.filter(c => c.token.disposition === 1).map(c => c.actor);

    const xing = {
        name: "刑(检定-2)",
        icon: "icons/svg/skull.svg",
        description: "投掷检定结果-2（请手动调整）。",
        changes: []
    };
    const shang = {
        name: "赏(检定+2)",
        icon: "icons/svg/angel.svg",
        description: "投掷检定结果+2（请手动调整）。",
        changes: []
    };

    for (const e of enemies) await game.xjzl.api.effects.addEffect(e, xing);
    for (const a of allies) await game.xjzl.api.effects.addEffect(a, shang);
}
