scripts.script:
const target = args.target;
// 检查流血相关状态 (bleed_stack 是系统流血 slug，sielie 是撕裂，bloodloss 是失血)
const hasBleed = target.effects.some(e => 
    ['bleed_stack', 'sielie', 'bloodloss'].includes(e.getFlag('xjzl-system', 'slug'))
);

if (hasBleed) {
    await target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info(`${thisItem.name} 对流血目标造成额外流失伤害！`);
}

// 1. 读取当前回合计数
let count = actor.getFlag('xjzl-system', 'weapon_hunshifu_count') || 0;
count += 1;
await actor.setFlag('xjzl-system', 'weapon_hunshifu_count', count);

// 2. 判断是否第三次
if (count === 3) {
    await game.xjzl.api.effects.addEffect(actor, 'xujin');
    ui.notifications.info('混世斧：已出招三次，获得蓄劲！');
}

if (args.isCrit) {
    await game.xjzl.api.effects.addEffect(actor, 'xujin');
    ui.notifications.info('魔影刀：暴击触发蓄劲！');
}

if (args.isHit) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

const cooking = S.arts?.pengren?.total || 0;
if (cooking > 0) {
    args.output.damage += cooking;
    args.output.bonusDesc.push(`烹饪加持(+${cooking})`);
}

if (args.isHit) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

if (args.isHit && args.target.statuses.has('blind')) {
    await args.target.applyDamage({
        amount: 10,
        type: 'waigong',
        attacker: actor,
        isHit: true,
        ignoreDefense: false
    });
    ui.notifications.info('鸣沙刀：对目盲目标造成额外伤害！');
}

const farm = S.arts?.nongshi?.total || 0;
if (farm > 0) {
    args.output.damage += farm;
    args.output.bonusDesc.push(`农事加持(+${farm})`);
}

if (args.damageResult && args.damageResult.isDying) {
    const healAmount = Math.floor(actor.system.resources.hp.max * 0.1);
    await actor.applyHealing({ amount: healAmount, type: 'hp' });
    ui.notifications.info('蚩尤古刀：饮血回春！');
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, 'bleed_stack');
}

const used = actor.getFlag('xjzl-system', 'weapon_qinglong_used');
if (!used) {
    args.flags.critThresholdMod += 20;
    // 设置临时标记供 calc 脚本使用
    actor.tempQinglongActive = true;
    // 标记已使用
    await actor.setFlag('xjzl-system', 'weapon_qinglong_used', true);
    ui.notifications.warn('青龙偃月刀：拖刀计触发！');
}

if (actor.tempQinglongActive) {
    args.output.damage += 10;
    args.output.bonusDesc.push('青龙首刀(+10)');
    // 清理临时标记
    delete actor.tempQinglongActive;
}

if (args.isHit && !args.target.system.martial?.stanceActive) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info('镀金苗刀：趁虚而入！');
}

if (args.isHit && !args.target.system.martial?.stanceActive) {
    const effect = CONFIG.statusEffects.find(e => e.id === 'jinliao');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
    ui.notifications.info('蘸金宣花斧：施加禁疗！');
}

if (args.isHit && args.move.type === 'basic') {
    const effect = CONFIG.statusEffects.find(e => e.id === 'blind');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
    ui.notifications.info('月牙钺：扬沙迷眼！');
}

if (args.damageResult && args.damageResult.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, 'bleed_stack');
    ui.notifications.info('凤头鱼尾斧：击破架招，造成流血！');
}

if (args.isHit && args.move.type === 'counter') {
    const effect = CONFIG.statusEffects.find(e => e.id === 'fengzhao');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
    ui.notifications.info('锟铻双刀：反击封招！');
}

if (args.isHit && !args.isCrit) {
    await game.xjzl.api.effects.addEffect(args.target, 'prone');
    ui.notifications.info('龙鳞双刀：横扫击倒！');
}

if (args.damageResult && args.damageResult.isDead) {
    const currentBonus = S.resources.mp.bonus || 0;
    if (currentBonus < 100) {
        await actor.update({'system.resources.mp.bonus': currentBonus + 1});
        ui.notifications.info('吸魂邪刀：吞噬生魂！');
    }
}

if (args.isHit && !args.target.system.martial?.stanceActive) {
    const effect = CONFIG.statusEffects.find(e => e.id === 'fengzhao');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
}

if (args.isCrit) {
    const effect = CONFIG.statusEffects.find(e => e.id === 'jiaoxie');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
    ui.notifications.info('割鹿神刀：斩断兵刃！');
}

const used = actor.getFlag('xjzl-system', 'weapon_qixing_daily');
if (!used) {
    await actor.applyHealing({ amount: 10, type: 'hp' });
    await actor.setFlag('xjzl-system', 'weapon_qixing_daily', true);
    ui.notifications.info('七星宝刀：护主续命！');
}

const effect = thisItem.effects.getName('戒律');
if (effect) {
    await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

if (args.damageResult) {
    const dmg = args.damageResult.finalDamage || 0;
    if (dmg > 0) {
        const heal = Math.floor(dmg * 0.1);
        await actor.applyHealing({ amount: heal, type: 'hp' });
    }
}

if (args.isHit) {
    const hasWeapon = args.target.items.some(i => i.type === 'weapon' && i.system.equipped);
    if (!hasWeapon) {
        await args.target.applyDamage({
            amount: 20,
            type: 'waigong',
            attacker: actor,
            isHit: true,
            ignoreDefense: false
        });
        ui.notifications.info('古锭刀：手无寸铁者斩！');
    }
}

const effect = thisItem.effects.getName('刀意');
if (effect) {
    await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

const bonus = Math.min(20, S.resources.shanie.value || 0);
if (bonus > 0) {
    args.output.damage += bonus;
    args.output.bonusDesc.push(`杀孽加持(+${bonus})`);
}

let stacks = 0;
// 检测年龄
if (S.info.age > 40) stacks++;
// 检测体重 (字符串解析)
const weight = parseInt(S.info.weight) || 0;
if (weight > 200) stacks++;

if (stacks > 0) {
    S.combat.damages.weapon.mod += (stacks * 5);
    S.combat.block += (stacks * 5);
}

if (args.isHit) {
    const existing = args.target.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'yugu_spine');
    const currentStacks = existing ? (existing.getFlag('xjzl-system', 'stacks') || 1) : 0;

    if (currentStacks >= 2) { // 本次加完就是3层
        if (existing) await existing.delete();
        const rootEffect = CONFIG.statusEffects.find(e => e.id === 'root');
        const data = foundry.utils.deepClone(rootEffect);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
        ui.notifications.info('鱼骨刀：骨刺爆发，禁足！');
    } else {
        const aeData = {
            name: '骨刺',
            icon: 'icons/svg/bones.svg',
            flags: { 'xjzl-system': { slug: 'yugu_spine', stackable: true, maxStacks: 3 } }
        };
        await game.xjzl.api.effects.addEffect(args.target, aeData);
    }
}

if (S.info.personality.includes('鲁莽')) {
    S.combat.damages.weapon.mod += 10;
}

if (args.isHit && args.move.isUltimate) {
    const effect = CONFIG.statusEffects.find(e => e.id === 'cuogu');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
}

if (args.isHit && args.target.system.martial?.stanceActive) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: 'jiaoli',
        dc: S.stats.liliang.total / 10 + 10,
        label: '天之伤对抗',
        onFail: async () => { ui.notifications.info('天之伤：对抗胜利，请手动击退目标！'); }
    });
}

if (args.damageResult && args.damageResult.isBroken) {
    const effect = CONFIG.statusEffects.find(e => e.id === 'unstable');
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
}

if (args.move.id === 'opportunity-attack') {
    args.output.damage += 5;
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit && args.target) {
    // 下盘不稳是系统状态 (unstable)
    // 创建一个 override 的效果数据以指定持续时间
    const effectData = {
        id: "unstable",
        duration: { rounds: 2 }
    };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isHit && args.target) {
    // 额外造成20点流失伤害
    await args.target.applyDamage({
        amount: 20,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info(`${actor.name} 破釜沉舟，造成额外流失伤害！`);
}

if (args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({
        amount: 10,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit && args.target) {
    const effectData = {
        id: "unstable",
        duration: { rounds: 2 }
    };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isHit && args.target) {
    await args.target.applyDamage({
        amount: 20,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info(`${actor.name} 破釜沉舟，造成额外流失伤害！`);
}

if (args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({
        amount: 10,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.type === 'attack' && args.isHit) {
    const statusData = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (statusData) {
        const effectData = foundry.utils.deepClone(statusData);
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: 'bleed',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info('破釜沉舟：造成额外20点流失伤害！');
}

if (args.type === 'attack' && args.isHit && args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: 'bleed',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.warn('蟠龙触发：暴击附加10点流失！');
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit) {
    const statusData = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (statusData) {
        const effectData = foundry.utils.deepClone(statusData);
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: 'bleed',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info('破釜沉舟：造成额外20点流失伤害！');
}

if (args.type === 'attack' && args.isHit && args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: 'bleed',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.warn('蟠龙触发：暴击附加10点流失！');
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit) {
    const statusData = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (statusData) {
        const effectData = foundry.utils.deepClone(statusData);
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: 'bleed',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info('破釜沉舟：造成额外20点流失伤害！');
}

if (args.type === 'attack' && args.isHit && args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: 'bleed',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.warn('蟠龙触发：暴击附加10点流失！');
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}
