scripts.script:
const t = args.target.system.social;
// 恶人判定: 恶行>=100 且 侠义<50
const isEvil = (t.exing >= 100 && t.xiayi < 50);
if (isEvil) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: 'rennai',
        dc: 11,
        label: '抵抗拷鬼尺',
        onFail: async () => {
            await game.xjzl.api.effects.addEffect(args.target, 'deaf');
            ui.notifications.info(`${args.target.name} 被震得耳鸣了！`);
        }
    });
}

const effect = thisItem.effects.getName('黑血');
if (effect) {
    await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
}

if (args.isCrit) {
    const effect = thisItem.effects.getName('空莲易伤');
    if (effect) {
        await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
        ui.notifications.info('空莲扇暴击！施加易伤！');
    }
}

if (args.isCrit) {
    ui.notifications.info('峨眉刺暴击触发！你可以免费施展一次普通攻击。');
}

const xiayi = S.social.xiayi || 0;
const bonus = Math.floor(xiayi / 10);
if (bonus > 0) {
    args.output.damage += bonus;
    args.output.bonusDesc.push(`侠义加持(+${bonus})`);
}

if (args.damageResult?.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, 'blind');
    ui.notifications.warn(`${args.target.name} 被龙睛笔戳瞎了眼睛！`);
}

if (args.target && args.target.system.stats.shencai.total < 20) {
    args.config.amount += 10;
    ui.notifications.info('剔红笔：神采压制！伤害+10');
}

if (args.damageResult?.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, 'blind');
}

// 使用宏助手检查格挡 (严格模式：需开启架招)
if (Macros.checkStance(actor, args)) {
    // 从物品自身的特效列表中获取配置，严禁硬编码
    const effect = thisItem.effects.getName('逍遥扇');
    if (effect) {
        await game.xjzl.api.effects.addEffect(actor, effect.toObject());
        ui.notifications.info('逍遥扇：格挡成功，神采激荡！');
    }
}

if (Macros.checkStance(actor, args)) {
    await actor.applyHealing({ amount: 5, type: 'mp' });
}

if (args.isBroken && args.attacker && args.attacker.uuid !== actor.uuid) {
    if (actor.isReceivingReflection) return;
    args.attacker.isReceivingReflection = true;
    try {
        const dmg = S.combat.blockTotal;
        await args.attacker.applyDamage({ amount: dmg, type: 'true', isHit: true, attacker: actor });
        await actor.stopStance();
        ui.notifications.info('界方尺触发！反伤并解除架招。');
    } finally {
        args.attacker.isReceivingReflection = false;
    }
}

if (args.damageResult?.isBroken) {
    const effect = thisItem.effects.getName('破戒');
    if (effect) {
        await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
    }
}

if (actor.statuses.has('prone')) {
    args.output.damage += 10;
    args.output.bonusDesc.push('倒地反击(+10)');
}

if (!args.target.system.martial?.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, 'prone');
    ui.notifications.info(`${args.target.name} 被马面钩勾倒在地！`);
}

if (args.isBroken) {
    await actor.applyHealing({ amount: 1, type: 'rage' });
    ui.notifications.info('伏妖双刺：怒气勃发！');
}

await args.target.applyDamage({ 
    amount: 15, 
    type: 'true', 
    isHit: true, 
    attacker: actor 
});
ui.notifications.info(`${args.target.name} 被子午鸳鸯刺割伤，流失15气血！`);

const bonus = S.skills.qianxing.total || 0;
if (bonus > 0) {
    args.output.damage += bonus;
    args.output.bonusDesc.push(`潜行加持(+${bonus})`);
}

const shanie = S.resources.shanie || 0;
if (shanie > 0) {
    await actor.update({'system.resources.huti': shanie});
    ui.notifications.info(`虞帝匕首：获得 ${shanie} 点护体`);
}

if (S.social.rep_chaoting < 0) {
    args.output.damage += 10;
    args.output.bonusDesc.push('逆臣(+10)');
}

if (S.social.rep_wulin < 0) {
    S.combat.block += 10;
}

if (args.damageResult?.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, 'cuogu');
}

if (args.target && args.target.statuses.has('cuogu')) {
    args.config.amount += 10;
    ui.notifications.info('金刚锥：错骨追击(+10)');
}

if (S.resources.shalu.value > 0) {
    args.flags.critThresholdMod += 3;
}

// 基础校验
if (!args.attacker || args.attacker.uuid === actor.uuid) return;

// 检查格挡是否生效 (Macros.checkStance 包含了是否开启架招、是否被无视的判断)
// 断玉匕首只要“格挡成功”即可，不一定非要开启架招（比如有被动格挡），所以我们手动判断
const blockEffective = (!args.config.ignoreBlock && S.combat.blockTotal > 0);

if (blockEffective) {
    if (actor.isReceivingReflection) return;
    args.attacker.isReceivingReflection = true;
    try {
         await args.attacker.applyDamage({ amount: 5, type: 'true', isHit: true, attacker: actor });
         ui.notifications.info('断玉匕首：格挡反伤触发！');
    } finally {
         args.attacker.isReceivingReflection = false;
    }
}

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: 'true', isHit: true, attacker: actor });
    ui.notifications.info('赤血匕首暴击！追加10点流失。');
}

if (args.move && args.move.type === 'basic') {
    await args.target.applyDamage({ amount: 10, type: 'true', isHit: true, attacker: actor });
}

if (args.damageResult?.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, 'bleed_stack');
}

const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'unstable'));
if (effectData) {
    effectData.duration = { rounds: 2 };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: 'true', isHit: true, attacker: actor });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp' });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp' });
}

if (args.type === 'attack' && args.isHit) {
    const effectData = {
        id: 'unstable',
        duration: { rounds: 2 }
    };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${actor.name} 的攻击使 ${args.target.name} 下盘不稳！`);
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({ amount: 20, type: 'liushi', attacker: actor, isHit: true, ignoreDefense: true });
}

if (args.type === 'attack' && args.isHit && args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', attacker: actor, isHit: true, ignoreDefense: true });
    ui.notifications.info('蟠龙触发：额外气血流失！');
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp' });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp' });
}

if (args.type === 'attack' && args.isHit) {
    const effectData = {
        id: 'unstable',
        duration: { rounds: 2 }
    };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${actor.name} 的攻击使 ${args.target.name} 下盘不稳！`);
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({ amount: 20, type: 'liushi', attacker: actor, isHit: true, ignoreDefense: true });
}

if (args.type === 'attack' && args.isHit && args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', attacker: actor, isHit: true, ignoreDefense: true });
    ui.notifications.info('蟠龙触发：额外气血流失！');
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp' });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "unstable"));
    if (effectData) {
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("破釜沉舟：额外造成流失伤害！");
}

if (args.type === 'attack' && args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("蟠龙：暴击触发额外流失！");
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "unstable"));
    if (effectData) {
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("破釜沉舟：额外造成流失伤害！");
}

if (args.type === 'attack' && args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("蟠龙：暴击触发额外流失！");
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}
