scripts.script:
if (!args.isHit) return;

// 1. 发起角力检定
await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "jiaoli",
    dc: 15,
    label: "抵抗锁喉镖",
    onFail: async () => {
        // 2. 失败应用状态 (禁言+禁内力)
        // 使用系统通用的 silence (封穴/禁言) 和 qizhi (气滞/禁内力)
        await game.xjzl.api.effects.addEffect(args.target, "silence");
        await game.xjzl.api.effects.addEffect(args.target, "qizhi");
        ui.notifications.warn(`${args.target.name} 被锁喉镖锁住！无法发声且无法使用内力！`);
    }
});

if (!args.isHit) return;
// 判定造成了实际伤害/破防
if (args.damageResult && args.damageResult.finalDamage > 0) {
    // 从物品特效中获取配置，杜绝硬编码
    const sourceEffect = thisItem.effects.getName("糖毒");
    if (sourceEffect) {
        await game.xjzl.api.effects.addEffect(args.target, sourceEffect.toObject());
        ui.notifications.info("糖毒入体！");
    }
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit) {
    // 使用系统状态 unstable
    await game.xjzl.api.effects.addEffect(args.target, {
        id: "unstable",
        duration: { rounds: 2 }
    });
    ui.notifications.info("霜月侵袭！");
}

if (args.outcome.isCrit) {
    args.config.amount += 10;
    ui.notifications.info("蟠龙飞刀触发暴击增伤！");
}

if (args.isHit) {
    await args.target.applyDamage({ amount: 5, type: "true", isHit: true });
    ui.notifications.info(`${args.target.name} 被铸血飞刀撕裂伤口！`);
}

if (!args.isHit || !args.move) return;
// 检查招式名称
if (args.move.name.includes("狂蛇锥法") && args.damageResult.finalDamage > 0) {
    const sourceEffect = thisItem.effects.getName("狂蛇");
    if (sourceEffect) {
        await game.xjzl.api.effects.addEffect(actor, sourceEffect.toObject());
    }
}

if (!args.isHit) return;
if (args.damageResult && args.damageResult.finalDamage > 0) {
    const sourceEffect = thisItem.effects.getName("血滴");
    if (sourceEffect) {
        await game.xjzl.api.effects.addEffect(args.target, sourceEffect.toObject());
    }
}

if (!args.isHit) return;
new Dialog({
    title: "药王针",
    content: "<p>选择效果：</p>",
    buttons: {
        heal: {
            label: "回复5气血",
            callback: async () => await args.target.applyHealing({ amount: 5, type: "hp" })
        },
        harm: {
            label: "流失5气血",
            callback: async () => await args.target.applyDamage({ amount: 5, type: "true", isHit: true })
        }
    }
}).render(true);

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, {
        id: "fushen",
        duration: { rounds: 1 }
    });
}

if (args.isHit) {
    await args.target.applyDamage({ amount: 5, type: "true", isHit: true });
}

if (args.isHit) {
    const sourceEffect = thisItem.effects.getName("五殃");
    if (sourceEffect) {
        await game.xjzl.api.effects.addEffect(args.target, sourceEffect.toObject());
    }
}

if (args.isHit) {
    const sourceEffect = thisItem.effects.getName("七邪");
    if (sourceEffect) {
        // 1. 添加/叠加特效
        const effect = await game.xjzl.api.effects.addEffect(args.target, sourceEffect.toObject());

        // 2. 检查层数是否爆炸
        const stacks = effect.getFlag("xjzl-system", "stacks") || 1;
        if (stacks >= 7) {
            // 爆发逻辑
            const rage = args.target.system.resources.rage.value || 0;
            const damage = rage * 70;
            ui.notifications.warn(`${args.target.name} 七邪发作！受到 ${damage} 点爆发伤害！`);

            // 造成伤害
            await args.target.applyDamage({ amount: damage, type: "true", isHit: true });

            // 移除特效
            await effect.delete();
        }
    }
}

// 仅当目标拥有七邪状态时生效
const qixie = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "qixie");
if (qixie) {
    const stacks = qixie.getFlag("xjzl-system", "stacks") || 0;
    // 每层使暴击骰+1 (阈值降低，更容易暴击)
    args.flags.critThresholdMod += stacks;
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, {
        id: "unstable",
        duration: { rounds: 1 }
    });
}

if (!args.isHit) return;
if (args.damageResult && args.damageResult.finalDamage > 0) {
    await game.xjzl.api.effects.addEffect(args.target, "bleed_stack");
}

if (args.isHit && args.isCrit) {
    // 复用流血状态
    await game.xjzl.api.effects.addEffect(args.target, "bleed_stack");
    ui.notifications.info("血鸦镖造成重创，目标血流不止！");
}

if (args.isHit) {
    await args.target.applyDamage({ amount: 10, type: "poison", isHit: true });
}

const selfSpeed = actor.system.combat.speedTotal || 0;
const targetSpeed = args.target.system.combat.speedTotal || 0;
if (targetSpeed < selfSpeed) {
    // 暴击骰-2 意味着更难暴击？通常语境是“更容易”。
    // 假设语境是 Threshold 降低 (更容易)，如 20 -> 18.
    // 系统中 critThresholdMod 正数表示更容易。
    args.flags.critThresholdMod += 2;
}

// 检测是否击破架招
if (args.isBroken) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "renxing",
        dc: 16,
        label: "浮生断梦",
        onFail: async () => {
            await game.xjzl.api.effects.addEffect(args.target, "unconscious"); // 昏迷
            ui.notifications.info(`${args.target.name} 陷入昏迷！`);
        }
    });
}

// 获取目标棋术等级
const qishu = args.target.system.arts?.qishu?.total || 0;
if (qishu <= 0) {
    // 强制暴击：大幅降低阈值 (例如 +20，使得 20-20=0，任何骰值都暴击)
    args.flags.critThresholdMod += 20;
    // ui.notifications.info("对手不懂棋术，破绽百出！(必定暴击)");
}

const qishu = actor.system.arts.qishu?.total || 0;
if (qishu > 0) {
    args.output.damage += qishu;
    args.output.bonusDesc.push(`棋术加持(+${qishu})`);
}

if (args.isBroken) {
    let dc = 17;
    if (args.target.system.resources.shanie > 10) dc += 5;

    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "dingli",
        dc: dc,
        label: "无涯心魔",
        onFail: async () => {
            await game.xjzl.api.effects.addEffect(args.target, {
                id: "rage",
                duration: { rounds: 2 }
            });
        }
    });
}

// 判定格挡：造成了外/内功伤害 AND 目标架招未被击破
if (["waigong", "neigong"].includes(args.damageType)) {
    // 如果 isBroken 为 false，说明格挡成功/未破防
    // 注意：damageResult.isBroken 为 true 时代表破招
    if (!args.isBroken) {
         await game.xjzl.api.effects.addEffect(args.target, "blind");
         ui.notifications.info("灿金飞刀：攻击被格挡，目标致盲！");
    }
}

// 检测目标是否有流血类特效 (slug匹配)
const hasBleed = args.target.effects.some(e => 
    e.getFlag("xjzl-system", "slug") === "bleed_stack" || 
    e.getFlag("xjzl-system", "slug") === "sielie"
);
if (hasBleed) {
    // 赋予10点额外伤害 (Flags需系统支持)
    args.flags.bonusDamage = 10;
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "shizhun");
}

if (args.isHit && args.isCrit) {
    const currentRage = args.target.system.resources.rage.value || 0;
    if (currentRage > 0) {
        await args.target.update({ "system.resources.rage.value": currentRage - 1 });
        ui.notifications.info(`${args.target.name} 怒气受损 (-1)`);
    }
}

// 获取物品内的特效模板
const poisonEffect = thisItem.effects.getName("蜂毒");
if (!poisonEffect) return ui.notifications.warn("未找到蜂毒特效模板");

// 发起韧性检定 DC 13
await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "renxing",
    dc: 13,
    label: "抵抗蜂毒",
    onFail: async () => {
        // 判定失败，应用蜂毒
        // 需要将模板转为对象，并设置为非传输(transfer=false)
        const effectData = poisonEffect.toObject();
        delete effectData._id;
        effectData.disabled = false;
        effectData.transfer = false;
        effectData.origin = thisItem.uuid;

        await game.xjzl.api.effects.addEffect(args.target, effectData);
    }
});

const stacks = thisEffect.stacks || 1;
const damage = 5 * stacks;
await actor.applyDamage({ amount: damage, type: "poison", isHit: true, ignoreDefense: true });

const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "unstable"));
effectData.duration = { rounds: 2 };
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: "liushi", attacker: actor, isHit: true, ignoreDefense: true });
}

const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "unstable"));
effectData.duration = { rounds: 2 };
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: "liushi", attacker: actor, isHit: true, ignoreDefense: true });
}

const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "unstable"));
effectData.duration = { rounds: 2 };
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: "liushi", attacker: actor, isHit: true, ignoreDefense: true });
}
