scripts.script:
const effect = thisItem.effects.getName("惜余琴");
if (effect) {
    await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

if (args.type !== 'buff') return;
if (args.target.id === actor.id) return; // 假设只对别人生效，根据语境“友方”

let healAmount = 10;

// 简单的异性判断
const selfGender = actor.system.info?.gender || "";
const targetGender = args.target.system.info?.gender || "";

// 如果双方都写了性别且不同
if (selfGender && targetGender && selfGender !== targetGender) {
    healAmount += 20;
}

await args.target.applyHealing({ amount: healAmount, type: "hp" });

if (!args.isHit) return;
// 添加 'unstable' (下盘不稳) 系统状态
// 这里设置 duration 1 round
const effectData = {
    id: "unstable", // 系统预设ID
    duration: { rounds: 1 }
};
await game.xjzl.api.effects.addEffect(args.target, effectData);

const yanzou = actor.system.arts.yanzou.total || 0;
const jiaoli = actor.system.skills.jiaoli.total || 0;
const extra = yanzou + jiaoli;

args.output.damage += extra;
args.output.bonusDesc.push(`金喇叭(+${extra})`);

if (!args.isCrit) return;

// 自身加士气
const res = actor.system.resources.morale;
const newVal = Math.min(res.max, (res.value || 0) + 10);
await actor.update({ "system.resources.morale.value": newVal });

ui.notifications.info("沙场号角暴击！自身士气已加，请手动为10米内友方增加10点士气。");

// 查找所有友方
const myToken = actor.token?.object || actor.getActiveTokens()[0];
if (!myToken) return;
const allies = canvas.tokens.placeables.filter(t => t.document.disposition === myToken.document.disposition);

// 乘风是系统状态 (id: chengfeng)
for (const t of allies) {
    if (t.actor) {
        await game.xjzl.api.effects.addEffect(t.actor, "chengfeng");
    }
}
ui.notifications.info("马头琴声起，全军乘风！");

if (args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: "mental",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("南音琵琶触发：额外10点精神伤害！");
}

if (args.isHit) {
    await args.target.applyDamage({
        amount: 30,
        type: "mental",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

// 耳鸣是 deaf
if (args.target.statuses.has("deaf")) {
    // 增加自身针对该目标的虚招对抗优势
    // feintLevel 是优劣势层级，题目说是“虚招值+4”，通常指数值
    // 如果是指 combat.xuzhao +4，则无法在 check 阶段动态修改数值面板
    // 假设是“虚招对抗层级+4” (极为强大) 或者 “数值+4”
    // 由于系统只暴露了 feintLevel (flags)，我们这里理解为给 1 个层级的优势，或者如果系统支持数值修改的话...
    // 修正：脚本系统不支持在 CHECK 阶段修改招式的基础虚招值(feintVal)，只能修改对抗层级(feintLevel)
    // 如果必须加数值，需要在 CALC 阶段预判目标，但 CALC 不知道目标。
    // 折衷方案：给予 2 层虚招优势 (相当强力，约等于 +4~+10 数值效果)
    args.flags.feintLevel += 2;
    ui.notifications.info("攻击耳鸣目标，虚招优势提升！");
}

if (args.damageResult && args.damageResult.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "prone"); // 倒地;
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit) {
    const effectData = { id: "unstable", duration: { rounds: 2 } };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: "true", // 真实伤害作为流失
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("破釜沉舟：额外造成20点流失！");
}

if (args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: "true",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit && args.target) {
    const effectData = { id: 'unstable', duration: { rounds: 2 } };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${args.target.name} 下盘不稳！`);
}

if (args.type === 'attack' && args.isHit && args.target) {
    await args.target.applyDamage({ amount: 20, type: 'liushi', isHit: true, ignoreBlock: true, ignoreDefense: true });
    if(args.target.token?.object) canvas.interface.createScrollingText(args.target.token.object.center, '流失 20', { fill: '#8b0000', stroke: '#ffffff' });
}

if (args.type === 'attack' && args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', isHit: true, ignoreBlock: true, ignoreDefense: true });
    ui.notifications.info(`${thisItem.name} 暴击触发额外流失！`);
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.type === 'attack' && args.isHit && args.target) {
    const effectData = { id: 'unstable', duration: { rounds: 2 } };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${args.target.name} 下盘不稳！`);
}

if (args.type === 'attack' && args.isHit && args.target) {
    await args.target.applyDamage({ amount: 20, type: 'liushi', isHit: true, ignoreBlock: true, ignoreDefense: true });
    if(args.target.token?.object) canvas.interface.createScrollingText(args.target.token.object.center, '流失 20', { fill: '#8b0000', stroke: '#ffffff' });
}

if (args.type === 'attack' && args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', isHit: true, ignoreBlock: true, ignoreDefense: true });
    ui.notifications.info(`${thisItem.name} 暴击触发额外流失！`);
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}
