scripts.script:
if (args.type === 'attack' && args.isHit && args.move.type !== 'basic') {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}

if (args.type === 'attack' && args.isCrit && args.move.type !== 'basic') {
    // 额外造成 10 点流失伤害
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        isSkill: false // 视为特效伤害
    });
    ui.notifications.info(`${actor.name} 的蟠龙触发特效，造成额外流失！`);
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        isSkill: false
    });
}

if (args.type === 'attack' && args.isHit) {
    // 获取系统预设的 unstable 特效
    const effect = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (effect) {
        const data = foundry.utils.deepClone(effect);
        data.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

if (args.type === 'attack' && args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (game.combat) {
    ui.notifications.info(`${actor.name} 受到万刃反噬！`);
    await actor.applyDamage({ amount: 20, type: 'liushi', isHit: true });
    // 扣除内力
    const newMp = Math.max(0, actor.system.resources.mp.value - 20);
    await actor.update({ 'system.resources.mp.value': newMp });
}

if (args.move.type !== 'basic') {
    const str = S.stats.liliang.total || 0;
    const extra = Math.min(40, str);
    args.output.damage += extra;
    args.output.bonusDesc.push(`虎尾鞭(+${extra})`);
}

if (args.type === 'attack' && args.isHit) {
   if (!target.system.martial.stanceActive) {
        // 使用系统通用的 bleed_stack (流血)
        await game.xjzl.api.effects.addEffect(target, 'bleed_stack');
    }
}

if (args.type === 'attack' && args.isHit) {
    if (!args.target.system.martial.stanceActive) {
        const effect = CONFIG.statusEffects.find(e => e.id === 'unstable');
        if (effect) {
             const data = foundry.utils.deepClone(effect);
             data.duration = { rounds: 1 };
             await game.xjzl.api.effects.addEffect(args.target, data);
        }
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        isSkill: false
    });
}

const sourceEffect = thisItem.effects.getName('菩提护身');
if (sourceEffect) {
    const effectData = sourceEffect.toObject();
    // 初始设置为5层
    foundry.utils.setProperty(effectData, 'flags.xjzl-system.stacks', 5);
    await game.xjzl.api.effects.addEffect(actor, effectData);
} else {
    ui.notifications.warn('特效缺失: 菩提护身');
}

if (args.type === 'attack' && args.isHit) {
    if (!args.target.system.martial.stanceActive) {
         await game.xjzl.api.effects.addEffect(args.target, 'fushen');
    }
}

if (S.social.exing >= 100 && S.social.xiayi < 50) {
    args.output.damage += 30;
    args.output.bonusDesc.push('恶人加成(+30)');
}

if (args.type === 'attack' && args.isCrit) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: 'renxing',
        dc: 15,
        label: '抵抗残疾',
        onFail: async () => { ui.notifications.warn('检定失败！请手动进行残疾检定。'); }
    });
}

// 检查本次攻击结果是否造成了击破 (isBroken)
if (args.isBroken) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'root'));
    if (effectData) {
        effectData.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${actor.name} 击破架招！施加禁足。`);
    }
}

if (args.type === 'attack' && args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: 'poison',
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        isSkill: false
    });
}

// 检查本次攻击结果是否造成了击破
if (args.isBroken) {
    // 破甲 (pojia) 是系统通用状态
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'pojia'));
    if (effectData) {
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${actor.name} 击破架招！施加破甲。`);
    }
}

const armors = actor.itemTypes.armor.filter(i => i.system.equipped);
if (armors.length === 0) {
    args.output.damage += 20;
    args.output.bonusDesc.push('裸装加成(+20)');
}

if (args.type === 'attack' && args.isHit) {
    const sourceEffect = thisItem.effects.getName('点燃');
    if (sourceEffect) {
        await game.xjzl.api.effects.addEffect(args.target, sourceEffect.toObject());
    } else {
        ui.notifications.warn('特效缺失: 点燃');
    }
}

const stacks = actor.effects.find(e => e.name === '霸王')?.getFlag('xjzl-system', 'stacks') || 0;
if (stacks > 0) {
    const extra = stacks * 5;
    args.output.damage += extra;
    args.output.bonusDesc.push(`霸王层数(+${extra})`);
}

if (args.target && args.target.token && actor.token) {
    const dist = canvas.grid.measurePath([actor.token.object.center, args.target.token.object.center]).distance;
    if (dist > 10) {
        args.output.damage += 5;
        args.output.bonusDesc.push('远射(+5)');
    }
}

if (actor) {
    // 流失气血 (真实伤害)
    await actor.applyDamage({ amount: 20, type: 'liushi', ignoreDefense: true, isHit: true });
    // 扣除内力
    const currentMp = actor.system.resources.mp.value;
    if (currentMp > 0) {
        await actor.update({ 'system.resources.mp.value': Math.max(0, currentMp - 20) });
        if (actor.token?.object) {
            actor.showFloatyText('-20 内力', { 
                fill: '#0000FF', stroke: '#000000', strokeThickness: 4 
            });
        }
    }
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp' });
}

if (args.isHit && args.target) {
    const effectData = { 
        id: "unstable", 
        duration: { rounds: 2 } 
    };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isHit && args.target) {
    await args.target.applyDamage({ amount: 20, type: 'liushi', attacker: actor, isHit: true });
    ui.notifications.info(`${actor.name} 破釜沉舟，造成额外流失伤害！`);
}

if (args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', attacker: actor, isHit: true });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp' });
}

