scripts.script:
if (args.isHit) {
    await args.target.applyDamage({
        amount: 10,
        type: "true", // 真实伤害模拟流失
        attacker: actor,
        isHit: true,
        ignoreBlock: true,
        ignoreDefense: true
    });
    ui.notifications.info(`${thisItem.name} 造成了 10 点气血流失！`);
}

if (args.move.type === 'basic') {
    args.output.damage += 5;
    args.output.bonusDesc.push("火焰伤害(+5)");
}

// 假设侠义 > 50 视为[侠]
if (S.social.xiayi > 50) {
    S.combat.crit_waigong -= 2;
    S.combat.crit_neigong -= 2;
}

// 假设恶行 > 50 视为[恶]
if (args.target && args.target.system.social.exing > 50) {
    args.config.amount += 10;
    ui.notifications.info("惩恶扬善！伤害 +10");
}

if (args.isHit) {
    await args.target.applyDamage({ amount: 10, type: "true", isHit: true, ignoreDefense: true });
}

if (args.damageResult && args.damageResult.isDying) {
    await actor.applyHealing({ amount: 30, type: "hp" });
    ui.notifications.info("击敌濒死，士气大振，回复30气血！");
}

/**
 * 水火囚龙棒暴击特效
 * Trigger: preDamage (伤害计算前)
 */
// 检查是否暴击 (preDamage 阶段暴击状态已确定，在 args.outcome 中)
if (args.outcome.isCrit) {
    const choice = await foundry.applications.api.DialogV2.wait({
        window: { title: "水火囚龙棒：暴击特效" },
        content: "<p>双龙盘踞，刚猛无比！请选择暴击效果：</p>",
        buttons: [
            { label: "阴：无视外防", action: "yin", icon: "fas fa-ghost" },
            { label: "阳：击飞1米", action: "yang", icon: "fas fa-sun" }
        ]
    });

    if (choice === "yin") {
        // 修改配置：无视防御
        args.config.ignoreDefense = true;
        ui.notifications.info("囚龙棒·阴：无视防御！");
    } else {
        // 阳：提示手动击飞
        ui.notifications.warn(`囚龙棒·阳：请将 ${args.target?.name || "目标"} 击飞 1 米！`);
    }
}

// 假设恶行 > 50 为恶
if (args.target.system.social.exing > 50) {
    args.flags.grantFeintLevel += 2; // 守方被虚招+2(即攻方优势)
    // 注意：这里是攻方触发，所以要修改 flags.critThresholdMod
    args.flags.critThresholdMod += 2;
}

if (args.move.element === 'taiji') {
    args.output.damage += 10;
    args.output.bonusDesc.push("两仪鞭(+10)");
}

if (Macros.checkStance(actor, args)) {
    await game.xjzl.api.effects.addEffect(args.attacker, "unstable");
    ui.notifications.info("沥泉神矛：格挡成功！目标下盘不稳！");
}

if (args.move.type === 'basic') {
    args.output.damage += 10;
}

if (args.isCrit && args.isHit) {
    await args.target.applyDamage({ 
        amount: 20, 
        type: "true", 
        attacker: actor, 
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("虎头湛金枪：暴击撕裂，气血流失 20 点！");
}

if (args.isBroken) {
    ui.notifications.warn("丈八蛇矛：击破架招！请消耗反应动作怒吼！");
}

if (args.isHit && args.move.type === 'basic') {
    let amt = 5;
    if (args.target.system.martial.stanceActive !== true) {
        amt += 5;
    }
    await args.target.applyDamage({
        amount: amt,
        type: "true",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info(`断魂枪造成 ${amt} 点流失`);
}

if (args.isHit && args.target.system.martial.stanceActive !== true) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    ui.notifications.info("月牙戟：目标无架招，击倒！");
}

if (args.isHit) {
    if (args.target.statuses.has('prone')) {
        ui.notifications.info("倒海翻江：目标已倒地，击飞1米！");
    } else {
        await game.xjzl.api.effects.addEffect(args.target, "prone");
    }
}

if (args.move.weaponType !== 'staff') return;

// 1. 效果一：人/地级加成
if (args.move.tier === 1 || args.move.tier === 2) {
    args.output.damage += 10;
    args.output.bonusDesc.push("锡杖(+10)");
}

// 2. 效果二：万佛寺(佛门)内功加成
const ngId = S.martial.active_neigong;
if (ngId) {
    const ng = actor.items.get(ngId);
    // 精准匹配万佛寺
    if (ng && ng.system.sect === "WanFoSi") {
        args.output.damage += 10;
        args.output.bonusDesc.push("佛门共鸣(+10)");
    }
}

// 自动检测万佛寺内功
const ngId = S.martial.active_neigong;
if (ngId) {
    const ng = actor.items.get(ngId);
    if (ng && ng.system.sect === "WanFoSi") {
        args.output.damage += 5;
        args.output.bonusDesc.push("禅心(+5)");
    }
}

if (args.isHit && args.target.system.social.exing > 50) {
    await args.target.update({ 'system.resources.rage.value': Math.max(0, args.target.system.resources.rage.value - 1) });
    ui.notifications.info("祖师棍：打散了恶人的1点怒气！");
}

if (args.isHit && ['yang', 'gang'].includes(args.move.element)) {
    await Macros.requestContest({
        attacker: actor,
        defender: args.target,
        type: 'jiaoli',
        label: '伏虎棍 - 角力',
        outcome: {
            win: {
                text: '对抗胜利：目标 [倒地]。',
                targetEffect: 'prone'
            },
            lose: { text: '对抗失败：目标站稳了脚跟。' }
        }
    });
}

if (args.isBroken) {
    args.output.damage += 10;
    ui.notifications.info(`${actor.name} 击破架招！伤害+10`);
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "bleed_stack");
    ui.notifications.info("击破架招！施加流血");
}

// 调用系统主手宏判断格挡/架招成功
if (Macros.checkStance(actor, args)) {
    const sourceEffect = thisItem.effects.getName("天机");
    if (sourceEffect) {
         const effectData = sourceEffect.toObject();
         delete effectData._id;
         effectData.origin = thisItem.uuid;
         await game.xjzl.api.effects.addEffect(actor, effectData);
    }
}

const validMoves = ["碎齿脱爪", "丧门之犬", "野狗挡道", "关门打狗"];
if (validMoves.includes(args.move.name)) {
    const extra = args.baseData.weapon;
    args.output.damage += extra;
    args.output.bonusDesc.push("打狗棒法翻倍");
}

if (args.isBroken) {
    const effect = CONFIG.statusEffects.find(e => e.id === "jiaoxie");
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
    ui.notifications.info("击破架招！施加缴械");
}

if (S.martial.stanceActive && args.move.type === 'qi') {
    args.move.currentCost.mp = 0;
    ui.notifications.info("赤霄杖：气招内力消耗减免");
}

if (!args.isHit) {
    const effectData = CONFIG.statusEffects.find(e => e.id === "benzhuo");
    // 系统benzhuo默认stackable=true
    const data = foundry.utils.deepClone(effectData);
    foundry.utils.setProperty(data, "flags.xjzl-system.maxStacks", 3);
    await game.xjzl.api.effects.addEffect(args.target, data);
    ui.notifications.warn(`${args.target.name} 闪避了攻击，被施加笨拙！`);
}

const myXiayi = actor.system.social.xiayi || 0;
const targetXiayi = args.target.system.social.xiayi || 0;
if (targetXiayi < myXiayi) {
    // 正数表示阈值降低（更容易暴击）
    args.flags.critThresholdMod += 5;
}

if (args.outcome.isCrit) {
    const effect = CONFIG.statusEffects.find(e => e.id === "pojia");
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    // 这里的 target 需要通过 canvas 获取或者等待 Hit 阶段
    // 但 preDamage 无法直接给 target 加状态 (因为还没到 hit 阶段)
    // 变通：这里不加，改用 Hit 阶段加。PreDamage 也可以，但 target 不在 args 里
    // 修正：使用 Hit 阶段
}

if (args.isCrit) {
    const effect = CONFIG.statusEffects.find(e => e.id === "pojia");
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, data);
}

if (!args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    ui.notifications.info(`${args.target.name} 无架招，被击倒！`);
}

if (!args.target.system.martial.stanceActive) {
    const effect = CONFIG.statusEffects.find(e => e.id === "pain");
    const data = foundry.utils.deepClone(effect);
    data.duration = { rounds: 3 };
    await game.xjzl.api.effects.addEffect(args.target, data);
}

// 距离检测
const t1 = actor.token?.object || actor.getActiveTokens()[0];
const t2 = args.target?.token?.object || args.target?.getActiveTokens()[0];

if (t1 && t2) {
    const measure = canvas.grid.measurePath([t1.center, t2.center]);
    // 1.1 米兼容斜向
    if (measure.distance <= 1.1) {
        args.config.amount += 5;
        ui.notifications.info("混铁棍：近身伤害+5");
    }
}

if (game.combat) {
    await actor.applyDamage({ amount: 20, type: 'true', isHit: true });
    await actor.update({ "system.resources.mp.value": Math.max(0, S.resources.mp.value - 20) });
    ui.notifications.warn("万刃双棍反噬：流失气血内力");
}

const effect = CONFIG.statusEffects.find(e => e.id === "unstable");
const data = foundry.utils.deepClone(effect);
data.duration = { rounds: 2 };
await game.xjzl.api.effects.addEffect(args.target, data);

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', isHit: true, ignoreDefense: true });
    ui.notifications.info("蟠龙双棍：暴击追加流失");
}

if (game.combat) {
    await actor.applyDamage({ amount: 20, type: 'true', isHit: true });
    await actor.update({ "system.resources.mp.value": Math.max(0, S.resources.mp.value - 20) });
    ui.notifications.warn("万刃长枪反噬：流失气血内力");
}

const effect = CONFIG.statusEffects.find(e => e.id === "unstable");
const data = foundry.utils.deepClone(effect);
data.duration = { rounds: 2 };
await game.xjzl.api.effects.addEffect(args.target, data);

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: 'liushi', isHit: true, ignoreDefense: true });
    ui.notifications.info("蟠龙长枪：暴击追加流失");
}

// 获取系统预设的 unstable 状态
const baseEffect = CONFIG.statusEffects.find(e => e.id === "unstable");
if (baseEffect) {
    // 克隆并修改持续时间为 2 回合
    const effectData = foundry.utils.deepClone(baseEffect);
    effectData.duration = { rounds: 2 };
    // 应用特效
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: "liushi", isHit: true });
}

// 获取系统预设的 unstable 状态
const baseEffect = CONFIG.statusEffects.find(e => e.id === "unstable");
if (baseEffect) {
    // 克隆并修改持续时间为 2 回合
    const effectData = foundry.utils.deepClone(baseEffect);
    effectData.duration = { rounds: 2 };
    // 应用特效
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isCrit) {
    await args.target.applyDamage({ amount: 10, type: "liushi", isHit: true });
}
