scripts.script:
if (S.martial.stanceActive && S.martial.stanceItemId === thisItem.id) {
    // 简易实现：如果当前开启了本武器的架招，尝试移除倒地状态
    // 注意：完全免疫击飞/击倒通常需要Hook拦截，脚本引擎做不到“阻止状态添加”，只能“添加后立即移除”
    if (actor.hasStatusEffect("prone")) {
        const effect = actor.effects.find(e => e.statuses.has("prone"));
        if (effect) effect.delete();
    }
}

if (args.isHit) {
    // 造成 10 点流失 (真实伤害或专用流失逻辑)
    // 这里使用 applyDamage，类型为 'true' 模拟流失
    await args.target.applyDamage({
        amount: 10,
        type: "true",
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        ignoreBlock: true
    });
    ui.notifications.info(`${thisItem.name} 造成了锯齿流失伤害！`);
}

if (args.type === 'attack' && args.isHit && args.move.type === 'basic') {
    await args.target.applyDamage({
        amount: 5,
        type: "fire",
        attacker: actor,
        isHit: true
    });
}

// 检查自身侠义: 侠义>=50 且 恶行<50
if (S.social.xiayi >= 50 && S.social.exing < 50) {
    S.combat.crit_waigong -= 2;
}

if (args.isHit && args.target) {
    // 检查目标恶行: >= 100 视为[恶]
    if (args.target.system.social?.exing >= 100) {
        await args.target.applyDamage({
            amount: 10,
            type: "waigong", // 视为武器额外伤害
            attacker: actor,
            isHit: true
        });
        ui.notifications.info("惩恶！造成额外伤害！");
    }
}

if (args.target && args.target.system.resources.hp.value <= 0) {
    // 辅助判定：如果这一击正好打死了，提示回复
    ui.notifications.info("无当号旗：若目标进入濒死，请手动应用 30 点治疗。");
}

if (args.outcome.isCrit) {
    new Dialog({
        title: "水火囚龙棒 - 暴击特效",
        content: "<p>请选择暴击触发效果：</p>",
        buttons: {
            yin: {
                label: "阴 - 无视外防",
                callback: () => {
                    args.config.ignoreDefense = true;
                    ui.notifications.info("囚龙棒：无视防御触发！");
                }
            },
            yang: {
                label: "阳 - 击飞",
                callback: async () => {
                    ui.notifications.info("囚龙棒：请手动击飞目标 1 米！");
                    // 可选：添加击飞状态
                }
            }
        }
    }).render(true);
}

if (args.target.system.social?.exing >= 100) {
    args.flags.grantFeintLevel += 2; // 虚招优势
    // 暴击骰-2 (Crit Threshold Mod, 正数表示更容易)
    args.flags.critThresholdMod += 2;
    ui.notifications.info("打神鞭：对恶人特效触发！");
}

if (args.move.element === 'taiji') {
    args.output.damage += 10;
    args.output.bonusDesc.push("两仪(+10)");
}

if (args.isCrit) {
    await args.target.applyDamage({
        amount: 20,
        type: "true",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("虎头湛金枪：暴击撕裂！流失 20 点气血。");
}

if (args.isBroken) {
    ui.notifications.info("丈八蛇矛：击破架招！请手动吼叫加怒。");
}

if (args.move.type === 'basic' && args.isHit) {
    let amount = 5;
    // 检查目标架招
    if (!args.target.system.martial?.stanceActive) {
        amount += 5;
    }
    await args.target.applyDamage({
        amount: amount,
        type: "true",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info(`断魂枪：造成 ${amount} 点流失。`);
}

if (args.isHit && !args.target.system.martial?.stanceActive) {
    await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
    ui.notifications.info("月牙戟：目标无架招，击倒！");
}

S.arts.fofa.mod += 1;

// 检查内功门派
const neigongId = S.martial.active_neigong;
if (neigongId) {
    const neigong = actor.items.get(neigongId);
    if (neigong && neigong.system.sect === 'buddhism') {
        // 全局检定优势
        actor.xjzlStatuses.globalCheckLevel += 1;
        // 虚招对抗优势
        actor.xjzlStatuses.feintLevel += 1;
        // 被虚招优势
        actor.xjzlStatuses.defendFeintLevel += 1;
    }
}

if (args.isHit) {
    // 检查是否已倒地
    const isProne = args.target.statuses.has("prone");

    if (isProne) {
        ui.notifications.info("倒海翻江：目标已倒地，将其击飞 1 米！");
    } else {
        await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
        ui.notifications.info("倒海翻江：击倒目标！");
    }
}

const move = args.move;
// 判断是否为长棍武学 (move.weaponType == 'staff') 且为 人/地级 (tier <= 2)
if (move.weaponType === 'staff') {
    // 条件1: 人/地级
    if ((move.tier || 1) <= 2) {
        args.output.damage += 10;
    }

    // 条件2: 佛门内功
    const neigongId = S.martial.active_neigong;
    if (neigongId) {
        const neigong = actor.items.get(neigongId);
        if (neigong && neigong.system.sect === 'buddhism') {
            args.output.damage += 10;
        }
    }
}

const neigongId = S.martial.active_neigong;
if (neigongId) {
    const neigong = actor.items.get(neigongId);
    if (neigong && neigong.system.sect === 'buddhism') {
        args.output.damage += 5;
    }
}

if (args.isHit && args.target.system.social?.exing >= 100) {
    await args.target.applyHealing({ amount: -1, type: "rage" }); // applyHealing 支持负数作为扣除
    ui.notifications.info("祖师棍：打散了恶人的怒气！");
}

// 检测伤害类型：阳(yang) 或 刚(gang)
const type = args.move?.element;
if (args.isHit && (type === 'yang' || type === 'gang')) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "jiaoli",
        dc: S.skills.jiaoli.total + 10, // 对抗 DC = 攻击者角力 + 10
        label: "伏虎棍对抗",
        onFail: async () => {
            await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
            ui.notifications.info("伏虎棍：角力胜出，目标倒地！");
        }
    });
}
