scripts.script:
if (args.move.weaponType === 'sword' && (!args.move.tier || args.move.tier === 1)) {
    args.output.damage += 50;
    args.output.bonusDesc.push('五岳剑(+50)');
}

if (args.move.element === 'yin') {
    args.output.damage += 10;
    args.output.bonusDesc.push('寒渊(+10)');
}

if (args.move.element === 'yin') {
    args.flags.critThresholdMod -= 2;
}

if (actor.system.info.gender === '女' || actor.system.info.gender === 'Female') {
    thisItem.system.block += 10;
}

if (args.move.type === 'basic') {
    args.config.type = 'liushi';
}

if (args.hpLost > 0 || args.hutiLost > 0) {
    const eff = {
        name: '碧光',
        icon: thisItem.img,
        changes: [
            { key: 'flags.xjzl-system.feintLevel', mode: 2, value: 3 },
            { key: 'flags.xjzl-system.defendFeintLevel', mode: 2, value: 3 }
        ],
        duration: { seconds: 9999 },
        flags: { 'xjzl-system': { slug: 'weapon_wudu_light' } }
    };
    await game.xjzl.api.effects.addEffect(actor, eff);
}

if (args.isHit && (args.hpLost > 0 || args.hutiLost > 0)) {
    // 触发碧光
    const eff = {
        name: '碧光',
        icon: thisItem.img,
        changes: [
            { key: 'flags.xjzl-system.feintLevel', mode: 2, value: 3 },
            { key: 'flags.xjzl-system.defendFeintLevel', mode: 2, value: 3 }
        ],
        duration: { seconds: 9999 },
        flags: { 'xjzl-system': { slug: 'weapon_wudu_light' } }
    };
    await game.xjzl.api.effects.addEffect(actor, eff);

    // 狂蛇剑法联动
    if (args.move && args.move.name.includes('狂蛇')) {
        const snakeEff = {
            name: '狂蛇',
            icon: 'icons/creatures/reptiles/snake-cobra-hood-green.webp',
            changes: [],
            duration: { seconds: 9999 },
            flags: { 'xjzl-system': { slug: 'weapon_wudu_snake', stackable: true } }
        };
        await game.xjzl.api.effects.addEffect(actor, snakeEff);
        ui.notifications.info('狂蛇剑法：获得一层狂蛇');
    }
}

if (actor.effects.some(e => e.name === '清风')) {
    thisItem.system.block += 5;
}

if (actor.system.info.gender === '女' || actor.system.info.gender === 'Female') {
    S.combat.damages.skill.mod += 2;
    S.combat.hit_waigong += 2;
    S.combat.hit_neigong += 2;
}

if (args.hpLost > 0 || args.hutiLost > 0) {
    // 引用 Item Effects 中的尝胆模板
    const template = thisItem.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'weapon_goujian_stack');
    if (template) {
         await game.xjzl.api.effects.addEffect(actor, template.toObject());
    }

    // 检查层数爆发
    const eff = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'weapon_goujian_stack');
    if (eff && eff.getFlag('xjzl-system', 'stacks') >= 3) {
        await eff.delete();
        const newRage = Math.min(actor.system.resources.rage.max, actor.system.resources.rage.value + 6);
        await actor.update({'system.resources.rage.value': newRage});
        ui.notifications.info('勾践剑：苦心人天不负，怒气+6！');
    }
}

const eff = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'weapon_goujian_stack');
if (eff) {
    const stacks = eff.getFlag('xjzl-system', 'stacks') || 0;
    if (stacks > 0) {
        thisItem.system.block += (stacks * 10);
    }
}

if (args.isHit && args.target && !args.target.system.martial.stanceActive) {
    // 直接使用 ID 引用系统状态
    await game.xjzl.api.effects.addEffect(args.target, 'qixu');
    ui.notifications.info(`分水剑：${args.target.name} 气虚了`);
}

if (S.stats.liliang.total < 40) {
    args.flags.level -= 1;
    ui.notifications.warn('玄铁重剑：力量不足，命中劣势');
}

if (S.martial.stanceActive) {
    thisItem.system.block += 40; // 翻倍即 +40 (原值40)
}

if (args.isHit && args.target) {
    const hasSword = args.target.itemTypes.weapon.some(w => w.system.equipped && w.system.type === 'sword');
    if (hasSword) {
        // 直接使用 ID 引用系统状态
        await game.xjzl.api.effects.addEffect(args.target, 'chanshou');
    }
}

if (args.target && (args.target.system.resources.huti || 0) > 0) {
    // 动态增加本次结算的伤害
    args.damageResult.finalDamage += 30;
    // 为了显示正确，最好也修改 hpLost 分配，但系统核心已算完，这里只能强行追加
    // 最好的方式是申请一次额外的伤害，或者在 calc 阶段做（但 calc 不知道目标状态）
    // 妥协方案：造成额外 30 点真实伤害
    await args.target.applyDamage({ amount: 30, type: 'liushi', attacker: actor, isHit: true });
    ui.notifications.info('巨阙剑：破气重击 (+30)');
}

if (!target) return;
const exing = target.system.social.exing || 0;
const xiayi = target.system.social.xiayi || 0;
const isEvil = exing >= 100 && xiayi < 50;

if (isEvil) {
    await target.applyDamage({ amount: 10, type: 'liushi', attacker: actor, isHit: true });
    if (args.isBroken) {
        // 直接使用 ID 引用系统状态
        await game.xjzl.api.effects.addEffect(target, 'pojia');
        ui.notifications.info('破邪：击破架招，施加破甲！');
    }
}

if (S.social.xiayi > S.social.exing) {
    S.combat.crit_waigong -= 2;
}

if (args.isHit && args.target.type === 'creature') {
    const tili = args.target.system.resources.tili;
    if (tili && tili.value > 0) {
        await args.target.update({'system.resources.tili.value': tili.value - 1});
        ui.notifications.info('兽骨大剑：野兽体力 -1');
    }
}

if (args.calcDamage < args.baseDamage) {
    // 判定为触发了防御/格挡减伤
    args.output.damage = Math.max(0, args.output.damage - 5);
}

if (args.isHit && args.move.type === 'real' && args.target && !args.target.system.martial.stanceActive) {
    // 引用 Item Effects 中的水月模板
    const template = thisItem.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'weapon_shuiyue');
    if (template) {
        await game.xjzl.api.effects.addEffect(actor, template.toObject());
    }

    const eff = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'weapon_shuiyue');
    if (eff && eff.getFlag('xjzl-system', 'stacks') >= 5) {
        await eff.delete();
        ui.notifications.info('仙女双剑：水月盈满，获得额外动作！');
    }
}

if (args.move.type === 'basic') {
    args.output.damage += 20;
    args.output.bonusDesc.push('墨霄(+20)');
}

if (!args.target) return;
const sGender = actor.system.info.gender;
const tGender = args.target.system.info.gender;
// 简单的异性判断：都有值且不相等
if (sGender && tGender && sGender !== tGender) {
    args.config.amount += 10;
    ui.notifications.info("龙凤双剑：异性相吸，伤害+10！");
}

// 遍历所有技能，增加 checkMod
for (const key of Object.keys(S.skills)) {
    if (S.skills[key]) {
        S.skills[key].checkMod += 2;
    }
}

const targetMoves = ["道镇九州", "万道归一", "道灭道生", "无我无道"];
if (targetMoves.includes(args.move.name)) {
    args.output.damage *= 2;
    args.output.bonusDesc.push("真武剑效(x2)");
}

if (actor.system.martial.stanceActive && args.type !== 'taiji' && args.config.isCrit) {
    args.config.isCrit = false;
    ui.notifications.warn("真武剑气护体，化解了暴击！");
}

if (!args.config.ignoreBlock && S.combat.blockTotal > 0) {
    // 从物品中获取特效模板
    const sourceEffect = thisItem.effects.getName("止戈");
    if (sourceEffect) {
        const effectData = sourceEffect.toObject();
        delete effectData._id;
        // 应用特效
        const effect = await game.xjzl.api.effects.addEffect(actor, effectData);

        // 检查层数
        const stacks = effect?.getFlag("xjzl-system", "stacks") || 1;
        if (stacks >= 5) {
            ui.notifications.warn(`${actor.name} 止戈剑意已满！请手动结束对方回合！`);
            await game.xjzl.api.effects.removeEffect(actor, "zhuge_stack", 5);
        }
    }
}

if (args.target && args.target.system.social.rep_chaoting > 0) {
    args.config.isCrit = true;
    ui.notifications.info("尚方宝剑：见官大一级，必定暴击！");
}

if (args.isHit && ['waigong', 'neigong'].includes(args.type)) {
    await actor.applyHealing({ amount: 15, type: "hp" });
}

const extra = Math.floor(S.stats.shencai.total / 2);
args.output.damage += extra;
args.output.bonusDesc.push(`长生(+${extra})`);

if (args.isHit && args.target && !args.target.system.martial.stanceActive) {
    // yishang 是系统预设状态 ID
    await game.xjzl.api.effects.addEffect(args.target, "yishang");
}

if (!args.target) return;
const sPers = actor.system.info.personality;
const tPers = args.target.system.info.personality;

if (sPers && tPers) {
    if (sPers === tPers) {
        args.config.isCrit = true;
        ui.notifications.info("泰阿剑：道同相惜，必定暴击！");
    } else {
        args.config.amount += 10;
    }
}

if (args.move.element === 'rou') {
    // 正数表示更容易暴击
    args.flags.critThresholdMod += 5;
}

if (!args.target) return;
const sSoc = actor.system.social;
const tSoc = args.target.system.social;
if ((sSoc.rep_chaoting > tSoc.rep_chaoting) || (sSoc.rep_wulin > tSoc.rep_wulin)) {
    args.config.amount += 20;
}

// 获取同阵营 Token 数量 (减去自己)
const allies = canvas.tokens.placeables.filter(t => t.document.disposition === actor.token?.document.disposition).length - 1;
if (allies > 0) {
    const huti = Math.min(100, allies * 20);
    await actor.applyHealing({ amount: huti, type: 'huti' });
    ui.notifications.info(`照胆剑：感召${allies}名友方，获得${huti}护体！`);
}

// 判定侠义 > 恶行
if (S.social.xiayi > S.social.exing) {
    args.output.feint += 2;
    args.output.bonusDesc.push("湛卢(+2)");
}

const tier = args.move.tier || 1;
if (tier === 1) args.output.damage += 5;
if (tier === 2) args.output.damage += 10;

if (args.target && args.target.system.martial.stanceActive) {
    args.config.amount += 10;
}

if (actor.system.martial.stanceItemId === thisItem.id) {
    S.combat.initiative += 2;
}

if (args.move.element === 'yin') {
    args.output.damage += 10;
    args.output.bonusDesc.push("冰阙(+10)");
}

const neigongId = S.martial.active_neigong;
if (neigongId) {
    const neigong = actor.items.get(neigongId);
    if (neigong && args.move.element && args.move.element !== neigong.system.element) {
        args.output.damage += 10;
    }
}

if (S.social.xiayi > S.social.exing) {
    args.output.damage += 5;
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "bleed_stack");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit) {
    // 使用系统通用状态 unstable
    await game.xjzl.api.effects.addEffect(args.target, "unstable");
}

if (args.isCrit && args.target) {
    await args.target.applyDamage({ amount: 10, type: "liushi", isHit: true });
    ui.notifications.info("蟠龙：暴击引发气血流失！");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

system.scripts.script:
if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.isHit && args.target) {
    // 1. 获取系统标准状态数据
    const statusData = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (statusData) {
        const effectData = foundry.utils.deepClone(statusData);
        effectData.name = '下盘不稳 (霜月)';
        effectData.duration = { rounds: 2 };
        // 2. 应用特效
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.isHit && args.target) {
    await args.target.applyDamage({
        amount: 20,
        type: 'liushi', // 真实伤害模拟流失
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        ignoreBlock: true
    });
    ui.notifications.info("破釜沉舟，额外流失！");
}

if (args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("蟠龙暴击，鲜血飞溅！");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.isHit && args.target) {
    const statusData = CONFIG.statusEffects.find(e => e.id === 'unstable');
