        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.isHit && args.target) {
    await args.target.applyDamage({
        amount: 20,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        ignoreBlock: true
    });
    ui.notifications.info("破釜沉舟，额外流失！");
}

if (args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("蟠龙暴击，鲜血飞溅！");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'mp', showScrolling: true });
}

if (args.isHit && args.target) {
    const statusData = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (statusData) {
        const effectData = foundry.utils.deepClone(statusData);
        effectData.name = '下盘不稳 (霜月)';
        effectData.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 下盘不稳！`);
    }
}

if (args.isHit && args.target) {
    await args.target.applyDamage({
        amount: 20,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true,
        ignoreBlock: true
    });
    ui.notifications.info("破釜沉舟，额外流失！");
}

if (args.isHit && args.isCrit && args.target) {
    await args.target.applyDamage({
        amount: 10,
        type: 'liushi',
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("蟠龙暴击，鲜血飞溅！");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: 'hp', showScrolling: true });
}
