scripts.script:
if (!Macros.checkStance(actor, args)) return;
if (!args.attacker) return;

const drainAmount = 5;
let currentMp = args.attacker.system.resources.mp.value;

if (currentMp > 0) {
    let actualDrain = Math.min(currentMp, drainAmount);
    await args.attacker.update({"system.resources.mp.value": currentMp - actualDrain});
    await actor.applyHealing({ amount: actualDrain, type: "mp" });
    ui.notifications.info(`${actor.name} 吸取了 ${args.attacker.name} ${actualDrain} 点内力！`);
}

if (Macros.checkStance(actor, args)) {
    await actor.applyHealing({ amount: 1, type: "rage", showScrolling: true });
}

if (args.move.isUltimate) {
    const currentRageCost = args.move.currentCost.rage;
    if (currentRageCost > 1) {
        args.move.currentCost.rage -= 1;
        ui.notifications.info("煌龙靴刃生效：怒气消耗 -1");
    }
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(actor, "lianji");
    ui.notifications.info(`${actor.name} 击破架招，获得连击！`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, "root");
    // 禁足默认无限，物品描述说一回合，需要修改持续时间
    // 由于 addEffect 会返回创建的 AE，我们可以后续修改它，或者构造一个带 duration 的 data 传进去
    // 这里采用标准状态，GM或玩家手动移除，或者使用宏扩展
    // 更严谨写法：构造带时间的 data
    const rootData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "root"));
    rootData.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, rootData);
}

const speed = actor.system.combat.speedTotal || 0;
if (speed > 0) {
    args.output.damage += speed;
    args.output.bonusDesc.push(`银翼(${speed})`);
}

if (!Macros.checkStance(actor, args)) return;
if (!args.attacker || args.attacker.uuid === actor.uuid) return;
if (actor.isReceivingReflection) return;

args.attacker.isReceivingReflection = true;
try {
    await args.attacker.applyDamage({ 
        amount: 10, 
        type: "poison", 
        attacker: actor, 
        isHit: true, 
        ignoreDefense: true 
    });
    ui.notifications.info("毒牙绑腿反击触发！");
} finally {
    args.attacker.isReceivingReflection = false;
}

if (args.isBroken) {
    ChatMessage.create({content: `${actor.name} 击破了架招，将目标击飞 1 米！`});
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    // 额外伤害
    await args.target.applyDamage({
        amount: 20,
        type: args.move.damageType || "waigong",
        attacker: actor,
        isHit: true,
        ignoreDefense: true // 额外伤害通常视作直伤，或随主攻击。这里暂定无视防御以免被双重减免
    });
    ui.notifications.info("万钧护指：重创无架招目标 (+20伤害)！");
    ChatMessage.create({content: `${args.target.name} 被万钧之力击飞 3 米！`});
}

if (args.isBroken) {
    const effect = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "jinshi"));
    effect.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, effect);
}

if (args.isHit && args.move.type === "basic") {
    // 气血流失视为真实伤害或直接扣除
    await args.target.applyDamage({
        amount: 10,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info("逆鳞护指造成额外流失伤害！");
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effect = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "pojia"));
    effect.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, effect);
}

if (!Macros.checkStance(actor, args)) return;

const blockSlug = "tianzhu-block";
const damageSlug = "tianzhu-damage";

// 1. 如果已经神威大成 (伤害模式)，则只刷新神威，不再处理格挡
const damageEffect = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === damageSlug);
if (damageEffect) {
    const sourceDamage = thisItem.effects.find(e => e.getFlag("xjzl-system", "slug") === damageSlug);
    if (sourceDamage) await game.xjzl.api.effects.addEffect(actor, sourceDamage.toObject());
    return;
}

// 2. 检查格挡模式层数
const blockEffect = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === blockSlug);
const currentStacks = blockEffect ? (blockEffect.getFlag("xjzl-system", "stacks") || 1) : 0;

if (currentStacks < 5) {
    // 未满5层，叠加格挡
    const sourceBlock = thisItem.effects.find(e => e.getFlag("xjzl-system", "slug") === blockSlug);
    if (sourceBlock) await game.xjzl.api.effects.addEffect(actor, sourceBlock.toObject());
} else {
    // 满5层再触发 -> 质变！
    // 删除格挡
    await blockEffect.delete();

    // 添加神威 (直接设定为6层效果)
    const sourceDamage = thisItem.effects.find(e => e.getFlag("xjzl-system", "slug") === damageSlug);
    if (sourceDamage) {
        await game.xjzl.api.effects.addEffect(actor, sourceDamage.toObject());
        ui.notifications.info("天铸护手：神兵开锋，攻守逆转！");
    }
}

if (args.outcome.isCrit) {
    args.config.ignoreDefense = true;
    ui.notifications.info("定霸护手：暴击触发无视防御！");
}

const effect = thisItem.effects.find(e => e.name === "金刚护法");
if (effect) {
    await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

if (actor.system.combat.blockTotal > 0 && args.attacker && args.attacker.token) {
    const t1 = actor.token?.object || actor.getActiveTokens()[0];
    const t2 = args.attacker.token?.object || args.attacker.getActiveTokens()[0];
    if (t1 && t2) {
        const dist = canvas.grid.measurePath([t1.center, t2.center]).distance;
        if (dist <= 1.1) {
             const roll = await new Roll("1d20").evaluate();
             if (roll.total === 20) {
                 ChatMessage.create({ content: `<strong style='color:red'>蛛蝎影特效触发！</strong><br>判定投掷: 20<br>请GM手动击碎 ${args.attacker.name} 的武器 (玉品除外)。` });
             }
        }
    }
}

const ownerS = actor.system.social;
const isOwnerEvil = (ownerS.exing >= 100 && ownerS.xiayi < 50);

if (isOwnerEvil && args.target && args.target.actor) {
    const targetS = args.target.actor.system.social;
    const isTargetXia = (targetS.xiayi >= 100 && targetS.exing < 50);

    if (isTargetXia) {
        args.config.amount += 20;
        ui.notifications.info("阎魔之手：诛侠触发 (+20伤害)");
    }
}

if (args.move.element === "yang") {
    args.output.damage += 10;
    args.output.bonusDesc.push("流火(+10)");
}

if (args.move.weaponType === "hidden") {
    args.output.damage += 5;
    args.output.bonusDesc.push("摘星(+5)");
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, "bleed_stack");
}

if (args.move && args.move.weaponType === "hidden") {
    // 临时翻倍 (注意 prepareDerivedData 会重置它，所以这里修改是安全的临时修改)
    actor.system.combat.blockTotal *= 2;
    ui.notifications.info("天罗地网：对暗器格挡翻倍！");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit) {
    const effect = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "unstable"));
    effect.duration = { rounds: 2 };
    await game.xjzl.api.effects.addEffect(args.target, effect);
}

if (args.isHit) {
    await args.target.applyDamage({
        amount: 20,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

if (args.isHit && args.isCrit) {
    await args.target.applyDamage({
        amount: 10,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

if (args.move.type === "basic") {
    args.output.damage += 10;
    args.output.bonusDesc.push("刹绝(+10)");
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit && args.target) {
    await game.xjzl.api.effects.addEffect(args.target, { id: "unstable", name: "下盘不稳", duration: { rounds: 2 } });
}

if (args.isHit && args.isCrit) {
    await actor.applyDamage({ amount: 10, type: "true", isHit: true });
    ui.notifications.warn(`${actor.name} 受到蟠龙反噬！`);
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "mp" });
}

if (args.isHit && args.target) {
    await game.xjzl.api.effects.addEffect(args.target, { id: "unstable", name: "下盘不稳", duration: { rounds: 2 } });
}

if (args.isHit && args.isCrit) {
    await actor.applyDamage({ amount: 10, type: "true", isHit: true });
    ui.notifications.warn(`${actor.name} 受到蟠龙反噬！`);
}

if (args.isHit) {
    await actor.applyHealing({ amount: 5, type: "hp" });
}
