scripts.script:
if (args.isBroken) {
    const eff = CONFIG.statusEffects.find(e => e.id === "root");
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, data);
        ui.notifications.info(`${args.target.name} 被禁足！`);
    }
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    ui.notifications.info(`命中无架招目标！请手动处理击落武器效果 (5米内)。`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "renxing",
        dc: 11,
        label: "抵抗眩晕",
        onFail: {
            id: "xuanyun",
            name: "眩晕",
            img: "icons/svg/daze.svg",
            changes: [{ key: "flags.xjzl-system.stun", mode: 5, value: "true" }],
            duration: { rounds: 1 }
        }
    });
}

if (!Macros.checkStance(actor, args)) return;

// 1. 定义处理悲悯的函数
const handlePeiMin = async (target) => {
    if (!target) return;
    const effData = thisItem.effects.getName("悲悯").toObject();
    delete effData._id;
    effData.origin = thisItem.uuid;

    // 添加特效 (管理器会自动叠加)
    const effect = await game.xjzl.api.effects.addEffect(target, effData);

    // 检查层数触发判定 (仅针对自身，且仅当刚刚更新了特效时)
    if (target.uuid === actor.uuid && effect) {
        const stacks = effect.getFlag("xjzl-system", "stacks") || 1;
        if (stacks >= 3) {
            await effect.delete();
            // 发起定力检定
            // 规则：难度10，检定+2。等价于难度8。
            // 获取当前架招等级
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
            const bonus = 2 + (lvl - 1) * 2;
            const effectiveDC = 10 - bonus;

            ui.notifications.info("悲悯已满，触发自闭判定！");
            await Macros.requestSave({
                target: target,
                attacker: target, // 自己对自己
                type: "dingli",
                dc: effectiveDC,
                label: "悲悯爆发-自闭判定",
                onFail: "zibi"
            });
        }
    }
};

// 2. 对双方应用
await handlePeiMin(actor);
await handlePeiMin(args.attacker);

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effData = thisItem.effects.getName("悲悯").toObject();
    delete effData._id;
    effData.origin = thisItem.uuid;

    await game.xjzl.api.effects.addEffect(actor, effData);
    await game.xjzl.api.effects.addEffect(args.target, effData);
}

if (args.isBroken) {
    // 1. 扣怒
    const lvl = Math.max(1, move.computedLevel || 1);
    const rageLoss = 1 + (lvl - 1);
    const curRage = args.target.system.resources.rage.value || 0;
    await args.target.update({"system.resources.rage.value": Math.max(0, curRage - rageLoss)});

    // 2. 下盘不稳
    const rootEff = CONFIG.statusEffects.find(e => e.id === "unstable");
    if (rootEff) {
        const data = foundry.utils.deepClone(rootEff);
        data.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }

    // 3. 悲悯
    const peimin = thisItem.effects.getName("悲悯").toObject();
    delete peimin._id;
    peimin.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, peimin);
    await game.xjzl.api.effects.addEffect(args.target, peimin);
}

const selfStacks = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "beimin")?.getFlag("xjzl-system", "stacks") || 0;
const targetStacks = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "beimin")?.getFlag("xjzl-system", "stacks") || 0;

ChatMessage.create({
    content: `<div class="xjzl-chat-card">
        <div class="card-header" style="border-bottom:1px solid #ccc; font-weight:bold;">衔悲茹恨 - 夺武对抗</div>
        <div style="padding:5px; font-size:0.9em;">
            <p>请进行 <b>[角力]</b> 对抗。</p>
            <p>我方修正: <b>+${selfStacks}</b> (悲悯层数)</p>
            <p>敌方修正: <b>-${targetStacks}</b> (悲悯层数)</p>
            <p><i>胜利后请手动处理夺取兵器。</i></p>
        </div>
    </div>`,
    speaker: ChatMessage.getSpeaker({actor: actor})
});

// 1. 消耗悲悯
const peimin = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "beimin");
if (!peimin) return ui.notifications.warn("无悲悯层数！");

const stacks = peimin.getFlag("xjzl-system", "stacks") || 1;
await peimin.delete();

// 2. 计算修正
const lvl = Math.max(1, move.computedLevel || 1);
const critMod = (1 + (lvl - 1)) * stacks * -1; // 负数更好

// 3. 应用临时BUFF
const buffData = {
    name: "泣麟悲凤",
    icon: move.img,
    duration: { rounds: 1 },
    changes: [
        { key: "system.combat.crit_waigong", mode: 2, value: String(critMod) },
        { key: "system.combat.crit_neigong", mode: 2, value: String(critMod) }
    ]
};
await game.xjzl.api.effects.addEffect(actor, buffData);
ui.notifications.info(`消耗 ${stacks} 层悲悯，暴击骰 ${critMod}`);

const eff = thisItem.effects.getName("悲怒").toObject();
const lvl = Math.max(1, move.computedLevel || 1);

// 注入伤害数值到 flag
const dmg = 40 + (lvl - 1) * 30;
eff.flags["xjzl-system"].beinuDmg = dmg;

await game.xjzl.api.effects.addEffect(actor, eff);

// args.move 是当前使用的招式
// 检查是否是大悲手招式 (通过 Item 名字)
// args.item 是招式所属的物品
if (args.isCrit && args.item && args.item.name === "大悲手") {
    const dmg = thisEffect.getFlag("xjzl-system", "beinuDmg") || 40;
    // 应用流失
    await args.target.applyDamage({
        amount: dmg,
        type: "liushi",
        attacker: actor,
        isHit: true,
        ignoreDefense: true
    });
    ui.notifications.info(`悲怒触发！额外流失 ${dmg} 气血`);
}

if (!Macros.checkStance(actor, args)) return;

if (args.attacker) {
    const effData = thisItem.effects.getName("毒物").toObject();
    delete effData._id;
    effData.origin = thisItem.uuid;

    // 动态最大层数
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
    effData.flags["xjzl-system"].maxStacks = 3 + (lvl - 1);

    await game.xjzl.api.effects.addEffect(args.attacker, effData);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effData = thisItem.effects.getName("毒物").toObject();
    delete effData._id;
    effData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effData);
}

// 获取目标
const targets = game.user.targets;
if (targets.size === 1) {
    const target = targets.first().actor;
    const duwu = target.effects.find(e => e.getFlag("xjzl-system", "slug") === "duwu");
    if (duwu) {
        const stacks = duwu.getFlag("xjzl-system", "stacks") || 1;
        const lvl = Math.max(1, args.move.computedLevel || 1);
        const extraDmg = stacks * (10 + (lvl - 1) * 5);

        args.flags.damageResult.damage += extraDmg;
        args.flags.damageResult.breakdown += `\n+ 毒物爆发: ${extraDmg}`;
    }
}

if (args.isHit) {
    const effData = thisItem.effects.getName("毒物").toObject();
    delete effData._id;
    effData.origin = thisItem.uuid;

    // 升级效果: 额外层数
    const lvl = Math.max(1, move.computedLevel || 1);
    const stacksToAdd = 1 + (lvl - 1);

    // 循环添加 (因为 addEffect 每次加 1 层)
    for(let i=0; i<stacksToAdd; i++) {
        await game.xjzl.api.effects.addEffect(args.target, effData);
    }

    // 回血逻辑
    // 重新获取目标身上的层数
    const duwu = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "duwu");
    if (duwu) {
        const currentStacks = duwu.getFlag("xjzl-system", "stacks") || 0;
        const heal = currentStacks * 10;
        await actor.applyHealing({ amount: heal, type: "hp", showScrolling: true });
    }
}

if (args.move && args.move.currentCost && args.move.currentCost.mp > 0) {
    const stacks = thisEffect.getFlag("xjzl-system", "stacks") || 1;
    const dmg = stacks * 5;
    actor.applyDamage({
        amount: dmg,
        type: "poison",
        isHit: true,
        ignoreDefense: true
    }).then(res => {
        ui.notifications.info(`${actor.name} 毒物发作: -${res.finalDamage} HP`);
    });
}

if (args.hitCount >= 2) {
    const eff = thisItem.effects.getName("狂蛇").toObject();
    delete eff._id;
    eff.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, eff);
    ui.notifications.info("金蛇狂舞命中多人，获得 [狂蛇]！");
}

if (!Macros.checkStance(actor, args)) return;

if (args.attacker) {
    const eff = thisItem.effects.getName("蛇瘴").toObject();
    delete eff._id;
    eff.origin = thisItem.uuid;

    // 获取当前架招等级
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
    const drain = -5 - (lvl - 1) * 5;

    // 修改流失值
    eff.changes[0].value = String(drain);

    await game.xjzl.api.effects.addEffect(args.attacker, eff);
}

const targets = game.user.targets;
if (targets.size === 1) {
    const target = targets.first().actor;
    const shezhang = target.effects.find(e => e.getFlag("xjzl-system", "slug") === "shezhang");
    if (shezhang) {
        const stacks = shezhang.getFlag("xjzl-system", "stacks") || 1;
        const bonus = stacks * 30;

        args.flags.damageResult.damage += bonus;
        args.flags.damageResult.breakdown += `\n+ 吞噬蛇瘴: ${bonus}`;

        await shezhang.delete();
    }
}

if (args.isHit) {
    const eff = thisItem.effects.getName("狂蛇").toObject();
    delete eff._id;
    eff.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, eff);
}

const eff = thisItem.effects.getName("狂龙").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

const ks = thisItem.effects.getName("狂蛇").toObject();
delete ks._id;
ks.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, ks);
ui.notifications.info("狂龙生效：获得 1 层狂蛇");

if (args.isBroken) {
    // 击倒
    const prone = CONFIG.statusEffects.find(e => e.id === "prone");
    await game.xjzl.api.effects.addEffect(args.target, prone);

    // 禁足
    const root = CONFIG.statusEffects.find(e => e.id === "root");
    if (root) {
        const rd = foundry.utils.deepClone(root);
        rd.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, rd);
    }

    // 检查蛇瘴
    const hasShezhang = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "shezhang");
    if (hasShezhang) {
        const fushen = CONFIG.statusEffects.find(e => e.id === "fushen");
        if (fushen) {
            const fd = foundry.utils.deepClone(fushen);
            fd.duration = { rounds: 3 };
            await game.xjzl.api.effects.addEffect(args.target, fd);
            ui.notifications.info("触发蛇瘴追击：缚身！");
        }
    }
}

// 狂蛇
const ks = thisItem.effects.getName("狂蛇").toObject();
delete ks._id;
ks.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, ks);

// 龙游
const ly = thisItem.effects.getName("龙游").toObject();
delete ly._id;
ly.origin = thisItem.uuid;

const lvl = Math.max(1, move.computedLevel || 1);
ly.duration = { rounds: 1 + (lvl - 1) };
ly.changes[1].value = String(1 + (lvl - 1)); // 虚招值成长

await game.xjzl.api.effects.addEffect(actor, ly);

const ks = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "kuangshe");
if (ks) {
    const stacks = ks.getFlag("xjzl-system", "stacks") || 1;
    ui.notifications.info(`狂蛇追击：当前层数 ${stacks}，请手动追加 ${stacks} 次普通攻击。`);
}
