scripts.script:
if (args.isBroken) {
    ui.notifications.info(`${actor.name} 击破架招！可立即免费施展【金雁掠空】。`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    ChatMessage.create({content: `${args.target.name} 被击飞 1 米。`});
}

if (!args.isHit) {
    ui.notifications.info("攻击被闪避！可立即再次施展（每回合限1次）。");
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    ChatMessage.create({content: `${args.target.name} 被击落并倒地！`});
}

if (args.isHit) {
    ChatMessage.create({content: `${args.target.name} 被击飞 3 米。`});
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    ChatMessage.create({content: `${args.target.name} 被击飞 1 米。`});
}

if (args.isBroken) {
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const rounds = 1 + (lvl - 1);
    const blind = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "blind"));
    blind.duration = { rounds: rounds };
    await game.xjzl.api.effects.addEffect(args.target, blind);
    ui.notifications.info(`${args.target.name} 陷入目盲状态！`);
}

// 获取当前架招等级
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);

    if (Macros.checkStance(actor, args) && args.finalDamage <= 0) {
    const dist = 1 + (Math.max(1, lvl||1)-1);
    ChatMessage.create({content: `<b>铁闩横门</b>：格挡成功！若距离为1，将其击退 ${dist} 米。`});
}

if (args.isHit) {
    ChatMessage.create({content: `${args.target.name} 被击飞 3 米。`});
}

if (Macros.checkStance(actor, args) && args.finalDamage <= 0) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "yanzhan"));
    foundry.utils.setProperty(effectData, "flags.xjzl-system.maxStacks", 3);
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

const transferAmount = 10 + (Math.max(1, args.move.computedLevel||1) - 1) * 10;
await args.target.applyHealing({ amount: transferAmount, type: "mp" });
ChatMessage.create({content: `${actor.name} 为 ${args.target.name} 输送了 ${transferAmount} 点内力。`});

const lvl = Math.max(1, args.move.computedLevel||1);
const rounds = 2 - (lvl - 1);
const root = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "root"));
root.duration = { rounds: rounds };
await game.xjzl.api.effects.addEffect(actor, root);

const lvl = Math.max(1, args.move.computedLevel||1);
const threshold = 2 + (lvl - 1) * 2;
let hasHighArt = false;
if (args.target.system.arts) {
    for (const art of Object.values(args.target.system.arts)) {
        if (art.total > threshold) {
            hasHighArt = true;
            break;
        }
    }
}
if (!hasHighArt) {
    args.flags.grantHit += 100; // 强行必中
    ui.notifications.info(`${args.target.name} 技艺粗浅，此招必中！`);
}

const lvl = Math.max(1, args.move.computedLevel||1);
const hutiVal = 10 + (lvl - 1) * 10;
await actor.applyHealing({amount: hutiVal, type: "huti"});
const rounds = 1 + (lvl - 1);
const effectData = thisItem.effects.getName("善吾").toObject();
effectData.duration = { rounds: rounds };
await game.xjzl.api.effects.addEffect(actor, effectData);

if (args.isHit) {
    const lvl = Math.max(1, args.move.computedLevel||1);
    const rounds = 1 + (lvl - 1);
    const effect = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "chizhi"));
    effect.duration = { rounds: rounds };
    await game.xjzl.api.effects.addEffect(args.target, effect);
}

let maxArt = 0;
if (actor.system.arts) {
    for (const art of Object.values(actor.system.arts)) {
        if (art.total > maxArt) maxArt = art.total;
    }
}
const bonus = maxArt * 5;
args.output.damage += bonus;
args.output.bonusDesc.push(`技艺加成: +${bonus}`);

const lvl = Math.max(1, args.move.computedLevel||1);
const threshold = 2 + (lvl - 1);
let targetMaxArt = 0;
if (args.target.system.arts) {
    for (const art of Object.values(args.target.system.arts)) {
        if (art.total > targetMaxArt) targetMaxArt = art.total;
    }
}
if (targetMaxArt < threshold) {
    args.flags.critThresholdMod += 20;
    ui.notifications.info(`${args.target.name} 技艺不足，必受暴击！`);
}

if (actor.xjzlStatuses.blind) {
    args.flags.level += 1; // 抵消目盲的劣势(-1)
    ui.notifications.info("心眼通明，无视目盲劣势！");
}

const lvl = Math.max(1, move.computedLevel || 1);
const rounds = 1 + (lvl - 1);
const eff = thisItem.effects.getName("虚寂").toObject();
eff.duration = { rounds: rounds };
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === "deaf");
    if (eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

// 统计精通剑招数 (Stage >= 3 视为精通)
let count = 0;
for (const item of actor.itemTypes.wuxue) {
    for (const m of item.system.moves) {
        if (m.weaponType === "sword" && (m.computedLevel || 0) >= 3) {
            count++;
        }
    }
}
const bonus = count * 5;
if (bonus > 0) {
    args.output.damage += bonus;
    args.output.bonusDesc.push(`精通剑招(${count}式) +${bonus}`);
}
args.output.bonusDesc.push("若目标无反应动作，请手动+20伤害");

if (Macros.checkStance(actor, args) && args.finalDamage <= 0) {
    ChatMessage.create({content: `${actor.name} 格挡成功！可立即免费施展【风吹云散】，然后解除架招。`});
    await actor.stopStance();
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    const root = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "root"));
    root.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, root);
    ui.notifications.info(`${args.target.name} 被击倒并禁足！`);
}

if (args.isHit) {
    const lvl = Math.max(1, args.move.computedLevel||1);
    const rounds = 1 + (lvl - 1);
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    const root = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "root"));
    root.duration = { rounds: rounds };
    await game.xjzl.api.effects.addEffect(args.target, root);
}

if (args.isDying) {
    const curMp = args.target.system.resources.mp.value;
    await args.target.update({"system.resources.mp.value": Math.max(0, curMp - 30)});
    ChatMessage.create({content: `${args.target.name} 被打入濒死！额外流失 30 内力。请GM主持残疾检定（-5）。`});
}

if (Macros.checkStance(actor, args) && args.finalDamage <= 0) {
    // 获取当前架招等级
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
    const rounds = 1 + (lvl - 1);
    const effectData = thisItem.effects.getName("混元").toObject();
    effectData.duration = { rounds: rounds };
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (game.user.targets.size === 1) {
    ui.notifications.info("单目标攻击，请手动在伤害修正中 +10。");
}

const lvl = Math.max(1, args.move.computedLevel||1);
const rounds = 1 + (lvl - 1);
const effectData = thisItem.effects.getName("破绽").toObject();
effectData.duration = { rounds: rounds };
await game.xjzl.api.effects.addEffect(args.target, effectData);

await Macros.requestContest({
    attacker: actor,
    defender: args.target,
    type: "wuxue",
    defType: "wuxue",
    label: "破剑诀 - 武学对抗",
    outcome: {
        win: { text: "压制成功！请手动造成伤害。" },
        lose: { text: "压制失败。" }
    }
});

if (args.isBroken) {
    ui.notifications.info("【破气诀】击破架招：请手动移除目标 3 个状态，及自身任意状态。");
} else if (args.isHit && !args.target.system.martial.stanceActive) {
    ui.notifications.info("【破气诀】命中：请手动移除目标 1 个状态。");
}

await Macros.requestContest({
    attacker: actor,
    defender: args.target,
    type: "wuxue",
    defType: "wuxue",
    label: "破索诀 - 武学对抗",
    outcome: {
        win: { text: "压制成功！请手动造成伤害。" },
        lose: { text: "压制失败。" }
    }
});

await Macros.requestContest({
    attacker: actor,
    defender: args.target,
    type: "wuxue",
    defType: "wuxue",
    label: "破掌诀 - 武学对抗",
    outcome: {
        win: { text: "压制成功！请手动造成伤害。" },
        lose: { text: "压制失败。" }
    }
});

await Macros.requestContest({
    attacker: actor,
    defender: args.target,
    type: "wuxue",
    defType: "wuxue",
    label: "破刀诀 - 武学对抗",
    outcome: {
        win: { text: "压制成功！请手动造成伤害。" },
        lose: { text: "压制失败。" }
    }
});

await Macros.requestContest({
    attacker: actor,
    defender: args.target,
    type: "wuxue",
    defType: "wuxue",
    label: "破枪诀 - 武学对抗",
    outcome: {
        win: { text: "压制成功！请手动造成伤害。" },
        lose: { text: "压制失败。" }
    }
});

const lvl = Math.max(1, args.move.computedLevel||1);
const rounds = lvl >= 4 ? 2 : 1;
const effectData = thisItem.effects.getName("破箭").toObject();
effectData.duration = { rounds: rounds };
await game.xjzl.api.effects.addEffect(actor, effectData);

await Macros.requestContest({
    attacker: actor,
    defender: args.target,
    type: "wuxue",
    defType: "wuxue",
    label: "破鞭诀 - 武学对抗",
    outcome: {
        win: { text: "压制成功！请手动造成伤害。" },
        lose: { text: "压制失败。" }
    }
});

const lvl = Math.max(1, move.computedLevel || 1);
const bonus = 5 + (lvl - 1) * 5;
ui.notifications.info(`已建立 [拭剑听风] (伤害+${bonus})。请打开特效栏手动应用。`);

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effect = CONFIG.statusEffects.find(e => e.id === 'root');
    const effectData = foundry.utils.deepClone(effect);
    effectData.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${args.target.name} 因未开架招被禁足！`);
}

if (args.isHit) {
    const root = CONFIG.statusEffects.find(e => e.id === 'root');
    const rootData = foundry.utils.deepClone(root);
    rootData.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, rootData);

    const chizhi = CONFIG.statusEffects.find(e => e.id === 'chizhi');
    const chizhiData = foundry.utils.deepClone(chizhi);
    chizhiData.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(args.target, chizhiData);
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, 'poyi');
}

const buff = thisItem.effects.getName('剑曲绝鸣');
if(buff) {
    const effectData = buff.toObject();
    delete effectData._id;
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.finalAmount > 0) {
    await args.target.applyHealing({ amount: args.finalAmount, type: 'mp', showScrolling: true });
    ui.notifications.info('请在角色面板手动移除一个负面状态。');
}

await game.xjzl.api.effects.removeEffect(args.target, 'blind');
await game.xjzl.api.effects.removeEffect(args.target, 'deaf');

const lvl = Math.max(1, args.move.computedLevel || 1);
const hitBonus = 5 + (lvl - 1) * 5;
const critBonus = -1 + (lvl - 1) * (-1);

const sourceEffect = thisItem.effects.getName('神眸');
if (sourceEffect) {
    const effectData = sourceEffect.toObject();
    delete effectData._id;
    effectData.origin = thisItem.uuid;

    effectData.changes[0].value = String(hitBonus);
    effectData.changes[1].value = String(critBonus);
    effectData.changes[2].value = String(hitBonus);
    effectData.changes[3].value = String(critBonus);

    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info('耳聪目明：神眸已应用');
}
