scripts.script:
if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.toggleStatus(args.target, "root", true);
    ui.notifications.info(`${args.target.name} 被捆锁(禁足)！`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === "blind");
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.toggleStatus(args.target, "yishang", true);
}

if (args.isHit) {
    await game.xjzl.api.toggleStatus(args.target, "prone", true);
}

const heal = Math.floor(args.damageResult.damage);
if (heal > 0) {
    await actor.applyHealing({amount: heal, type: "hp", showScrolling: true});
    ui.notifications.info(`回光返照：吸取生命 ${heal}`);
}

const hasYishang = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "yishang");
if (hasYishang && args.isHit) {
    // 追加一次 0.4 力量系数伤害（约为总伤害的一半）
    const extraDmg = Math.floor(args.damageResult.damage / 2);

    await args.target.applyDamage({
        amount: extraDmg,
        type: args.move.damageType,
        attacker: actor,
        isHit: true,
        ignoreBlock: args.damageResult.ignoreBlock,
        ignoreDefense: args.damageResult.ignoreDefense,
        ignoreStance: args.damageResult.ignoreStance
    });
    ui.notifications.info(`命断黄泉：易伤追加伤害 ${extraDmg}`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.toggleStatus(args.target, "yishang", true);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.toggleStatus(args.target, "root", true);
    ui.notifications.info(`${args.target.name} 被捆锁(禁足)！`);
}

if (!Macros.checkStance(actor, args)) return;
if (args.attacker) {
    await game.xjzl.api.effects.toggleStatus(args.attacker, "bleed_stack", true);
    ui.notifications.info(`${args.attacker.name} 触发水月难寻，受到流血！`);
}

// 检查是否处于禁足状态 (捆锁的系统Flag)
const isRooted = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "root");
if (isRooted) {
    await game.xjzl.api.toggleStatus(args.target, "prone", true);
    ui.notifications.info(`${args.target.name} 被天狗食月击倒！(请手动处理拖拽击飞)`);
}

await actor.stopStance();
const eff = thisItem.effects.getName("殊死").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = thisItem.effects.getName("合璧").toObject();
    delete eff._id;
    eff.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, eff);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.toggleStatus(args.target, "root", true);
}

// 检查攻击来源是否为对对胡
if (args.item && args.item.name.includes("对对胡")) {
    game.xjzl.api.toggleStatus(actor, "prone", true);
    ui.notifications.info(`${actor.name} 触发合璧，倒地！`);
}

if (!Macros.checkStance(actor, args)) return;
if (args.attacker) {
    await args.attacker.applyHealing({amount: -15, type: "mp", showScrolling: true});
    ui.notifications.info(`${args.attacker.name} 触发一股连枝，流失 15 内力！`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === "shizhun");
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.flags["xjzl-system"].maxStacks = 3;
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

const neixi = actor.system.stats.neixi.total;
const skill = actor.system.skills.liaoshang.total;
const lvl = Math.max(1, move.computedLevel || 1);
const growth = (lvl - 1) * 10;

const heal = Math.floor(neixi * 0.2) + skill + growth;
await actor.applyHealing({amount: heal, type: "hp", showScrolling: true});
ui.notifications.info(`一色常青：回复气血 ${heal}`);

if (args.isHit) {
    const lvl = Math.max(1, move.computedLevel || 1);
    const drain = 20 + (lvl - 1) * 5;
    await args.target.applyHealing({amount: -drain, type: "mp", showScrolling: true});
}

const eff = thisItem.effects.getName("听牌").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

if (args.isHit) {
    const eff = CONFIG.statusEffects.find(e => e.id === "blind");
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 2 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

const tingpai = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "tingpai");
const stacks = tingpai ? (tingpai.getFlag("xjzl-system", "stacks") || 1) : 0;

const lvl = Math.max(1, move.computedLevel || 1);
const coef = 10 + (lvl - 1) * 10;
const hutiAmount = stacks * coef;

if (hutiAmount > 0) {
    // 给自己加
    await actor.applyHealing({amount: hutiAmount, type: "huti", showScrolling: true});
    // 提示给队友加
    ui.notifications.info(`顶聚三元：请手动为范围内友方增加 ${hutiAmount} 点护体。`);
}

const eff = thisItem.effects.getName("听牌").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
eff.flags["xjzl-system"].stacks = 3; // 初始3层
await game.xjzl.api.effects.addEffect(actor, eff);
