scripts.script:
if (!Macros.checkStance(actor, args)) return;
const eff = thisItem.effects.getName("凌虚").toObject();
await game.xjzl.api.effects.addEffect(actor, eff);

const hasBuff = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "lingxu");
if (hasBuff) {
    const lvl = Math.max(1, move.computedLevel || 1);
    // 基础+10，升级额外+10，即 10 + (lvl-1)*10
    const bonus = 10 + (lvl - 1) * 10;
    args.output.damage += bonus;
    args.output.bonusDesc.push(`凌虚加伤 +${bonus}`);
}

if (args.isBroken) {
    ui.notifications.info("击破架招！请手动获得额外主要动作。");
}

if (args.isHit) {
    // 敌方封招
    await game.xjzl.api.effects.addEffect(args.target, "fengzhao");

    // 自身凌虚
    const lingxu = thisItem.effects.getName("凌虚").toObject();
    await game.xjzl.api.effects.addEffect(actor, lingxu);
}

if (args.isBroken) {
    const confirm = await foundry.applications.api.DialogV2.prompt({
        window: { title: "商阳指 - 点穴" },
        content: `<p>击破架招！请输入消耗的内力值 (X)。</p><p>难度 = X / 10。</p><input type="number" name="mpCost" value="100" autofocus>`,
        ok: { label: "确定", callback: (event, button) => button.form.elements.mpCost.value }
    });

    const cost = parseInt(confirm) || 0;
    if (cost > 0) {
        // 扣除内力
        const currentMp = actor.system.resources.mp.value;
        if (currentMp >= cost) {
            await actor.update({"system.resources.mp.value": currentMp - cost});
            const dc = Math.floor(cost / 10);
            ui.notifications.info(`消耗 ${cost} 内力，点穴 DC ${dc}`);

            // 发起请求
            await Macros.requestSave({
                target: args.target,
                attacker: actor,
                type: "neixi",
                dc: dc,
                label: "抵抗商阳指点穴",
                onFail: "dianxue"
            });
        } else {
            ui.notifications.warn("内力不足！");
        }
    }
}

if (!Macros.checkStance(actor, args)) return;

// 获取模板
const effData = thisItem.effects.getName("气脉").toObject();
delete effData._id;
effData.origin = thisItem.uuid;

// 动态计算数值 (基础 + Lv成长)
const stanceId = actor.system.martial.stance;
const moveData = thisItem.system.moves.find(m => m.id === stanceId);
const lvl = Math.max(1, moveData?.computedLevel || 1);

// 基础5/5/10, 每级+5/+5/+10
const costMod = 5 + (lvl - 1) * 5;
const hitMod = 5 + (lvl - 1) * 5;
const dmgMod = 10 + (lvl - 1) * 10;

// 写入 Flags 供脚本读取
effData.flags["xjzl-system"].qimai = {
    cost: costMod,
    hit: hitMod,
    dmg: dmgMod
};

await game.xjzl.api.effects.addEffect(actor, effData);

const lvl = Math.max(1, move.computedLevel || 1);
const rounds = 1 + (lvl - 1);
const rangeBonus = 5 + (lvl - 1) * 5;

const eff = thisItem.effects.getName("六脉贯通").toObject();
eff.duration = { rounds: rounds };
eff.description += ` (射程+${rangeBonus}米)`;

await game.xjzl.api.effects.addEffect(actor, eff);

const currentMp = actor.system.resources.mp.value;
if (currentMp > 0) {
    // 计算增伤: 基础100 + Lv成长50
    const lvl = Math.max(1, move.computedLevel || 1);
    const bonus = 100 + (lvl - 1) * 50;

    args.flags.damageResult.damage += bonus;
    args.flags.damageResult.breakdown += `\n+ 耗空内力: ${bonus}`;

    // 扣除
    await actor.update({"system.resources.mp.value": 0});
    ui.notifications.info(`少商剑: 消耗剩余 ${currentMp} 内力，伤害提升 ${bonus}`);
}

if (args.move.item.name === "六脉神指") {
    const data = thisEffect.getFlag('xjzl-system', 'qimai') || { cost: 5, hit: 5, dmg: 10 };

    // 降低命中
    args.flags.bonusHit -= data.hit;

    // 额外扣除内力 (attack阶段扣除，注意检查余额)
    const mp = actor.system.resources.mp.value;
    if (mp >= data.cost) {
        await actor.update({"system.resources.mp.value": mp - data.cost});
        ui.notifications.info(`气脉: 额外消耗 ${data.cost} 内力`);
    } else {
        ui.notifications.warn("气脉: 内力不足支付额外消耗 (可能导致负数)");
    }
}

if (args.move.item.name === "六脉神指") {
    const data = thisEffect.getFlag('xjzl-system', 'qimai') || { cost: 5, hit: 5, dmg: 10 };
    args.output.damage += data.dmg;
    args.output.bonusDesc.push(`气脉增伤 +${data.dmg}`);
}

if (args.move.item.name === "六脉神指") {
    const cost = args.move.currentCost.mp || 0;
    const mp = actor.system.resources.mp.value;
    await actor.update({"system.resources.mp.value": mp + cost});
    ui.notifications.info(`凝气: 返还 ${cost} 内力`);
}

// 二品(Lv2) 点穴优势
if ((move.computedLevel || 0) >= 2) {
    system.skills.dianxue.checkMod += 1;
    system.stats.neixi.checkMod += 1; // 兼容属性检定
}

if ((move.computedLevel || 0) >= 3) {
    const confirm = await foundry.applications.api.DialogV2.confirm({
        window: { title: "天阳指模式" },
        content: "是否对友方施展治疗模式？",
        yes: { label: "治疗", icon: "fas fa-heart" },
        no: { label: "攻击", icon: "fas fa-skull" }
    });

    if (confirm) {
        // 阻断攻击流程
        args.flags.abort = true;

        // 获取伤害值 (此时已计算完毕)
        const healAmt = args.flags.damageResult.damage;

        ChatMessage.create({
            content: `<div class="xjzl-chat-card">
                <div class="card-header" style="background:#e8f5e9; color:#2e7d32; font-weight:bold;">天阳指 - 治疗</div>
                <div style="padding:5px;">
                    <p>治疗量: <b>${healAmt}</b></p>
                    <button data-action="applyHeal">应用治疗</button>
                </div>
            </div>`,
            flags: { "xjzl-system": { 
                actionType: "applyHeal",
                damage: healAmt,
                itemId: item.id,
                moveId: move.id
            }},
            speaker: ChatMessage.getSpeaker({actor: actor})
        });
    }
}

const lvl = move.computedLevel || 0;

// 1阶段
if (lvl >= 1) {
    system.skills.jiaoli.mod += 1;
    system.skills.renxing.mod += 1;
}
// 2阶段
if (lvl >= 2) {
    system.combat.block += 5;
    // 徒手加伤
    if (system.combat.damages.weaponTypes) {
        system.combat.damages.weaponTypes.unarmed.mod += 5;
    }
}
// 3阶段
if (lvl >= 3) {
    system.stats.liliang.mod += 5;
    system.stats.tipo.mod += 5;
}

// 检查修为
const general = actor.system.cultivation.general;
if (general < 2000) {
    ui.notifications.warn("通用修为不足 2000，无法占卜。");
    args.flags.abort = true;
    return;
}

// 扣除
await actor.update({"system.cultivation.general": general - 2000});

// 投掷
const r = await new Roll("1d20").evaluate();
await r.toMessage({speaker: ChatMessage.getSpeaker({actor: actor}), flavor: "巫筮 - 结果"});

let res = "";
if (r.total <= 5) res = "天机难测，模糊不清。";
else if (r.total <= 10) res = "模糊地点。";
else if (r.total <= 15) res = "模糊时间。";
else res = "结果 (甲乙丙代替)。";

ChatMessage.create({
    content: `<div style="padding:10px; font-weight:bold;">${res}</div>`,
    speaker: ChatMessage.getSpeaker({actor: actor})
});
