scripts.script:
if (args.isBroken) {
    const shizhun = args.target.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'shizhun');
    if (shizhun) {
        const stacks = shizhun.getFlag('xjzl-system', 'stacks') || 1;
        await game.xjzl.api.effects.removeEffect(args.target, 'shizhun', stacks);
        const bleedData = CONFIG.statusEffects.find(e => e.id === 'bleed_stack');
        if (bleedData) {
            await game.xjzl.api.effects.addEffect(args.target, bleedData, stacks);
            ui.notifications.info(`${args.target.name} 体内寒气爆发，${stacks} 层失准转化为流血！`);
        }
    }
}

if (Macros.checkStance(actor, args)) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'shizhun');
    if (eff) {
        const data = foundry.utils.deepClone(eff);
        foundry.utils.setProperty(data, 'flags.xjzl-system.maxStacks', 3);
        await game.xjzl.api.effects.addEffect(args.attacker, data);
    }
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'shizhun');
    if (eff) {
        const data = foundry.utils.deepClone(eff);
        foundry.utils.setProperty(data, 'flags.xjzl-system.maxStacks', 3);
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

if (args.move.weaponType === 'blade' && actor.system.resources.rage.value >= 2) {
    const confirm = await foundry.applications.api.DialogV2.confirm({
        window: { title: '悟刀' },
        content: '<p>是否消耗 2 点怒气获得一种劲力？</p>',
        rejectClose: false
    });
    if (confirm) {
        await actor.update({'system.resources.rage.value': actor.system.resources.rage.value - 2});
        const selection = await foundry.applications.api.DialogV2.wait({
            window: { title: '选择劲力' },
            content: '<p>请选择要获得的劲力：</p>',
            buttons: [
                { action: 'gangjin', label: '刚劲 (+刚伤)', icon: 'fas fa-fist-raised' },
                { action: 'mianjin', label: '绵劲 (+柔伤)', icon: 'fas fa-water' },
                { action: 'juqi', label: '聚气 (回蓝)', icon: 'fas fa-bolt' },
                { action: 'xujin', label: '蓄劲 (暴击)', icon: 'fas fa-crosshairs' }
            ],
            close: () => null
        });
        if (selection) {
            const eff = CONFIG.statusEffects.find(e => e.id === selection);
            if (eff) await game.xjzl.api.effects.addEffect(actor, eff);
        }
    }
}

const eff = thisItem.effects.getName('大朝妙计').toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

const juqi = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'juqi');
const stacks = juqi ? (juqi.getFlag('xjzl-system', 'stacks') || 1) : 0;
const range = 1 + stacks;
args.output.bonusDesc.push(`当前范围: ${range}米 (聚气加成)`);
const qingling = actor.effects.some(e => e.getFlag('xjzl-system', 'slug') === 'qingling');
if (qingling) args.output.bonusDesc.push('轻灵激活：可掠至目标位置并攻击路径敌人。');

if (args.isHit && !args.target.system.martial.stanceActive) {
    const juqi = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'juqi');
    if (juqi) {
        const currentStacks = juqi.getFlag('xjzl-system', 'stacks') || 1;
        const maxSpend = Math.min(currentStacks, 3);
        const confirm = await foundry.applications.api.DialogV2.confirm({
            window: { title: '千秋飞历' },
            content: `<p>击中未招架敌人！是否消耗至多 ${maxSpend} 层聚气施加流血？</p>`,
            rejectClose: false
        });
        if (confirm) {
            await game.xjzl.api.effects.removeEffect(actor, 'juqi', maxSpend);
            const bleedData = CONFIG.statusEffects.find(e => e.id === 'bleed_stack');
            await game.xjzl.api.effects.addEffect(args.target, bleedData, maxSpend);
            ui.notifications.info(`${actor.name} 消耗 ${maxSpend} 层聚气，使目标流血！`);
        }
    }
}

if (args.isBroken) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'qingling');
    if(eff) await game.xjzl.api.effects.addEffect(actor, eff);
}

if (Macros.checkStance(actor, args)) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'juqi');
    if(eff) await game.xjzl.api.effects.addEffect(actor, eff);
}

if (args.isHit) {
    const lvl = Math.max(1, move.computedLevel || 1);
    const stacks = 1 + (lvl - 1);
    const eff = CONFIG.statusEffects.find(e => e.id === 'chanshou');
    if (eff) await game.xjzl.api.effects.addEffect(args.target, eff, stacks);

    // 绵劲检测
    const mianjin = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'mianjin');
    if (mianjin) {
        ChatMessage.create({
            content: `<div class='xjzl-chat-card'><div style='padding:5px;'><b>绵劲追击：</b><br>可消耗 1 层绵劲获得次要动作。<br>可消耗 3 层绵劲获得主要动作。</div></div>`,
            speaker: ChatMessage.getSpeaker({actor: actor})
        });
    }
}

if (args.isBroken) {
    const mianjin = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'mianjin');
    if (mianjin) {
        ui.notifications.info("击破且有绵劲！可消耗1层绵劲，飞跃并消耗反应再次施展！");
    }
}

if (Macros.checkStance(actor, args)) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'mianjin');
    if(eff) await game.xjzl.api.effects.addEffect(actor, eff);
}

if (args.isHit) {
    const gangjin = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'gangjin');
    if (gangjin) {
        ui.notifications.info(`${args.target.name} 被击退 1 米！`);
    }
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'gangjin');
    if(eff) await game.xjzl.api.effects.addEffect(actor, eff);
}

if (args.isBroken) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'chizhi');
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

if (Macros.checkStance(actor, args)) {
    ui.notifications.info("格挡成功，击退对手 1 米！");
}

const eff = thisItem.effects.getName('汇刀谱监听').toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

if (args.item.name.includes('绣春刀法')) {
    const ranks = actor.system.combat.weaponRanks;
    if (ranks.unarmed.total >= 6 && args.move.type === 'stance') {
        args.output.damage += 5;
        args.output.bonusDesc.push('汇刀谱(徒手): 格挡+5');
    }
    if (ranks.sword.total >= 6 && args.move.type === 'real') {
        args.output.damage += 20;
        args.output.bonusDesc.push('汇刀谱(剑): 伤害+20');
    }
    if (ranks.dagger.total >= 6 && args.move.type === 'counter') {
        args.output.damage += 20;
        args.output.bonusDesc.push('汇刀谱(匕首): 伤害+20');
    }
    if (ranks.hidden.total >= 6 && args.move.type === 'feint') {
        args.output.damage += 20;
        args.output.bonusDesc.push('汇刀谱(暗器): 伤害+20');
    }
    if (ranks.special.total >= 6 && args.move.type === 'feint') {
        args.output.feint += 3;
    }
    if (ranks.staff.total >= 6 && args.move.type === 'real') {
        args.output.bonusDesc.push('汇刀谱(棍): 距离+1 (文本提示)');
    }
}

const rep = actor.system.social.rep_chaoting || 0;
const bonus = rep * 5;
S.resources.hp.bonus = (S.resources.hp.bonus || 0) + bonus;
S.resources.mp.bonus = (S.resources.mp.bonus || 0) + bonus;

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'bleed_stack');
    if(eff) await game.xjzl.api.effects.addEffect(args.target, eff);
}

if (Macros.checkStance(actor, args) && args.attacker) {
    ChatMessage.create({
        content: `<div class='xjzl-chat-card'><div style='padding:5px;'><b>诏狱缚：</b><br>格挡成功！请双方进行角力对抗（发起者：力量 vs 目标：力量）。<br>若目标失败，失去武器操纵权。</div></div>`,
        speaker: ChatMessage.getSpeaker({actor: actor})
    });
}

if (args.isBroken) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'fushen');
    if(eff) await game.xjzl.api.effects.addEffect(args.target, eff);
    const lvl = Math.max(1, move.computedLevel || 1);
    const dc = 15 + (lvl - 1) * 2;
    ui.notifications.info(`${args.target.name} 被擒抱！挣脱难度 DC ${dc}。`);
}

if (args.isHit) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'blind');
    if (eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 3 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

if (Macros.checkStance(actor, args)) {
    // 获取当前架招等级
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
    const dist = 1 + (lvl - 1);
    ui.notifications.info(`格挡成功！可向后移动 ${dist} 米。`);
}

if (args.isBroken) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'prone');
    if(eff) await game.xjzl.api.effects.addEffect(args.target, eff);
    ui.notifications.info(`${args.target.name} 被击退 1 米并倒地！`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const eff = CONFIG.statusEffects.find(e => e.id === 'unstable');
    if (eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}
