scripts.script:
const eff = thisItem.effects.getName("轻灵").toObject();
// 确保ID移除以触发新建/叠加
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

const eff = CONFIG.statusEffects.find(e => e.id === "root");
if(eff) {
    const data = foundry.utils.deepClone(eff);
    data.duration = { rounds: 1 };
    await game.xjzl.api.effects.addEffect(actor, data);
}

const eff = thisItem.effects.getName("星河").toObject();
// 确保ID移除以触发新建/叠加
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(actor, eff);

const roll = await actor.rollAttributeTest('daofa', { async: true });
if (roll && roll.total >= 10) {
    ui.notifications.info('阴阳路：道法检定成功！');

    // 1. 对敌人施加下盘不稳
    if (args.target) {
        const unstable = CONFIG.statusEffects.find(e => e.id === 'unstable');
        if (unstable) {
            const data = foundry.utils.deepClone(unstable);
            data.duration = { rounds: 3 };
            await game.xjzl.api.effects.addEffect(args.target, data);
            ui.notifications.info(`已对 ${args.target.name} 施加 [下盘不稳]`);
        }
    } else {
        ui.notifications.warn('未选中目标，无法施加 [下盘不稳]。');
    }

    // 2. 给自己施加乘风
    const chengfeng = CONFIG.statusEffects.find(e => e.id === 'chengfeng');
    if (chengfeng) {
        const data = foundry.utils.deepClone(chengfeng);
        // 覆盖最大层数为 3
        foundry.utils.setProperty(data, 'flags.xjzl-system.maxStacks', 3);
        await game.xjzl.api.effects.addEffect(actor, data);
        ui.notifications.info('已获得 [乘风]');
    }
} else {
    ui.notifications.warn('阴阳路：道法检定失败。');
}
