scripts.script:
await game.xjzl.api.effects.removeEffect(actor, 'unstable');
await game.xjzl.api.effects.removeEffect(actor, 'root');
await game.xjzl.api.effects.removeEffect(actor, 'fushen');
ui.notifications.info(`${actor.name} 解除了限制移动的状态。`);

if (!Macros.checkStance(actor, args)) return;

const wanshi = thisItem.effects.getName("顽石");
if (wanshi) {
    await game.xjzl.api.effects.addEffect(args.attacker, wanshi);
    ui.notifications.info(`架招反击：已为 ${args.attacker.name} 添加【顽石】。`);
}

if (!args.isHit) return;
if (target.system.martial?.stanceActive) return;

const wanshi = thisItem.effects.getName("顽石");
if (wanshi) {
    await game.xjzl.api.effects.addEffect(target, wanshi);
    ui.notifications.info(`推石出山：已为 ${target.name} 添加【顽石】。`);
}

if (args.isHit) {
    const effect = await game.xjzl.api.effects.addEffect(args.target, "root");
    if (effect) await effect.update({ "duration.rounds": 2 });
}

if (args.isHit) {
    const wanshi = thisItem.effects.getName("顽石");
    if (wanshi) await game.xjzl.api.effects.addEffect(args.target, wanshi);
}

if (args.outcome.isBroken) {
    args.config.amount += 10;
    ui.notifications.info("粉碎重击：击破架招，伤害+10！");
}

if (!Macros.checkStance(actor, args)) return;

// 获取等级计算持续时间
const lvl = Math.max(1, args.move.computedLevel || 1);
const durationRounds = 1 + (lvl - 1);

const manli = thisItem.effects.getName("蛮力");
if (manli) {
    // 动态调整持续时间
    const effectData = manli.toObject();
    effectData.duration.rounds = durationRounds;
    await game.xjzl.api.effects.addEffect(actor, effectData);
    ui.notifications.info("架招反击：获得【蛮力】");
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (!Macros.checkStance(actor, args)) return;

const tiebi = thisItem.effects.getName("铁壁");
if (tiebi) {
    await game.xjzl.api.effects.addEffect(actor, tiebi);
    ui.notifications.info("架招反击：获得【铁壁】");
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (!args.isHit) return;
// 检查目标是否开启架招
if (!args.target.system.martial?.stanceActive) {
    // 施加错骨，持续1回合
    const effect = await game.xjzl.api.effects.addEffect(args.target, "cuogu");
    // 修正持续时间
    if (effect) await effect.update({ "duration.rounds": 1 });
    ui.notifications.info(`${args.target.name} 被施加【错骨】。`);
}

if (!Macros.checkStance(actor, args)) return;
await game.xjzl.api.effects.addEffect(actor, "yanli");
ui.notifications.info("架招反击：获得/叠加【眼力】");

if (!Macros.checkStance(actor, args)) return;

// 获取等级计算效果(暴击骰减免)
const lvl = Math.max(1, args.move.computedLevel || 1);
const critReduct = -1 + (lvl - 1) * (-1);

const suihuo = thisItem.effects.getName("燧火");
if (suihuo) {
    const effectData = suihuo.toObject();
    // 动态修改暴击骰减免
    effectData.changes[0].value = critReduct;
    await game.xjzl.api.effects.addEffect(actor, effectData);
    ui.notifications.info("架招反击：获得【燧火】");
}

if (!args.isHit) return;
if (!args.target.system.martial?.stanceActive) {
    // 获取等级计算持续时间
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const rounds = 1 + (lvl - 1);

    const laoshang = thisItem.effects.getName("烙伤");
    if (laoshang) {
        const effectData = laoshang.toObject();
        effectData.duration.rounds = rounds;
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 被施加【烙伤】。`);
    }
}

// 获取当前等级对应的品级需求 (0=凡, 1=铜, 2=银)
const lvl = Math.max(1, args.move.computedLevel || 1);
const requiredTier = 0 + (lvl - 1);

// 检查目标主手武器
const targetWeapon = args.target.itemTypes?.weapon?.find(w => w.system.equipped);

// 如果目标没武器，或者武器品级 <= 需求品级，则无视格挡
let ignore = false;
if (!targetWeapon) {
    ignore = true; // 没武器算最低级
} else {
    const quality = targetWeapon.system.quality ?? 0;
    if (quality <= requiredTier) ignore = true;
}

if (ignore) {
    args.flags.ignoreBlock = true;
    ui.notifications.info("兵毁凿击：目标武器品级低，无视格挡！");
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "suihuo")) {
    ui.notifications.info("【薪火相传】检测到燧火！请选择友方施加【薪火】（伤害+10且必暴）。");
}

// 必须暴击才触发
if (!args.isCrit) return;

// 获取烙伤模板
// 因为脚本在AE上运行，thisItem是AE。需要找到所属的物品(Wuxue)。
// AE.origin 指向的是 Item.uuid。我们可以通过 fromUuidSync 获取。
// 但 args.item 已经是当前攻击使用的物品了，直接用 args.item 即可。

const laoshang = args.item.effects.getName("烙伤");
if (laoshang) {
    await game.xjzl.api.effects.addEffect(args.target, laoshang);
    ui.notifications.info("燧火暴击：目标被施加【烙伤】");
}

if (!Macros.checkStance(actor, args)) return;

// 获取等级计算持续时间
const lvl = Math.max(1, args.move.computedLevel || 1);
const durationRounds = 1 + (lvl - 1);

const panshi = thisItem.effects.getName("泰山磐石");
if (panshi) {
    const effectData = panshi.toObject();
    effectData.duration.rounds = durationRounds;
    await game.xjzl.api.effects.addEffect(actor, effectData);
    ui.notifications.info("架招反击：获得【泰山磐石】");
}

if (args.isHit && args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "root");
    await game.xjzl.api.effects.addEffect(args.target, "fushen");
    ui.notifications.info(`${args.target.name} 被破架，陷入【禁足】和【缚身】。`);
}

// 解除架招
await actor.stopStance();

// 获取等级计算持续时间
const lvl = Math.max(1, args.move.computedLevel || 1);
const durationRounds = 1 + (lvl - 1);

const shenjian = thisItem.effects.getName("神剑");
if (shenjian) {
    const effectData = shenjian.toObject();
    effectData.duration.rounds = durationRounds;
    await game.xjzl.api.effects.addEffect(actor, effectData);
    ui.notifications.info("进入【神剑】姿态。请使用【神剑普攻】招式。");
}

const eff = thisItem.effects.getName("踵息");
if (!eff) return ui.notifications.warn("未找到特效数据");
// 动态更新描述中的数值
const bonus = (args.move.computedLevel || 1) * 10;
const effectData = eff.toObject();
effectData.description = `若本回合没有造成任何伤害，则下回合招式命中+${bonus}。`;
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (actor.system.stats.liliang.total < 100) return ui.notifications.warn("力量不足100，无法触发。");
// 获取系统颤手数据
const chanshou = CONFIG.statusEffects.find(e => e.id === 'chanshou');
await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "jiaoli",
    dc: 0, 
    label: "角力对抗 (请双方投掷)",
    onFail: chanshou
});

const eff = thisItem.effects.getName("力道");
if (!eff) return ui.notifications.warn("未找到特效数据");
const lvl = args.move.computedLevel || 1;
const maxStacks = 1 + lvl;
const effectData = eff.toObject();
foundry.utils.setProperty(effectData, "flags.xjzl-system.maxStacks", maxStacks);
await game.xjzl.api.effects.addEffect(args.target, effectData);

const eff = thisItem.effects.getName("剑嶽");
if (!eff) return ui.notifications.warn("未找到特效数据");
const lvl = args.move.computedLevel || 1;
const range = 1 + lvl;
const pull = lvl;
const effectData = eff.toObject();
effectData.description = `周围${range}米困难地形。回合末范围外敌人向中心拉扯${pull}米。`;
await game.xjzl.api.effects.addEffect(args.target, effectData);
