scripts.script:
await game.xjzl.api.effects.removeEffect(actor, 'unstable');
await game.xjzl.api.effects.removeEffect(actor, 'root');
await game.xjzl.api.effects.removeEffect(actor, 'fushen');
ui.notifications.info(`${actor.name} 解除了限制移动的状态。`);

// 检查是否满足触发架招条件
if (!Macros.checkStance(actor, args)) return;

// 获取顽石特效模板
const wanshiEffect = {
    name: "顽石",
    icon: "icons/svg/stone.svg",
    duration: { rounds: 99 },
    description: "当与其他顽石目标贴近时流失气血并倒地。",
    flags: { "xjzl-system": { slug: "wanshi" } }
};

// 获取等级计算流失伤害说明(仅作提示，不改数值)
// 顽石气血流失: 10 + (Level-1)*5
const lvl = Math.max(1, args.move.computedLevel || 1);
const dmg = 10 + (lvl - 1) * 5;
ui.notifications.info(`架招反击：请给 ${args.attacker.name} 添加【顽石】。距离贴近时流失 ${dmg} 点气血。`);

await game.xjzl.api.effects.addEffect(args.attacker, wanshiEffect);

if (!args.isHit) return;
const target = args.target;
// 检查目标是否开启架招
if (target.system.martial?.stanceActive) return;

const wanshiEffect = {
    name: "顽石",
    icon: "icons/svg/stone.svg",
    duration: { rounds: 99 },
    description: "当与其他顽石目标贴近时流失气血并倒地。",
    flags: { "xjzl-system": { slug: "wanshi" } }
};

await game.xjzl.api.effects.addEffect(target, wanshiEffect);
ui.notifications.info(`推石出山：已为 ${target.name} 添加【顽石】。`);

if (args.isHit) {
    await game.xjzl.api.effects.toggleStatus(args.target, "root", true);
    // 修正持续时间为2回合
    const effect = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "root");
    if (effect) await effect.update({ "duration.rounds": 2 });
}

if (args.isHit) {
    const wanshiEffect = {
        name: "顽石",
        icon: "icons/svg/stone.svg",
        duration: { rounds: 99 },
        description: "当与其他顽石目标贴近时流失气血并倒地。",
        flags: { "xjzl-system": { slug: "wanshi" } }
    };
    await game.xjzl.api.effects.addEffect(args.target, wanshiEffect);
}

if (args.outcome.isBroken) {
    args.config.amount += 10;
    ui.notifications.info("粉碎重击：击破架招，伤害+10！");
}

if (!Macros.checkStance(actor, args)) return;

// 获取等级计算持续时间
const lvl = Math.max(1, args.move.computedLevel || 1);
const durationRounds = 1 + (lvl - 1);

const manli = {
    name: "蛮力",
    icon: "icons/svg/upgrade.svg",
    duration: { rounds: durationRounds },
    description: "招式伤害+10",
    changes: [
        { key: "system.combat.damages.skill.mod", mode: 2, value: 10 }
    ],
    flags: { "xjzl-system": { slug: "manli" } }
};

await game.xjzl.api.effects.addEffect(actor, manli);
ui.notifications.info("架招反击：获得【蛮力】");

if (args.isHit) {
    await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
}

if (!Macros.checkStance(actor, args)) return;

const tiebi = {
    name: "铁壁",
    icon: "icons/svg/shield.svg",
    duration: { rounds: 1 },
    description: "无法被范围攻击选中。",
    flags: { "xjzl-system": { slug: "tiebi" } }
};

await game.xjzl.api.effects.addEffect(actor, tiebi);
ui.notifications.info("架招反击：获得【铁壁】");

if (args.isHit) {
    await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
}

if (args.isHit) {
    await game.xjzl.api.effects.toggleStatus(args.target, "prone", true);
}

if (!args.isHit) return;
// 检查目标是否开启架招
if (!args.target.system.martial?.stanceActive) {
    // 施加错骨，持续1回合
    await game.xjzl.api.effects.toggleStatus(args.target, "cuogu", true);
    // 修正持续时间
    const effect = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "cuogu");
    if (effect) await effect.update({ "duration.rounds": 1 });
    ui.notifications.info(`${args.target.name} 被施加【错骨】。`);
}

if (!Macros.checkStance(actor, args)) return;

const yanli = {
    name: "眼力",
    icon: "icons/svg/eye.svg",
    flags: { "xjzl-system": { slug: "yanli", stackable: true, maxStacks: 3 } },
    changes: [
        { key: "system.combat.hit_waigong", mode: 2, value: 5 },
        { key: "system.combat.hit_neigong", mode: 2, value: 5 }
    ]
};

await game.xjzl.api.effects.addEffect(actor, yanli);
ui.notifications.info("架招反击：获得/叠加【眼力】");

if (!Macros.checkStance(actor, args)) return;

// 获取等级计算效果(暴击骰减免)
const lvl = Math.max(1, args.move.computedLevel || 1);
const critReduct = -1 + (lvl - 1) * (-1);

const suihuo = {
    name: "燧火",
    icon: "icons/svg/fire.svg",
    flags: { "xjzl-system": { slug: "suihuo" } },
    changes: [
        { key: "system.combat.crit_waigong", mode: 2, value: critReduct }
    ],
    // 燧火内部脚本：监听攻击命中
    flags: { 
        "xjzl-system": { 
            scripts: [{
                label: "燧火暴击施加烙伤",
                trigger: "hit",
                active: true,
                script: `
                    // 必须暴击才触发
                    if (!args.isCrit) return;
                    // 烙伤：回合结束/出招后受伤。这里使用出招时扣血逻辑(regenHpAttack -10)
                    const laoshang = {
                        name: "烙伤",
                        icon: "icons/svg/fire-circle.svg",
                        duration: { rounds: 1 },
                        description: "每次出招后，受到 10 点火焰伤害。",
                        changes: [{ key: "flags.xjzl-system.regenHpAttack", mode: 2, value: -10 }]
                    };
                    await game.xjzl.api.effects.addEffect(args.target, laoshang);
                    ui.notifications.info("燧火暴击：目标被施加【烙伤】");
                `
            }]
        }
    }
};

await game.xjzl.api.effects.addEffect(actor, suihuo);
ui.notifications.info("架招反击：获得【燧火】");

if (!args.isHit) return;
if (!args.target.system.martial?.stanceActive) {
    // 获取等级计算持续时间
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const rounds = 1 + (lvl - 1);

    const laoshang = {
        name: "烙伤",
        icon: "icons/svg/fire-circle.svg",
        duration: { rounds: rounds },
        description: "每次出招后，受到 10 点火焰伤害。",
        changes: [{ key: "flags.xjzl-system.regenHpAttack", mode: 2, value: -10 }]
    };
    await game.xjzl.api.effects.addEffect(args.target, laoshang);
    ui.notifications.info(`${args.target.name} 被施加【烙伤】。`);
}

// 获取当前等级对应的品级需求 (0=凡, 1=铜, 2=银)
const lvl = Math.max(1, args.move.computedLevel || 1);
const requiredTier = 0 + (lvl - 1);

// 检查目标主手武器
const targetWeapon = args.target.itemTypes?.weapon?.find(w => w.system.equipped);

// 如果目标没武器，或者武器品级 <= 需求品级，则无视格挡
let ignore = false;
if (!targetWeapon) {
    ignore = true; // 没武器算最低级
} else {
    const quality = targetWeapon.system.quality ?? 0;
    if (quality <= requiredTier) ignore = true;
}

if (ignore) {
    args.flags.ignoreBlock = true;
    ui.notifications.info("兵毁凿击：目标武器品级低，无视格挡！");
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "suihuo")) {
    ui.notifications.info("【薪火相传】检测到燧火！请选择友方施加【薪火】（伤害+10且必暴）。");
}

if (!Macros.checkStance(actor, args)) return;

// 获取等级计算持续时间
const lvl = Math.max(1, args.move.computedLevel || 1);
const durationRounds = 1 + (lvl - 1);

const panshi = {
    name: "泰山磐石",
    icon: "icons/svg/castle.svg",
    duration: { rounds: durationRounds },
    description: "免疫所有敌人添加的状态(需手动确认)。",
    flags: { "xjzl-system": { slug: "taishanpanshi" } }
};

await game.xjzl.api.effects.addEffect(actor, panshi);
ui.notifications.info("架招反击：获得【泰山磐石】");

if (args.isHit && args.isBroken) {
    await game.xjzl.api.effects.toggleStatus(args.target, "root", true);
    await game.xjzl.api.effects.toggleStatus(args.target, "fushen", true);
    ui.notifications.info(`${args.target.name} 被破架，陷入【禁足】和【缚身】。`);
}

// 解除架招
await actor.stopStance();

// 获取等级计算持续时间
const lvl = Math.max(1, args.move.computedLevel || 1);
const durationRounds = 1 + (lvl - 1);
// 获取普攻增伤 (0 + 20*lvl-1)
const bonusDmg = 20 * (lvl - 1);

const shenjian = {
    name: "神剑",
    icon: "icons/svg/sword.svg",
    duration: { rounds: durationRounds },
    description: "普攻视为0.4力量系数招式。",
    changes: [
        { key: "system.combat.damages.normal.mod", mode: 2, value: bonusDmg }
    ],
    flags: { "xjzl-system": { slug: "shenjian" } }
};

await game.xjzl.api.effects.addEffect(actor, shenjian);
ui.notifications.info("进入【神剑】姿态。普攻加成已应用，但系数变化需手动计算。");
