scripts.script:
const sourceUuid = thisEffect.getFlag('xjzl-system', 'sourceActorUuid');
if (args.target && args.target.uuid !== sourceUuid) {
    args.config.amount = Math.max(0, args.config.amount - 20);
    ui.notifications.info("以武会友：伤害降低 20 点");
}

const effectData = thisItem.effects.getName("以武会友").toObject();
foundry.utils.setProperty(effectData, "flags.xjzl-system.sourceActorUuid", actor.uuid);
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (args.move.type === "basic" && args.move.weaponType === "hidden") {
    args.output.damage *= 2;
    args.output.bonusDesc.push("雁行有序：伤害翻倍");
    await thisEffect.delete();
}

const ammo = actor.items.find(i => i.name === "飞镖" && i.type === "consumable");
if (ammo && ammo.system.quantity > 0) {
    await ammo.update({"system.quantity": ammo.system.quantity - 1});
}

const ammo = actor.items.find(i => i.name === "飞镖" && i.type === "consumable");
if (ammo && ammo.system.quantity > 0) {
    await ammo.update({"system.quantity": ammo.system.quantity - 1});
}

if (args.isBroken) {
    const effect = await game.xjzl.api.effects.addEffect(args.target, "yishang");
    // 确保最大层数为3
    if (effect && effect.getFlag('xjzl-system', 'maxStacks') !== 3) {
        await effect.update({ "flags.xjzl-system.maxStacks": 3 });
    }
}

const ammo = actor.items.find(i => i.name === "飞镖" && i.type === "consumable");
if (ammo && ammo.system.quantity > 0) {
    await ammo.update({"system.quantity": ammo.system.quantity - 1});
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName("雁行有序").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

const ammo = actor.items.find(i => i.name === "飞镖" && i.type === "consumable");
if (ammo && ammo.system.quantity > 0) {
    await ammo.update({"system.quantity": ammo.system.quantity - 1});
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

const t = args.target;
if (t.effects.some(e => e.getFlag("xjzl-system", "slug") === "unstable" || e.getFlag("xjzl-system", "slug") === "prone")) {
    args.config.amount += 10;
}

if (args.attacker && Macros.checkStance(actor, args)) {
    const target = args.attacker;
    if (target.effects.some(e => e.getFlag("xjzl-system", "slug") === "unstable")) {
        await game.xjzl.api.effects.addEffect(target, "prone");
    } else {
        await game.xjzl.api.effects.addEffect(target, "unstable");
    }
}

const t = args.target;
if (t.effects.some(e => e.getFlag("xjzl-system", "slug") === "unstable" || e.getFlag("xjzl-system", "slug") === "prone")) {
    ui.notifications.info(`${t.name} 被击飞 1 米`);
}

if (!args.target.system.martial.stanceActive) {
    ui.notifications.info(`${args.target.name} 被击退 2 米`);
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "root");
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "cuoluan");
}

const effectData = thisItem.effects.getName("衔刀").toObject();
// 衔刀伤害+5逻辑实现比较复杂，这里只给一个描述性buff，具体数值手动
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, "chizhi");
}

if (actor.statuses.has("prone") && args.target.statuses.has("prone")) {
    args.config.amount += 10;
    ui.notifications.info("地躺增伤生效");
}

if (Macros.checkStance(actor, args) && args.attacker) {
    // 需判定距离，脚本暂无法精准判定Token距离，默认为真
    await game.xjzl.api.effects.addEffect(actor, "prone");
    await game.xjzl.api.effects.addEffect(args.attacker, "prone");
    ui.notifications.info("虾背龟身：双方倒地");
}

if (args.isHit) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    await game.xjzl.api.effects.addEffect(args.target, "root");
}

if (actor.statuses.has("prone") && args.target.statuses.has("prone")) {
    ui.notifications.info("双方倒地，请进行[角力]对抗，失败者手动扣除获胜者角力等级*10的气血。");
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName("浪潮").toObject();
    let stacks = 1;
    if (args.move.computedLevel >= 2) stacks++; 
    for(let i=0; i<stacks; i++) {
        await game.xjzl.api.effects.addEffect(actor, effectData);
    }
}

if (await game.xjzl.api.effects.removeEffect(actor, "wuxue_langchao_stack", 1)) {
    args.flags.bonusDamage = (args.flags.bonusDamage || 0) + 5;
    ui.notifications.info("消耗浪潮，伤害+5");
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, "unstable");
}

if (await game.xjzl.api.effects.removeEffect(actor, "wuxue_langchao_stack", 1)) {
    ui.notifications.info(`${args.target.name} 被击飞 1 米`);
}

const effect = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_langchao_stack");
if (effect) {
    const stacks = effect.getFlag("xjzl-system", "stacks") || 1;
    const bonus = stacks * 5;
    args.flags.bonusDamage = (args.flags.bonusDamage || 0) + bonus;
    await effect.delete();
    ui.notifications.info(`消耗 ${stacks} 层浪潮，伤害+${bonus}`);
}

const hasBainiao = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "wuxue_bainiao_buff");
if (hasBainiao) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (args.isBroken) {
    ui.notifications.info(`${args.target.name} 被击飞 1 米`);
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName("百鸟").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

const hasBainiao = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "wuxue_bainiao_buff");
if (hasBainiao) {
    ui.notifications.info("百鸟加持：对抗自动获胜，击飞+击倒");
    await game.xjzl.api.effects.addEffect(args.target, "prone");
} else {
    ui.notifications.info("请进行[角力]对抗，若获胜则将敌人击飞1米并击倒");
}

const lvl = Math.max(1, args.move.computedLevel || 1);
S.combat.hit_waigong.mod += 5 * lvl;
S.combat.hit_neigong.mod += 5 * lvl;
S.combat.crit_waigong.mod -= 1 * lvl;
S.combat.crit_neigong.mod -= 1 * lvl;

const lvl = Math.max(1, args.move.computedLevel || 1);
const dc = 15 + (lvl - 1) * 2;

// 1. 准备倒地数据 (Prone)
const proneData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'prone'));
if(proneData) proneData.statuses = ['prone'];

// 2. 准备禁足数据 (Root) - 需覆盖持续时间
const rootData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'root'));
if(rootData) {
    rootData.statuses = ['root'];
    rootData.duration = { rounds: 1 };
}

// 3. 发起请求
// 注意：onFail 必须是数据对象或数组，不能是函数
await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: 'jiaoli',
    dc: dc,
    label: '抵抗地龙翻身',
    onFail: [proneData, rootData]
});
