scripts.script:
if (args.isBroken) {
    const effect = thisItem.effects.getName("刚劲");
    if (effect) {
        await game.xjzl.api.effects.addEffect(actor, effect.toObject());
        if (args.move.computedLevel >= 2) await game.xjzl.api.effects.addEffect(actor, effect.toObject());
    }
}

if (Macros.checkStance(actor, args)) {
    const effect = thisItem.effects.getName("磐石");
    if (effect) {
        await game.xjzl.api.effects.addEffect(actor, effect.toObject());
    }
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "gangjin") && move.name.includes("罗汉拳")) {
    const bonus = 5 + (args.move.computedLevel >= 2 ? 5 : 0);
    output.damage += bonus;
    output.bonusDesc.push(`迦叶微笑 +${bonus}`);
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "panshi")) {
    const current = S.combat.block || 0;
    S.combat.block += current;
}

if (args.isHit) {
    ui.notifications.info("若对方格挡成功，请手动确认分神状态。");
    const effect = thisItem.effects.getName("分神");
    if (effect) {
       const active = await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
       if (active && active.getFlag("xjzl-system", "stacks") >= 3) {
           await args.target.stopStance();
           await active.delete();
           ui.notifications.info(`${args.target.name} 分神达到3层，架招解除！`);
       }
    }
}

const effect = thisItem.effects.getName("罗汉棍阵");
if (effect) {
    const data = effect.toObject();
    if (args.move.computedLevel >= 2) data.duration.rounds += 1;
    if (args.move.computedLevel >= 2) {
        const change = data.changes.find(c => c.key === "system.combat.damages.weapon.mod");
        if (change) change.value = "15";
    }
    await game.xjzl.api.effects.addEffect(actor, data);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, { id: "unstable", duration: { rounds: 2 } });
}

if (Macros.checkStance(actor, args) && args.attacker) {
    const rounds = 1 + (args.move.computedLevel >= 2 ? 1 : 0);
    await game.xjzl.api.effects.addEffect(args.attacker, { id: "unstable", duration: { rounds: rounds } });
}

if (args.isBroken) {
    const hasUnstable = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "unstable");
    if (hasUnstable) {
        await game.xjzl.api.effects.addEffect(args.target, "prone");
    } else {
        await game.xjzl.api.effects.addEffect(args.target, { id: "unstable", duration: { rounds: 2 } });
    }
}

if (args.isHit) {
    const unstable = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "unstable");
    const prone = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "prone");
    if (unstable || prone) {
        ui.notifications.info(`${args.target.name} 被击飞至半空！(需手动处理)`);
    }
}

if (args.isHit && args.target.effects.find(e => e.name === "刀木")) {
    const effect = thisItem.effects.getName("引燃");
    if (effect) await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
}

if (args.isBroken) {
    const effect = thisItem.effects.getName("刀木");
    if (effect) await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
}

if (Macros.checkStance(actor, args) && args.attacker) {
    const effect = thisItem.effects.getName("刀木");
    if (effect) await game.xjzl.api.effects.addEffect(args.attacker, effect.toObject());
}

if (args.target && args.target.effects.find(e => e.name === "引燃")) {
    args.config.amount += 20;
}

if (args.move.weaponType === "blade") {
    args.flags.critThresholdMod -= 3;
}

if (Macros.checkStance(actor, args)) {
    const effect = thisItem.effects.getName("金刚");
    if (effect) {
        const data = effect.toObject();
        if (args.move.computedLevel >= 2) data.duration.rounds += 1;
        await game.xjzl.api.effects.addEffect(actor, data);
    }
}

const unstable = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "unstable");
if (args.isHit && (!args.target.system.martial.stanceActive || unstable)) {
    const dist = 1 + (args.move.computedLevel >= 2 ? 1 : 0);
    ui.notifications.info(`${args.target.name} 被击退 ${dist} 米！(需手动处理)`);
    await game.xjzl.api.effects.addEffect(args.target, { id: "cuogu", duration: { rounds: 1 } });
}

if (args.isBroken) {
    ui.notifications.info("击破架招！可消耗反应动作释放【铁劲刚脚】(击退+1，需手动处理)");
}

if (actor.effects.find(e => e.name === "金刚")) {
    ui.notifications.info(`将 ${args.target.name} 击退 5 米！若撞击则手动添加破甲。 (AE: pojia)`);
}

if (actor.system.social.xiayi >= 100 && actor.system.social.exing <= 50) {
    args.flags.critThresholdMod += 1;
}

if (args.isBroken && actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "chanxin")) {
    await game.xjzl.api.effects.removeEffect(actor, "chanxin");
    const effect = thisItem.effects.getName("禅意");
    if (effect) await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "chanxin")) {
    await game.xjzl.api.effects.removeEffect(actor, "chanxin");
    const eff = thisItem.effects.getName("禅意");
    if (eff) await game.xjzl.api.effects.addEffect(actor, eff.toObject());
} else if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "chanyi")) {
    await game.xjzl.api.effects.removeEffect(actor, "chanyi");
    const eff = thisItem.effects.getName("禅法");
    if (eff) await game.xjzl.api.effects.addEffect(actor, eff.toObject());
}

if (args.isHit && !args.target.system.martial.stanceActive && actor.effects.some(e => e.name === "禅心")) {
    ui.notifications.info("可消耗禅心使此招式暴击（需手动移除禅心并应用暴击伤害）。");
}

await game.xjzl.api.effects.toggleStatus(actor, "blind", true);
const effect = thisItem.effects.getName("禅心");
if (effect) await game.xjzl.api.effects.addEffect(actor, effect.toObject());

await game.xjzl.api.effects.removeEffect(actor, "blind");
await game.xjzl.api.effects.removeEffect(actor, "chanxin");
await game.xjzl.api.effects.addEffect(args.target, { id: "root", duration: { rounds: 3 } });

const effect = thisItem.effects.getName("禅意");
if (effect) {
    const data = effect.toObject();
    if (args.move.computedLevel >= 2) data.duration.rounds += 1;
    await game.xjzl.api.effects.addEffect(actor, data);
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "fumo")) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (Macros.checkStance(actor, args)) {
    const effect = thisItem.effects.getName("伏魔");
    if (effect) {
        const data = effect.toObject();
        if (args.move.computedLevel >= 2) data.duration.rounds += 1;
        await game.xjzl.api.effects.addEffect(actor, data);
    }
}

if (args.target && args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "prone")) {
    args.config.amount += 10;
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "fumo")) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
}

if (args.target) {
    const xiayi = args.target.system.social.xiayi;
    const exing = args.target.system.social.exing;
    if (exing >= 100 && xiayi <= 50) {
        args.config.amount += 10;
    }
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.addEffect(args.target, { id: "cuogu", duration: { rounds: 1 } });
}

if (Macros.checkStance(actor, args) && args.attacker) {
    const dist = 2 + (args.move.computedLevel >= 2 ? 1 : 0);
    ui.notifications.info(`力按千斤：可拉扯 ${args.attacker.name} ${dist} 米。(需手动处理)`);
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, { id: "cuogu", duration: { rounds: 3 } });
}

if (args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "cuogu")) {
    await game.xjzl.api.effects.addEffect(args.target, { id: "fengzhao", duration: { rounds: 1 } });
}

if (args.isBroken) {
    const effect = thisItem.effects.getName("愿力");
    if (effect) await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

if (actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "yuanli")) {
    ui.notifications.info("愿力加持：击飞周围 1 米无架招敌人！(需手动处理)");
}

const maxConsume = 1 + (args.move.computedLevel >= 2 ? 1 : 0);
const msg = `
<div class="xjzl-chat-card">
    <div class="card-content-wrapper">
        <p><b>大愿成佛</b>: 可消耗至多 ${maxConsume} 层愿力反击，造成等同于对方伤害的数值。</p>
        <p style="color: #666; font-size: 0.9em;">请手动移除愿力层数并应用伤害。</p>
    </div>
</div>`;
ChatMessage.create({ user: game.user.id, content: msg });

const effect = thisItem.effects.getName("愿力");
if (effect) {
    await game.xjzl.api.effects.addEffect(actor, effect.toObject());
    const extra = args.move.computedLevel >= 2 ? 1 : 0;
    if (extra > 0) await game.xjzl.api.effects.addEffect(actor, effect.toObject());
}

const msg = `
<div class="xjzl-chat-card">
    <div class="card-content-wrapper">
        <p><b>菩提证果</b>: 请进行 [佛法] 检定 (DC 11)。</p>
        <p>成功后请手动为场上友方添加 "禅心"。</p>
    </div>
</div>`;
ChatMessage.create({ user: game.user.id, content: msg });

if (move.type === "feint") {
    const stacks = thisEffect.getFlag("xjzl-system", "stacks") || 1;
    output.damage += 10 * stacks;
    output.bonusDesc.push(`愿力 +${10 * stacks}`);
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.find(e => e.name === "纯阳气");
    if (effectData) {
        const data = effectData.toObject();
        data.duration.rounds = 1 + (args.move.computedLevel - 1);
        await game.xjzl.api.effects.addEffect(actor, data);
    }
}

if (args.isBroken) {
    const chunyang = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_chunyangqi");
    if (chunyang) {
        ui.notifications.info("击破架招！若目标持有金铁武器，可手动删除【纯阳气】并给予目标【缴械】状态。");
    }
}

if (args.isHit) {
    const effectData = thisItem.effects.find(e => e.name === "纯阳气");
    if (effectData) {
        const data = effectData.toObject();
        data.duration.rounds = 99;
        await game.xjzl.api.effects.addEffect(actor, data);
    }
}

if (args.isHit) {
    const chunyang = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_chunyangqi");
    if (chunyang) {
        const confirm = await foundry.applications.api.DialogV2.confirm({
            window: { title: "一指黄粱" },
            content: `<p>是否消耗<b>纯阳气</b>以造成额外火焰伤害并判定走火入魔？</p>`,
            ok: { label: "消耗" }
        });
        if (confirm) {
            await chunyang.delete();
            await args.target.applyDamage({
                amount: 30,
                type: "fire",
                attacker: actor,
                isHit: true
            });
            const rageDuration = 1 + (args.move.computedLevel - 1);
            const rageEffect = CONFIG.statusEffects.find(e => e.id === "rage");
            if (rageEffect) {
                const effectData = foundry.utils.deepClone(rageEffect);
                effectData.duration = { rounds: rageDuration };
                await Macros.requestSave({
                    target: args.target,
                    attacker: actor,
                    type: "dingli",
                    dc: 15,
                    label: "抵抗走火入魔",
                    onFail: effectData
                });
            }
        }
    }
}

if (Macros.checkStance(actor, args) && args.attacker) {
    const attSocial = args.attacker.system.social || {};
    const isXia = (attSocial.xiayi >= 100 && attSocial.exing <= 50);
    const isE = (attSocial.exing >= 100 && attSocial.xiayi <= 50);
    if (isXia || isE) {
        const dmg = 10 + (args.move.computedLevel - 1) * 10;
        await args.attacker.applyDamage({
            amount: dmg,
            type: "mental",
            attacker: actor,
            isHit: true,
            ignoreDefense: true
        });
        ui.notifications.info("无相戒刀直指人心，造成精神伤害！");
    }
}

if (args.isHit) {
    const f = actor.flags["xjzl-system"] || {};
    if (f.wuxue_wuxiang_wuyi && f.wuxue_wuxiang_wunian && f.wuxue_wuxiang_mowang) {
        await game.xjzl.api.effects.toggleStatus(args.target, "chizhi", true);
        const hasImmunity = actor.effects.find(e => e.name === "免疫迟滞");
        if (!hasImmunity) {
            const immuneEffect = thisItem.effects.find(e => e.name === "免疫迟滞");
            if (immuneEffect) {
                await game.xjzl.api.effects.addEffect(actor, immuneEffect.toObject());
                ui.notifications.info("三刀圆满，获得迟滞免疫！");
            }
        }
    }
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(actor, "panshi");
}

if (args.isHit) {
    const confirm = await foundry.applications.api.DialogV2.confirm({
        window: { title: "指尖天风" },
        content: "<p>是否消耗 10 点内力，使下次【惊天动地】怒气消耗 -1？</p>",
        ok: { label: "消耗" }
    });
    if (confirm) {
        if (actor.system.resources.mp.value >= 10) {
            await actor.update({ "system.resources.mp.value": actor.system.resources.mp.value - 10 });
            const buff = thisItem.effects.find(e => e.name === "惊天动地·蓄势");
            if (buff) await game.xjzl.api.effects.addEffect(actor, buff.toObject());
        } else {
            ui.notifications.warn("内力不足！");
        }
    }
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_damoke_xushi");
if (buff) {
    const stacks = buff.getFlag("xjzl-system", "stacks") || 1;
    await actor.update({"system.resources.rage.value": Math.min(10, actor.system.resources.rage.value + stacks)});
    ui.notifications.info(`蓄势生效，返还 ${stacks} 点怒气。`);
    await buff.delete();
}
args.flags.bonusHit += 999; // 必中

if (args.isHit) {
    const stacksToAdd = 1 + (args.move.computedLevel - 1);
    for(let i=0; i<stacksToAdd; i++) {
         await game.xjzl.api.effects.addEffect(args.target, "yudun");
    }
}

if (Macros.checkStance(actor, args)) {
    const buff = thisItem.effects.find(e => e.name === "金刚掌力");
    if (buff) await game.xjzl.api.effects.addEffect(actor, buff.toObject());
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_banruo_buff");
if (buff) {
    args.output.damage += 10;
    args.output.bonusDesc.push("金刚掌力 +10");
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_banruo_buff");
if (buff) {
    args.output.damage += 10;
    args.output.bonusDesc.push("金刚掌力 +10");
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_banruo_buff");
if (buff) await buff.delete();

if (args.isBroken) {
    const dc = 10 + (args.move.computedLevel - 1);
    const stunEffect = CONFIG.statusEffects.find(e => e.id === "stun");
    const effectData = foundry.utils.deepClone(stunEffect);
    effectData.duration = { rounds: 1 };

    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "tipo",
        dc: dc,
        label: "抵抗峡谷天风",
        onFail: effectData
    });
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_banruo_buff");
if (buff) {
    args.output.damage += 10;
    args.output.bonusDesc.push("金刚掌力 +10");
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_banruo_buff");
if (buff) await buff.delete();
const hasStatus = actor.effects.some(e => e.isTemporary && !e.disabled);
if (!hasStatus) {
    await game.xjzl.api.effects.toggleStatus(args.target, "fengzhao", true);
    ui.notifications.info("照空见寂：心无挂碍，封锁敌招！");
}

if (args.isHit && !args.target.system.martial?.stanceActive) {
    const effectData = thisItem.effects.find(e => e.name === "化身");
    if (effectData) await game.xjzl.api.effects.addEffect(actor, effectData.toObject());
}

if (args.isHit) {
    const effectData = thisItem.effects.find(e => e.name === "化身");
    if (effectData) await game.xjzl.api.effects.addEffect(actor, effectData.toObject());
}

if (args.isHit) {
    let stacks = 1;
    if (args.move.computedLevel >= 2) stacks = 2;
    const effectData = thisItem.effects.find(e => e.name === "化身");
    if (effectData) {
        for(let i=0; i<stacks; i++) {
            await game.xjzl.api.effects.addEffect(actor, effectData.toObject());
        }
    }
}

if (args.isHit) {
    const effectData = thisItem.effects.find(e => e.name === "八部天龙");
    if (effectData) {
        const data = effectData.toObject();
        data.duration.rounds = 2 + (args.move.computedLevel - 1);
        await game.xjzl.api.effects.addEffect(actor, data);
    }
    ui.notifications.info("长剑飞出！请手动在装备栏卸下当前武器。");
}

const huashen = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_babu_huashen");
const stacks = huashen ? (huashen.getFlag("xjzl-system", "stacks") || 1) : 0;
if (stacks < 8) {
    ui.notifications.warn(`化身层数不足 (${stacks}/8)`);
    args.flags.abort = true;
    return;
}
await huashen.delete();
args.flags.autoApplied = true;
ui.notifications.info("化身消耗完毕。请在物品特效栏中找到对应的化身效果并拖拽给角色。");

const bonus = args.move.computedLevel - 1;
await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "fofa",
    dc: 15,
    label: "论禅对抗 (攻方加值 " + bonus + ")",
    onFail: null 
});

if (Macros.checkStance(actor, args) && args.attacker) {
    ui.notifications.info("苦海无涯触发！若目标持有有刃兵器，请手动对其施加流失伤害。");
}

const rage = args.target.system.resources.rage.value || 0;
if (rage > 0) {
    const healHP = rage * (10 + (args.move.computedLevel - 1) * 5);
    const healMP = rage * (5 + (args.move.computedLevel - 1) * 5);
    await actor.applyHealing({ amount: healHP, type: "hp" });
    await actor.applyHealing({ amount: healMP, type: "mp" });
    if (rage > 5) {
        await game.xjzl.api.effects.toggleStatus(args.target, "jiaoxie", true);
        ui.notifications.info("放下屠刀：目标怒火攻心，兵刃脱手！");
    }
} else {
    ui.notifications.warn("目标心中无怒，无法度化。");
}
ui.notifications.info("你丢下了手中兵刃。");

if (args.isHit) {
    const targetRage = args.target.system.resources.rage.value || 0;
    if (targetRage > 0) {
        const exing = args.target.system.social?.exing || 0;
        const limit = 100 + (args.move.computedLevel - 1) * 100;
        const dmg = Math.min(limit, exing);
        if (dmg > 0) {
            await args.target.applyDamage({
                amount: dmg,
                type: "liushi",
                attacker: actor,
                isHit: true
            });
            ui.notifications.info(`恶业反噬！流失 ${dmg} 点气血。`);
        }
    } else {
        ui.notifications.info("目标已放下屠刀（无怒气），免受恶业反噬。");
    }
    ui.notifications.info("你触发了一次必定成功的诵经（请手动结算诵经效果）。");
}

if (args.isHit) {
    const effectData = thisItem.effects.find(e => e.name === "须弥");
    if (effectData) await game.xjzl.api.effects.addEffect(actor, effectData.toObject());
}

if (args.isHit && !args.target.system.martial?.stanceActive) {
    const effectData = thisItem.effects.find(e => e.name === "须弥");
    if (effectData) await game.xjzl.api.effects.addEffect(actor, effectData.toObject());
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.find(e => e.name === "须弥");
    if (effectData) await game.xjzl.api.effects.addEffect(actor, effectData.toObject());
}

const buff = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_xumi_stack");
if (buff) {
    const stacks = buff.getFlag("xjzl-system", "stacks") || 1;
    const confirm = await foundry.applications.api.DialogV2.confirm({
        window: { title: "须弥镇邪" },
        content: `<p>是否消耗 ${stacks} 层须弥，展开小须弥天？</p>`,
        ok: { label: "展开" }
    });
