scripts.script:
if (args.isBroken) {
    // 基础1回合，升级+1
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const rounds = lvl;
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'root'));
    effectData.duration = { rounds: rounds };
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

if (args.isBroken) {
    const effects = [
        foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "fushen")),
        foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "cuogu"))
    ];

    for (let e of effects) {
        e.duration = { rounds: 2 };
        e.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(args.target, e);
    }
}

if (Macros.checkStance(actor, args)) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === 'cuogu'));
    effectData.duration = { rounds: 1 };
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.attacker, effectData);
}

const isDianxue = args.target.statuses.has('dianxue') || args.target.statuses.has('stun');
if (isDianxue) {
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const bonus = 10 + (lvl - 1) * 10;
    args.config.amount += bonus;
    ui.notifications.info(`【归路晚霞明】点穴增伤：+${bonus}`);
}

if (Macros.checkStance(actor, args)) {
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
    const effectData = thisItem.effects.getName('逍遥身法').toObject();
    delete effectData._id;
    effectData.flags['xjzl-system'].level = lvl;
    effectData.description = `施展《逍遥》武学时，暴击骰 -${lvl}`;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.isBroken) {
    const effects = [
        foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "prone")),
        foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "root"))
    ];

    for (let e of effects) {
        if (e.id === "root") e.duration = { rounds: 2 };
        e.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(args.target, e);
    }
}

// 1. 自身逍遥身法
const hasBuff = actor.effects.some(e => e.getFlag('xjzl-system', 'slug') === 'xiaoyao-shenfa');
if (hasBuff) {
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const bonus = lvl * 10;
    args.config.amount += bonus;
    ui.notifications.info(`【姗姗绿影】逍遥身法增伤：+${bonus}`);
}
// 2. 目标禁足击飞提示
const isRooted = args.target.statuses.has('root') || args.target.statuses.has('forceSpeedZero');
if (isRooted) {
    ui.notifications.info(`【姗姗绿影】目标禁足，请手动击飞 5 米！`);
}

const lvl = thisEffect.getFlag('xjzl-system', 'level') || 1;
if (args.item && args.item.name.includes('逍遥')) {
    args.flags.critThresholdMod += lvl;
    ui.notifications.info(`逍遥身法生效：暴击阈值优化 ${lvl}`);
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName('剑势').toObject();
    delete effectData._id;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "yangxue"));
    effectData.flags["xjzl-system"].maxStacks = 3;
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.isBroken) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "fushen"));
    effectData.duration = { rounds: 2 };
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

const effect = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'jianshi');
if (effect) {
    const stacks = effect.getFlag('xjzl-system', 'stacks') || 1;
    const bonus = stacks * 20;
    args.output.damage += bonus;
    args.output.bonusDesc.push(`剑势消耗(+${bonus})`);
}

if (args.isHit) {
    // 应用缚身
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "fushen"));
    effectData.duration = { rounds: 2 };
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);

    // 消耗剑势 (仅执行一次)
    const effect = actor.effects.find(e => e.getFlag('xjzl-system', 'slug') === 'jianshi');
    if (effect) {
        await effect.delete();
        ui.notifications.info("剑势已消耗。");
    }
}

const hasJianshi = actor.effects.some(e => e.getFlag('xjzl-system', 'slug') === 'jianshi');
if (hasJianshi) {
    args.config.ignoreDefense = true;
    args.config.ignoreBlock = true;
    ui.notifications.info("【凌霄冲日】剑势加持：无视防御与格挡！");
}

if (args.isBroken) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "lianji"));
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.hitCount > 0) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "chengfeng"));
    effectData.flags['xjzl-system'].maxStacks = 3;
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "root"));
    effectData.duration = { rounds: 1 };
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${args.target.name} 被禁足！`);
}

const lvl = Math.max(1, args.move.computedLevel || 1);
const blockBonus = 10 + (lvl - 1) * 5;
const debuffVal = 10 + (lvl - 1) * 5;

const effectData = {
    name: "垂天",
    icon: "icons/svg/castle.svg",
    origin: thisItem.uuid,
    description: `格挡+${blockBonus}，3米内敌人伤害-${debuffVal} (光环需手动)`,
    changes: [
        { key: "system.combat.block", mode: 2, value: blockBonus }
    ]
};
await game.xjzl.api.effects.addEffect(actor, effectData);

if (args.isBroken) {
    ui.notifications.info("【绿蛇火舞】击破架招，请在目标脚下放置‘碧火’。");
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName('黑子').toObject();
    delete effectData._id;
    await game.xjzl.api.effects.addEffect(args.attacker, effectData);
}

// 获取当前架招等级
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);

    if (Macros.checkStance(actor, args)) {
    const dist = 1 + (Math.max(1, lvl||1) - 1);
    const type = args.attacker.system.martial.stanceActive ? "击退" : "击飞";
    ui.notifications.info(`【风静沙平】反击：${type} ${args.attacker.name} ${dist} 米，自身后撤 ${dist} 米。`);
}

if (args.finalAmount > 0) {
    await args.target.applyHealing({ amount: args.finalAmount, type: 'huti' });
}

if (Macros.checkStance(actor, args)) {
    const stanceId = actor.system.martial.stance;
    const moveData = thisItem.system.moves.find(m => m.id === stanceId);
    const lvl = Math.max(1, moveData?.computedLevel || 1);
    const drain = 5 + (lvl - 1) * 5;

    const tarMp = args.attacker.system.resources.mp.value;
    const actualDrain = Math.min(tarMp, drain);
    await args.attacker.update({ 'system.resources.mp.value': tarMp - actualDrain });

    await actor.applyHealing({ amount: actualDrain, type: 'mp' });

    ui.notifications.info(`【怡然自得】吸取内力 ${actualDrain} 点！`);
}

if (args.isBroken) {
    const effectData = foundry.utils.deepClone(CONFIG.statusEffects.find(e => e.id === "dianxue"));
    effectData.duration = { rounds: 1 };
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, effectData);
    ui.notifications.info(`${args.target.name} 被点穴！`);
}

const lvl = Math.max(1, args.move.computedLevel || 1);
const speed = 6 + (lvl - 1) * 3;

const effectData = thisItem.effects.getName('御风').toObject();
delete effectData._id;
effectData.description = `速度固定为 ${speed}，可滞空。`;
effectData.changes = [
    { key: "system.combat.speed", mode: 5, value: speed } 
];
await game.xjzl.api.effects.addEffect(actor, effectData);

const moveLvl = Math.max(1, args.move.computedLevel || 1);
const baseEffect = thisItem.effects.getName("白玉");
if (!baseEffect) return ui.notifications.warn("未找到特效配置");

// 动态设置暴击骰修正值 (Lv1: -1, Lv2: -2, Lv3: -3)
const critMod = moveLvl;

const effectData = baseEffect.toObject();
delete effectData._id;
effectData.origin = thisItem.uuid;
foundry.utils.setProperty(effectData, "flags.xjzl-system.critMod", critMod);

await game.xjzl.api.effects.addEffect(args.target, effectData);

// 检查武器类型
if (args.move.weaponType !== 'unarmed') return;

// 检查距离 (需要 Token)
const attackerToken = args.attacker.token?.object || args.attacker.getActiveTokens()[0];
const targetToken = args.target.token?.object || args.target.getActiveTokens()[0];
if (!attackerToken || !targetToken) return;

// 计算网格距离
const dist = canvas.grid.measureDistance(attackerToken, targetToken, {gridSpaces: true});

// 距离为1
if (dist <= 1.5) { // 考虑到斜角可能略大于1，或者浮点误差，通常格点距离是整数，这里宽松一点
    args.flags.grantHit += 100; // 必定命中

    const mod = thisEffect.getFlag("xjzl-system", "critMod") || 1;
    args.flags.critThresholdMod += mod;
}

if (!args.isHit) return;

const lvl = Math.max(1, args.move.computedLevel || 1);
const dc = 13 + (lvl - 1) * 3;

// 1. 获取物品上自带的“生死令”特效数据
const sourceEffect = thisItem.effects.getName("生死令");
let effectData = null;
if (sourceEffect) {
    effectData = sourceEffect.toObject();
    delete effectData._id; // 清理ID以便创建新实例
    effectData.origin = thisItem.uuid;
}

await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "qinggong",
    dc: dc,
    label: "躲避生死令",
    // 失败后果1: 扣除 20 点气血 (流失)
    damageOnFail: { value: 20, type: "hp" },
    // 失败后果2: 自动应用提取出的特效
    onFail: effectData,
    failureText: "生死令入体，气血骤降！"
});

const lvl = Math.max(1, args.move.computedLevel || 1);
const cost = 2000 - (lvl - 1) * 500;

ui.notifications.info(`【万法皆通】请手动扣除 ${cost} 点修为。`);

const baseEffect = thisItem.effects.getName("天鉴");
if (baseEffect) {
    await game.xjzl.api.effects.addEffect(args.target, baseEffect);
}

if (!args.isHit) return;
if (args.type !== 'attack') return;

// 获取来源Buff以计算等级，如果来源丢失则默认为1级
// 注意：thisEffect在这里是指挂在身上的“仙舞”BUFF
const lvl = thisEffect.getFlag('xjzl-system', 'level') || 1;
const dc = 15 + (lvl - 1) * 2;

await Macros.requestSave({
    target: args.target,
    attacker: args.attacker,
    type: "dingli",
    dc: dc,
    label: "抵抗仙舞封禁",
    onFail: { id: "jinshi" } // 系统状态ID
});

// 强制锁定伤害为 100，覆盖所有属性/装备/全局增伤
args.output.damage = 100;
// 清空其他加成描述，只显示固定值提示
args.output.bonusDesc = ["[无相] 固定内功伤害: 100"];
