scripts.script:
if (args.isHit && !args.target.system.martial.stanceActive) {
    // 施加倒地
    const prone = CONFIG.statusEffects.find(e => e.id === "prone");
    if (prone) await game.xjzl.api.effects.addEffect(args.target, prone);

    // 施加下盘不稳 (3回合)
    const unstable = CONFIG.statusEffects.find(e => e.id === "unstable");
    if (unstable) {
        const data = foundry.utils.deepClone(unstable);
        data.duration = { rounds: 3 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
    ui.notifications.info(`${args.target.name} 受雅乐影响倒地不稳。`);
}

if (args.target) {
    await args.target.applyHealing({ amount: 10, type: 'mp', showScrolling: true });
}

if (args.target) {
    await args.target.applyHealing({ amount: 20, type: 'huti', showScrolling: true });
}

if (!args.target) return;
const confirm = await foundry.applications.api.DialogV2.confirm({
    window: { title: "冲和吟" },
    content: "<p>选择对目标的效果：</p>",
    yes: { label: "增加 2 怒气", icon: "fas fa-plus" },
    no: { label: "减少 1 怒气", icon: "fas fa-minus" }
});

const current = args.target.system.resources.rage.value;
let newVal = current;
if (confirm) {
    newVal = Math.min(args.target.system.resources.rage.max, current + 2);
    ui.notifications.info(`${args.target.name} 怒气 +2`);
} else {
    newVal = Math.max(0, current - 1);
    ui.notifications.info(`${args.target.name} 怒气 -1`);
}
await args.target.update({ "system.resources.rage.value": newVal });

await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "dingli",
    dc: 13,
    label: "抵抗秋杵弄",
    onFail: {
        name: "捣衣",
        img: "icons/svg/falling.svg",
        duration: { rounds: 3 },
        changes: [
            { key: "flags.xjzl-system.unstable", mode: 5, value: "true" }
        ],
        flags: {
            "xjzl-system": {
                slug: "daoyi",
                scripts: [{
                    label: "捣衣流失",
                    trigger: "damaged",
                    active: true,
                    script: "if (args.finalDamage > 0) { await actor.applyHealing({amount: -5, type: 'mp', showScrolling: true}); }"
                }]
            }
        }
    }
});

const eff = thisItem.effects.getName("御风").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

if (args.target) {
    await args.target.applyHealing({ amount: -10, type: 'mp', showScrolling: true });
}

const eff = thisItem.effects.getName("石泉").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

if (args.isHit) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "dingli",
        dc: 11,
        label: "抵抗月儿高",
        onFail: "deaf"
    });
}

if (args.isHit) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "dingli",
        dc: 13,
        label: "抵抗梅花落",
        onFail: "cuogu"
    });
    ChatMessage.create({
        content: `<div class="xjzl-chat-card"><p>若目标检定失败，请手动流失 30 点气血。</p></div>`,
        speaker: ChatMessage.getSpeaker({actor: actor})
    });
}

const hasBaiXue = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "baixue");

// 添加阳春BUFF
const eff = thisItem.effects.getName("阳春").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

// 触发回血
if (hasBaiXue) {
    const skill = actor.system.skills.yanzou.total || 0;
    const heal = 50 + (skill * 10);
    await args.target.applyHealing({ amount: heal, type: 'hp', showScrolling: true });
    ui.notifications.info(`${args.target.name} 白雪阳春共鸣，回复 ${heal} 气血`);
}

const hasYangChun = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "yangchun");

const eff = thisItem.effects.getName("白雪").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

if (hasYangChun) {
    const skill = actor.system.skills.yanzou.total || 0;
    const heal = 20 + skill;
    await args.target.applyHealing({ amount: heal, type: 'mp', showScrolling: true });
    ui.notifications.info(`${args.target.name} 阳春白雪共鸣，回复 ${heal} 内力`);
}

const hasLiuShui = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "liushui");
let blockBonus = 0;
if (hasLiuShui) blockBonus = 20;

const eff = thisItem.effects.getName("高山").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
// 动态写入
eff.changes[1].value = String(blockBonus);
await game.xjzl.api.effects.addEffect(args.target, eff);

const hasGaoShan = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "gaoshan");
let blockBonus = 0;
if (hasGaoShan) blockBonus = 20;

const eff = thisItem.effects.getName("流水").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
// 动态写入
eff.changes[1].value = String(blockBonus);
await game.xjzl.api.effects.addEffect(args.target, eff);

const eff = thisItem.effects.getName("盼归").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

if (args.isHit) {
    ui.notifications.info(`${args.target.name} 被击飞 3 米`);
}

const slugs = ["root", "unstable", "prone", "stun", "forceSpeedZero"];
const toRemove = args.target.effects.find(e => slugs.includes(e.getFlag("xjzl-system", "slug")));
if (toRemove) {
    await toRemove.delete();
    ui.notifications.info(`${args.target.name} 解除了 [${toRemove.name}]`);
} else {
    ui.notifications.info(`${args.target.name} 无可解除的移动限制`);
}

await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "dingli",
    dc: 16,
    label: "抵抗渔樵问答",
    onFail: {
        name: "问答",
        duration: { rounds: 2 },
        flags: { "xjzl-system": { slug: "wenda", 
            scripts: [{
                label: "回合初提示",
                trigger: "turnStart",
                active: true,
                script: "ChatMessage.create({ content: `${actor.name} 需进行说服检定(DC13)以解除[问答]`, speaker: ChatMessage.getSpeaker({actor: actor}) });"
            }]
        }}
    }
});

await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "dingli",
    dc: 16,
    label: "抵抗昭君出塞",
    onFail: {
        name: "肝肠断",
        img: "icons/svg/ruins.svg",
        duration: { rounds: 3 },
        flags: { "xjzl-system": { slug: "ganchangduan", 
            scripts: [{
                label: "受伤增加",
                trigger: "preTake",
                active: true,
                script: "args.output.damage += 20;"
            }]
        }}
    }
});

// 注意：此招式是 成功 获得BUFF
const roll = await args.target.rollAttributeTest("dingli", { level: 0 });
if (roll.total >= 16) {
    const eff = thisItem.effects.getName("夕阳").toObject();
    delete eff._id;
    eff.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, eff);
    ui.notifications.info("检定成功，获得 [夕阳]");
} else {
    ui.notifications.info("检定失败，无事发生");
}

if (args.isHit) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "dingli",
        dc: 15,
        label: "抵抗苍梧引",
        onFail: [
            CONFIG.statusEffects.find(e => e.id === "prone"),
            CONFIG.statusEffects.find(e => e.id === "deaf")
        ]
    });
}

if (args.target) {
    const currentHuti = args.target.system.resources.huti || 0;
    let addAmount = 0;
    if (currentHuti > 0) {
        addAmount = 10;
    } else {
        addAmount = 40;
    }
    await args.target.applyHealing({ amount: addAmount, type: 'huti', showScrolling: true });
}

const zuidao = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "zuidao");
if (zuidao) {
    args.output.damage += 20;
    args.output.bonusDesc.push("醉倒增伤 +20");
}

if (args.isHit) {
    const current = args.target.system.resources.alcohol?.value || 0;
    await args.target.update({ "system.resources.alcohol.value": current + 10 });
    ui.notifications.info(`${args.target.name} 醉意 +10`);
}

const eff = thisItem.effects.getName("映月").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const eff = thisItem.effects.getName("愿者").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const social = args.target.system.social;
let count = 0;
if (social.attitude_chaoting === 'disdain') count++;
if (social.attitude_wulin === 'disdain') count++;
if (social.attitude_shisu === 'disdain') count++;

if (count > 0) {
    const bonus = count * 20;
    args.config.amount += bonus;
    ui.notifications.info(`水龙吟对 ${args.target.name} 额外造成 ${bonus} 伤害 (无视 ${count})`);
}

if (!args.target.system.martial.stanceActive) {
    args.flags.critThresholdMod = 20; // 降低阈值20 -> 必暴
}

const isRooted = args.target.effects.some(e => e.getFlag("xjzl-system", "slug") === "root");
let failed = false;

if (isRooted) {
    failed = true;
    ui.notifications.info(`${args.target.name} 被禁足，检定必定失败`);
} else {
    const roll = await args.target.rollAttributeTest("dingli", { level: 0 });
    if (roll.total < 20) failed = true;
}

if (failed) {
    const eff = thisItem.effects.getName("哀恨").toObject();
    delete eff._id;
    eff.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(args.target, eff);
}

const isEvil = (args.target.system.social.exing >= 100 && args.target.system.social.xiayi <= 50);
let failed = false;

if (isEvil) {
    failed = true;
    ui.notifications.info(`${args.target.name} 为恶人，检定必定失败`);
} else {
    const roll = await args.target.rollAttributeTest("dingli", { level: 0 });
    if (roll.total < 16) failed = true;
}

if (failed) {
    await args.target.applyHealing({ amount: -2, type: 'rage', showScrolling: true });
}

let heal = 30;
const isDying = args.target.effects.some(e => e.statuses.has("dying"));
if (isDying) heal = 50;
await args.target.applyHealing({ amount: heal, type: 'hp', showScrolling: true });

const eff = thisItem.effects.getName("笑傲").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const eff = thisItem.effects.getName("笑傲").toObject();
delete eff._id;
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const roll = await args.target.rollAttributeTest("dingli", { level: 0 });
if (roll.total < 13) {
    await args.target.applyDamage(args.damageResult);
} else {
    ui.notifications.info(`${args.target.name} 抵抗了雨霖铃`);
}

const confirm = await foundry.applications.api.DialogV2.confirm({
    window: { title: "忆江南" },
    content: "<p>选择添加的效果：</p>",
    yes: { label: "养血 (回血)", icon: "fas fa-heart" },
    no: { label: "聚气 (回内)", icon: "fas fa-bolt" }
});

let slug = confirm ? "yangxue" : "juqi";
const effData = CONFIG.statusEffects.find(e => e.id === slug);
if (effData) {
    const data = foundry.utils.deepClone(effData);
    data.flags["xjzl-system"].maxStacks = 3;
    await game.xjzl.api.effects.addEffect(args.target, data);
}

const eff = thisItem.effects.getName("乘龙快婿").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(attacker, eff);

// 简单过滤：若是角色则提示手动，若是NPC则默认视为敌人发起检定（或由GM手动选择目标）
if (args.target.type !== "character") {
    await Macros.requestSave({
        target: args.target,
        attacker: attacker,
        type: "dingli",
        dc: 15,
        label: "抵抗飞鸣吟",
        onFail: "root" // 禁足
    });
} else {
    ui.notifications.info(`${args.target.name} 为玩家角色，请确认是否为敌方。若是友方请忽略。`);
}

const currentMorale = args.target.system.resources.morale.value;
const maxMorale = args.target.system.resources.morale.max;
const newMorale = Math.min(maxMorale, currentMorale + 20);
await args.target.update({"system.resources.morale.value": newMorale});

const eff = thisItem.effects.getName("强击").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

if (args.isCrit) {
    const current = actor.system.resources.morale.value;
    const max = actor.system.resources.morale.max;
    if (current < max) {
        await actor.update({"system.resources.morale.value": Math.min(max, current + 10)});
        ui.notifications.info(`${actor.name} 触发强击：士气+10`);
    }
}

const currentMorale = args.target.system.resources.morale.value;
const maxMorale = args.target.system.resources.morale.max;
const newMorale = Math.min(maxMorale, currentMorale + 20);
await args.target.update({"system.resources.morale.value": newMorale});

const eff = thisItem.effects.getName("强击").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

if (args.isCrit) {
    const current = actor.system.resources.morale.value;
    const max = actor.system.resources.morale.max;
    if (current < max) {
        await actor.update({"system.resources.morale.value": Math.min(max, current + 10)});
        ui.notifications.info(`${actor.name} 触发强击：士气+10`);
    }
}

const eff = thisItem.effects.getName("采药").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const eff = CONFIG.statusEffects.find(e => e.id === "yangxue");
if(eff) {
    const data = foundry.utils.deepClone(eff);
    data.flags["xjzl-system"].maxStacks = 3;
    await game.xjzl.api.effects.addEffect(args.target, data);
}

const rep = args.target.system.social?.rep_chaoting || 0;
if (rep < 0) {
    await Macros.requestSave({
        target: args.target,
        attacker: attacker,
        type: "dingli",
        dc: 15,
        label: "抵抗华胥引",
        onFail: "root" // 禁足
    });
}

const eff = thisItem.effects.getName("逢时").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const bayinNames = ["粘", "杀", "夺", "摄", "牵", "拉", "旋", "锤"];
let count = 0;
if (args.target.effects) {
    count = args.target.effects.filter(e => bayinNames.includes(e.name)).length;
}
if (count > 0) {
    const bonus = count * 20;
    args.config.amount += bonus;
    ui.notifications.info(`天龙八音：检测到 ${count} 个八音状态，伤害 +${bonus}`);
}

const eff = thisItem.effects.getName("流水静心").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

// 扣除 20% 最大内力
const maxMp = actor.system.resources.mp.max;
const cost = Math.floor(maxMp * 0.2);
const currentMp = actor.system.resources.mp.value;

if (currentMp < cost) {
    args.flags.abort = true;
    args.flags.abortReason = `内力不足！需要 ${cost} (20%最大值)`;
    return;
}

// 手动扣除
await actor.update({"system.resources.mp.value": currentMp - cost});
ui.notifications.info(`龙龟寿：消耗 ${cost} 点内力`);

const eff = thisItem.effects.getName("龙龟气").toObject();
eff.origin = thisItem.uuid;
await game.xjzl.api.effects.addEffect(args.target, eff);

const heal = actor.system.skills.rennai.total || 0;
if (heal > 0) {
    await actor.applyHealing({amount: heal, type: 'hp', showScrolling: true});
    ui.notifications.info(`${actor.name} 触发龙龟气：回复 ${heal} 点气血`);
}

// 计算目标总属性
const stats = args.target.system.stats;
let totalStats = 0;
for (const k of ["liliang", "shenfa", "neixi", "tipo", "qigan", "shencai", "wuxing"]) {
    totalStats += (stats[k]?.total || 0);
}

console.log(`裂天地检测 ${args.target.name}: 总属性 ${totalStats}`);

if (totalStats < 300) {
    await game.xjzl.api.effects.toggleStatus(args.target, "root", true); // 禁足
    await game.xjzl.api.effects.toggleStatus(args.target, "blind", true); // 目盲
    await game.xjzl.api.effects.toggleStatus(args.target, "fushen", true); // 缚身
    ui.notifications.info(`${args.target.name} (属性<300) 受到严重干扰！`);
}
if (totalStats < 200) {
    const eff = CONFIG.statusEffects.find(e => e.id === "fengzhao");
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}
if (totalStats < 100) {
    const eff = CONFIG.statusEffects.find(e => e.id === "stun");
    if(eff) {
        const data = foundry.utils.deepClone(eff);
        data.duration = { rounds: 1 };
        await game.xjzl.api.effects.addEffect(args.target, data);
    }
}

// 增加怒气
const curRage = args.target.system.resources.rage.value;
const maxRage = args.target.system.resources.rage.max;
if (curRage < maxRage) {
    await args.target.update({"system.resources.rage.value": curRage + 1});
}

// 选择效果 (此处由GM/玩家操作选择)
new Dialog({
    title: `破阵乐: ${args.target.name}`,
    content: "<p>选择增益效果:</p>",
    buttons: {
        a: {
            label: "刀兵 (+伤害)",
            callback: async () => {
                const eff = thisItem.effects.getName("刀兵").toObject();
                eff.origin = thisItem.uuid;
                await game.xjzl.api.effects.addEffect(args.target, eff);
            }
        },
        b: {
            label: "龟甲 (+防御)",
            callback: async () => {
                const eff = thisItem.effects.getName("龟甲").toObject();
                eff.origin = thisItem.uuid;
                await game.xjzl.api.effects.addEffect(args.target, eff);
            }
        },
        c: {
            label: "大旗 (+速度)",
            callback: async () => {
                const eff = thisItem.effects.getName("大旗").toObject();
                eff.origin = thisItem.uuid;
                await game.xjzl.api.effects.addEffect(args.target, eff);
            }
        }
    }
}).render(true);

args.flags.damageResult.damage += 10;
args.flags.damageResult.breakdown += "\n+ 刀兵: 10";
// 一次性生效，消耗掉
await thisEffect.delete();

await Macros.requestSave({
    target: args.target,
    attacker: attacker,
    type: "qigan",
    dc: 12,
    label: "抵抗广汉咒",
    onFail: "fushen" // 缚身
});

// 仅执行一次
const current = actor.getFlag("xjzl-system", "wuxue_guanghanqi") || 0;
if (current < 100) {
    await actor.setFlag("xjzl-system", "wuxue_guanghanqi", current + 1);
    ui.notifications.info(`${actor.name} 获得 1 点广寒气 (当前: ${current + 1})`);
    if (current + 1 >= 100) {
        ui.notifications.info("广寒气已达 100，请手动增加属性！");
    }
}
