scripts.script:
if (args.isBroken) {
    const source = thisItem.effects.getName("惭愧");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info(`${args.target.name} 感到惭愧！`);
    }
}

// 自身判定: 侠 (侠义>=100 且 恶行<=50)
const isXia = (actor.system.social.xiayi >= 100 && actor.system.social.exing <= 50);
if (isXia) {
    ui.notifications.info("侠义加持：命中大幅提升！");
    args.flags.grantHit += 100;
}

const target = args.target;
// 邪/恶: 恶行>=100 且 侠义<=50
if (target.system.social.exing >= 100 && target.system.social.xiayi <= 50) {
    args.config.amount += 5;
    ui.notifications.info("惩奸除恶：伤害+5");
}

const isXia = (actor.system.social.xiayi >= 100 && actor.system.social.exing <= 50);
if (isXia) {
    args.output.damage += 20;
    args.output.bonusDesc.push("侠义加成 +20");
}

const isXia = (actor.system.social.xiayi >= 100 && actor.system.social.exing <= 50);
if (isXia) {
    await game.xjzl.api.effects.addEffect(args.target, "fengzhao");
    ui.notifications.info(`${args.target.name} 被封招！`);
}

const attackerShuxie = args.attacker.system.skills?.shuxie?.total || 0;
const myShuxie = actor.system.skills?.shuxie?.total || 0;
if (attackerShuxie < myShuxie) {
    args.output.damage = Math.max(0, args.output.damage - 20);
    ui.notifications.info("及第生效：伤害减免 20");
}

if (Macros.checkStance(actor, args)) {
    const source = thisItem.effects.getName("苦读");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(actor, effectData);
        ui.notifications.info("进入苦读状态");
    }
}

const hasKudu = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "kudu");
if (hasKudu) {
    await Macros.requestSave({
        target: args.target,
        attacker: actor,
        type: "shuxie",
        dc: 13,
        label: "对诗检定 (失败请手动应用额外伤害)"
    });
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const extraDmg = 5 + (lvl - 1) * 5;
    ui.notifications.info(`若对方对诗失败，请手动对其造成额外 ${extraDmg} 点伤害。`);
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    await game.xjzl.api.effects.addEffect(args.target, "unstable");
    ui.notifications.info(`${args.target.name} 悬梁刺股！`);
}

const kuduEffect = actor.effects.find(e => e.getFlag("xjzl-system", "slug") === "kudu");
if (kuduEffect) {
    await kuduEffect.delete();
    const source = thisItem.effects.getName("及第");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(actor, effectData);
        ui.notifications.info("金榜题名！苦读转化为及第！");
    }
}

if (Macros.checkStance(actor, args)) {
    const source = thisItem.effects.getName("知行");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(actor, effectData);
        ui.notifications.info("格挡成功，获得知行！");
    }
}

const hasZhixing = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "zhixing");
const roll = await new Roll("1d20").evaluate();
await roll.toMessage({ speaker: ChatMessage.getSpeaker({actor}), flavor: "知所往判定" });

let success = roll.total > 10;

if (!success && hasZhixing) {
    ui.notifications.info("判定失败，可手动移除【知行】视为成功。");
}

if (success || hasZhixing) {
    const target = args.target;
    const source = thisItem.effects.getName("识破");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        effectData.name = `识破:${target.id}`;
        await game.xjzl.api.effects.addEffect(actor, effectData);
        ui.notifications.info("获得识破标记。请GM/玩家自行查看目标信息。");
    }
}

const targetId = args.target.id;
const hasSpotted = actor.effects.some(e => e.name === `识破:${targetId}`);
if (hasSpotted) {
    args.config.amount += 10;
    ui.notifications.info("识破弱点！伤害+10");
}

const targetId = args.target.id;
const hasSpotted = actor.effects.some(e => e.name === `识破:${targetId}`);
if (hasSpotted) {
    args.config.amount += 30;
    ui.notifications.info("识破弱点！伤害+30");
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    await actor.update({ "system.resources.rage.value": actor.system.resources.rage.value + 1 });
    ui.notifications.info("命中无架招目标，怒气+1");
}

if (Macros.checkStance(actor, args)) {
    const source = thisItem.effects.getName("墨舞");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(actor, effectData);

        const lvl = Math.max(1, args.move.computedLevel || 1);
        if (lvl >= 2) {
             await game.xjzl.api.effects.addEffect(actor, effectData);
             ui.notifications.info("格挡成功，获得两层墨舞！");
        } else {
             ui.notifications.info("格挡成功，获得一层墨舞！");
        }
    }
}

if (args.isBroken) {
    await game.xjzl.api.effects.removeEffect(actor, "mowu", 99);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const source = thisItem.effects.getName("墨舞");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(actor, effectData);
        await game.xjzl.api.effects.addEffect(actor, effectData);
        ui.notifications.info("命中无架招目标，获得两层墨舞！");
    }
}

const block = actor.system.combat.blockTotal || 0;
if (block > 0) {
    args.output.damage += block;
    args.output.bonusDesc.push(`格挡转化 +${block}`);
}

await game.xjzl.api.effects.removeEffect(actor, "hengjian", 99);
const source = thisItem.effects.getName("纵剑");
if (source) {
    const effectData = source.toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
    ui.notifications.info("进入纵剑姿态！");
}

if (args.isBroken) {
    const source = thisItem.effects.getName("禁足");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(args.target, effectData);
        ui.notifications.info("目标被击退并禁足！(位移需手动)");
    }
}

await game.xjzl.api.effects.removeEffect(actor, "zongjian", 99);
const source = thisItem.effects.getName("横剑");
if (source) {
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const dodgeBonus = 4 + (lvl - 1) * 3; 
    const effectData = source.toObject();
    effectData.changes[0].value = String(dodgeBonus);
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
    ui.notifications.info("进入横剑姿态！");
}

const hasHengjian = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "hengjian");
if (hasHengjian) {
    ui.notifications.info("横剑闪避成功！可免费移动3米。");
    await game.xjzl.api.effects.removeEffect(actor, "hengjian");
}

if (Macros.checkStance(actor, args) && args.attacker) {
    const source = thisItem.effects.getName("乱法");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(args.attacker, effectData);
        ui.notifications.info(`对 ${args.attacker.name} 施加乱法！`);
    }
}

if (args.isHit) {
    const source = thisItem.effects.getName("乱法");
    if (source) {
        const effectData = source.toObject();
        effectData.origin = thisItem.uuid;
        await game.xjzl.api.effects.addEffect(args.target, effectData);
    }
}

if (args.isBroken) {
    await game.xjzl.api.effects.addEffect(args.target, "prone");
    await game.xjzl.api.effects.addEffect(args.target, "jinshi");
    ui.notifications.info(`${args.target.name} 倒地并禁实！`);
}

const effect = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "luanfa");
const stacks = effect ? (effect.getFlag("xjzl-system", "stacks") || 1) : 0;

if (stacks >= 2) {
    args.config.ignoreBlock = true;
    args.config.ignoreDefense = true;
    ui.notifications.info("乱法>=2：无视格挡防御");
}

const effect = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "luanfa");
const stacks = effect ? (effect.getFlag("xjzl-system", "stacks") || 1) : 0;

if (stacks >= 4) {
    await game.xjzl.api.effects.addEffect(args.target, "root");
    await game.xjzl.api.effects.addEffect(args.target, "fushen");
    await game.xjzl.api.effects.addEffect(args.target, "chizhi");
    ui.notifications.info("乱法>=4：禁足/缚身/迟滞");
}
if (stacks >= 6) {
    await game.xjzl.api.effects.addEffect(args.target, "jinshi");
    await game.xjzl.api.effects.addEffect(args.target, "jinxu");
    ui.notifications.info("乱法>=6：禁实/禁虚");
}

const source = thisItem.effects.getName("诡辩");
if (source) {
    const effectData = source.toObject();
    effectData.origin = thisItem.uuid;
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.isBroken) {
    await game.xjzl.api.effects.removeEffect(actor, "guibian");
}

const shuofu = actor.system.skills.shuofu.total || 0;
const dc = 10 + shuofu;
const lvl = Math.max(1, args.move?.computedLevel || 1);
const dmgRed = 10 + (lvl - 1) * 10;

await Macros.requestSave({
    target: args.target,
    attacker: actor,
    type: "dingli",
    dc: dc,
    label: "抵抗颠倒黑白 (失败请手动处理)"
});
ui.notifications.info(`若判定失败，手动减少伤害 ${dmgRed} 并取消暴击。若为虚招，可手动移除【诡辩】视为实招。`);

const hasGuibian = actor.effects.some(e => e.getFlag("xjzl-system", "slug") === "guibian");
if (hasGuibian) {
    ui.notifications.info("可手动移除【诡辩】使目标怒气-1。半空判断手动执行。");
}

if (!args.target.system.martial.stanceActive) {
    const effectData = thisItem.effects.getName("归心").toObject();
    effectData.description = "无法以 " + actor.name + " 为攻击目标。";
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

const effectData = thisItem.effects.getName("归心").toObject();
effectData.description = "无法以 " + actor.name + " 为攻击目标。";
await game.xjzl.api.effects.addEffect(args.target, effectData);

if (Macros.checkStance(actor, args)) {
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const stacksToAdd = lvl >= 2 ? 2 : 1;
    const effectData = thisItem.effects.getName("泼墨").toObject();
    for(let i=0; i<stacksToAdd; i++) {
       await game.xjzl.api.effects.addEffect(actor, effectData);
    }
    ui.notifications.info("格挡成功，获得泼墨！");
}

const moji = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_zhengqi_moji" || e.name === "墨迹");
if (moji) {
    await game.xjzl.api.effects.addEffect(args.target, "blind");
    ui.notifications.info(`${args.target.name} 墨迹爆发，陷入目盲！`);
}

const moji = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_zhengqi_moji" || e.name === "墨迹");
if (moji) {
    const effectData = thisItem.effects.getName("泼墨").toObject();
    for(let i=0; i<3; i++) await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.hitCount > 0) {
    const effectData = thisItem.effects.getName("祥云").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.hitCount > 0) {
    const effectData = thisItem.effects.getName("群山").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

const hasCloud = actor.effects.some(e => e.name === "祥云");
const hasMnt = actor.effects.some(e => e.name === "群山");
const hasRiver = actor.effects.some(e => e.name === "江河");

let damageBonus = 0;
let desc = "";

if (hasCloud && hasMnt && hasRiver) {
    desc = "[江山如画图] 范围+5, 击飞+击倒";
    damageBonus = 20;
} else if (hasCloud && hasMnt) {
    desc = "[云卷西峰图] 范围+5";
} else if (hasCloud && hasRiver) {
    desc = "[日出江海图] 击飞";
    damageBonus = 10;
} else if (hasMnt && hasRiver) {
    desc = "[山河辉映图] 击倒";
    damageBonus = 10;
}

args.output.damage += damageBonus;
args.output.bonusDesc.push(desc);

await game.xjzl.api.effects.removeEffect(actor, "wuxue_zhengqi_xiangyun");
await game.xjzl.api.effects.removeEffect(actor, "wuxue_zhengqi_qunshan");
await game.xjzl.api.effects.removeEffect(actor, "wuxue_zhengqi_jianghe");

if (Macros.checkStance(actor, args) && args.attacker) {
    const lvl = Math.max(1, args.move.computedLevel || 1);
    const stacksToAdd = lvl >= 2 ? 2 : 1;
    const effectData = thisItem.effects.getName("星位").toObject();
    for(let i=0; i<stacksToAdd; i++) await game.xjzl.api.effects.addEffect(args.attacker, effectData);
    ui.notifications.info(`${args.attacker.name} 被施加了星位！`);
}

if (args.isHit && !args.target.system.martial.stanceActive) {
    const existing = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_zhengqi_xingwei" || e.name === "星位");
    let stacksToAdd = 1;
    const lvl = Math.max(1, args.move.computedLevel || 1);
    if (lvl >= 2) stacksToAdd += 1;

    if (existing) {
        const currentStacks = existing.getFlag("xjzl-system", "stacks") || 1;
        stacksToAdd = currentStacks; 
    }

    const effectData = thisItem.effects.getName("星位").toObject();
    for(let i=0; i<stacksToAdd; i++) await game.xjzl.api.effects.addEffect(args.target, effectData);
}

const existing = args.target.effects.find(e => e.getFlag("xjzl-system", "slug") === "wuxue_zhengqi_xingwei" || e.name === "星位");
const stacks = existing ? (existing.getFlag("xjzl-system", "stacks") || 0) : 0;
if (stacks < 9) {
    ui.notifications.warn(`${args.target.name} 星位不足9层，天元耀世无法生效！`);
}

if (args.isHit) {
    const effectData = thisItem.effects.getName("天元禁锢").toObject();
    await game.xjzl.api.effects.addEffect(args.target, effectData);
}

await game.xjzl.api.effects.removeEffect(args.target, "unstable");
await game.xjzl.api.effects.removeEffect(args.target, "blind");
ui.notifications.info(`已移除 ${args.target.name} 的下盘不稳和目盲`);

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName("弄梅").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

const nongmei = actor.effects.find(e => e.name === "弄梅");
const stacks = nongmei ? (nongmei.getFlag("xjzl-system", "stacks") || 0) : 0;
args.output.damage += stacks * 5;
if (stacks > 0) args.output.bonusDesc.push(`弄梅加成 +${stacks*5}`);

if (args.isHit && !args.target.system.martial.stanceActive) {
    await game.xjzl.api.effects.toggleStatus(args.target, "deaf", true);
}

const nongmei = actor.effects.find(e => e.name === "弄梅");
const stacks = nongmei ? (nongmei.getFlag("xjzl-system", "stacks") || 0) : 0;
args.output.damage += stacks * 5;
if (stacks > 0) args.output.bonusDesc.push(`弄梅加成 +${stacks*5}`);

if (args.hitCount > 0) {
    const effectData = thisItem.effects.getName("傲梅").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (Macros.checkStance(actor, args)) {
    const effectData = thisItem.effects.getName("文骨").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

if (args.hitCount > 0) {
    const effectData = thisItem.effects.getName("文骨").toObject();
    await game.xjzl.api.effects.addEffect(actor, effectData);
}

const limit = 1 + (Math.max(1, args.move.computedLevel || 1) - 1); 
// 默认给予上限值
S.arts.shuxie.checkMod += limit;

const lvl = Math.max(1, args.move.computedLevel || 1);
// Lv1 -> floor=1, Lv2 -> floor=3, Lv3 -> floor=5
const floor = 1 + (lvl - 1) * 2;

if (S.combat && S.combat.weaponRanks) {
    for (const [key, rankData] of Object.entries(S.combat.weaponRanks)) {
        const current = rankData.value || 0;
        if (current < floor) {
            rankData.mod = (rankData.mod || 0) + (floor - current);
        }
    }
}

// 获取物品内定义的特效
const effect = thisItem.effects.getName("有我无我 (态)");
if (effect) {
    await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
}

const shuxie = S.arts.shuxie?.total || 0;
const lvl = Math.max(1, args.move.computedLevel || 1);
const coeff = 5 + (lvl - 1) * 5;
const huti = shuxie * coeff;
args.output.damage = huti;
args.output.bonusDesc.push(`书写等级(${shuxie}) * 系数(${coeff})`);

// 获得护体 (heal类型招式自动根据 output.damage 提供数值，但需脚本指定为 huti 类型应用)
// 注意：Hit脚本中 args.finalAmount 即为计算后的数值
if (args.finalAmount > 0) {
    await args.target.applyHealing({ amount: args.finalAmount, type: "huti" });
}

// 添加提示Buff
const effect = thisItem.effects.getName("立志明心 (免疫)");
if (effect) {
    await game.xjzl.api.effects.addEffect(args.target, effect.toObject());
}

if (args.target) {
    const res = args.target.system.resources;
    if ((res.shalu?.value > 0) || (res.shanie > 0)) {
        args.config.amount += 10;
    }
}

if (args.isHit && args.target) {
    // 检查计数器
    if (actor.getFlag("xjzl-system", "wuxue_qiufa_used")) return;

    const soc = args.target.system.social || {};
    // 恶标签: 恶行>=100 且 侠义<=50
    const isEvil = (soc.exing >= 100) && (soc.xiayi <= 50);
    if (isEvil) {
        // 调用系统通用AE: 禁足(root)
        const result = await game.xjzl.api.effects.addEffect(args.target, "root");
        // 设置Flag
        if (result) {
             await actor.setFlag("xjzl-system", "wuxue_qiufa_used", true);
             // 如果返回的是Effect对象，手动更新时长为1回合
             await result.update({ duration: { rounds: 1 } });
             ui.notifications.info(`${actor.name} 触发囚法，禁足目标！`);
        }
    }
}

if (args.target) {
    const evil = args.target.system.social?.exing || 0;
    const bonus = Math.floor(evil / 100) * 5;
    if (bonus > 0) {
        args.config.amount += bonus;
    }
}

// S.martial.stanceItemId 是当前开启的架招所属物品ID
// args.item.id 是当前攻击使用的招式所属物品ID
const stanceItem = S.martial.stanceItemId;
const currentItem = args.item.id;

// 如果开启了架招，且架招来源 != 当前攻击来源
if (stanceItem && stanceItem !== currentItem) {
    // 暴击骰-2 (变难) => 阈值修正 -2
    // 逻辑：XJZL中 critThresholdMod 是 ADD 修正。
    // 暴击更难 => d20需要更大 => 阈值提高 => Mod应为负数 (base - mod > base)
    // Wait, let's check config: 
    // flags.critThresholdMod: Global crit threshold modifier. Positive means easier crit (e.g. +2 means threshold drops by 2).
    // "暴击骰-2" usually means roll result -2, effectively making it harder.
    // Equivalent to threshold +2.
    // So critThresholdMod should be -2.
    args.flags.critThresholdMod -= 2;
}
